static enum MHD_Result
internal_run_from_select (struct MHD_Daemon *daemon,
                          const fd_set *read_fd_set,
                          const fd_set *write_fd_set,
                          const fd_set *except_fd_set,
                          int fd_setsize)
{
  MHD_socket ds;
#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
  struct MHD_UpgradeResponseHandle *urh;
  struct MHD_UpgradeResponseHandle *urhn;
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */

  /* Open a log file to record control flow and state for investigation of
     potential const-cast usage (violation scenario). Use append mode so
     multiple invocations are preserved. */
  FILE *mhd_log = fopen("/tmp/logs/2865.txt", "a");
  if (mhd_log)
  {
    fprintf(mhd_log, "ENTER internal_run_from_select: daemon=%p read_fd_set=%p write_fd_set=%p except_fd_set=%p fd_setsize=%d\n",
            (void *) daemon, (const void *) read_fd_set, (const void *) write_fd_set, (const void *) except_fd_set, fd_setsize);
    fflush(mhd_log);
  }

  mhd_assert ((0 == (daemon->options & MHD_USE_SELECT_INTERNALLY)) || \
              (MHD_thread_handle_ID_is_valid_ID_ (daemon->tid)));
  mhd_assert ((0 != (daemon->options & MHD_USE_SELECT_INTERNALLY)) || \
              (! MHD_thread_handle_ID_is_valid_ID_ (daemon->tid)));
  mhd_assert ((0 == (daemon->options & MHD_USE_SELECT_INTERNALLY)) || \
              (MHD_thread_handle_ID_is_current_thread_ (daemon->tid)));

  mhd_assert (0 < fd_setsize);
  (void) fd_setsize; /* Mute compiler warning */
#ifndef HAS_FD_SETSIZE_OVERRIDABLE
  (void) fd_setsize; /* Mute compiler warning */
  mhd_assert (((int) FD_SETSIZE) <= fd_setsize);
  fd_setsize = FD_SETSIZE; /* Help compiler to optimise */
#endif /* ! HAS_FD_SETSIZE_OVERRIDABLE */

  /* Clear ITC to avoid spinning select */
  /* Do it before any other processing so new signals
     will trigger select again and will be processed */
  if (MHD_ITC_IS_VALID_ (daemon->itc))
  { /* Have ITC */
    bool need_to_clear_itc = true; /* ITC is always non-blocking, it is safe to clear even if ITC not activated */
    if (MHD_SCKT_FD_FITS_FDSET_SETSIZE_ (MHD_itc_r_fd_ (daemon->itc),
                                         NULL, fd_setsize))
    {
      /* Preserve original behaviour: set need_to_clear_itc to result of FD_ISSET */
      bool itc_set = FD_ISSET (MHD_itc_r_fd_ (daemon->itc),
                               (fd_set *) _MHD_DROP_CONST (read_fd_set)); /* Skip clearing, if not needed */
      need_to_clear_itc = itc_set;
      if (mhd_log)
      {
        fprintf(mhd_log, "ITC check: itc_fd=%d itc_fdset_fits=1 FD_ISSET(read)=%d need_to_clear_itc=%d\n",
                MHD_itc_r_fd_ (daemon->itc), (int) itc_set, (int) need_to_clear_itc);
        fflush(mhd_log);
      }
    }
    else
    {
      if (mhd_log)
      {
        fprintf(mhd_log, "ITC check: itc_fd=%d itc_fdset_fits=0 default_need_to_clear_itc=%d\n",
                MHD_itc_r_fd_ (daemon->itc), (int) need_to_clear_itc);
        fflush(mhd_log);
      }
    }
    if (need_to_clear_itc)
    {
      if (mhd_log)
      {
        fprintf(mhd_log, "Clearing ITC (need_to_clear_itc=%d)\n", (int) need_to_clear_itc);
        fflush(mhd_log);
      }
      MHD_itc_clear_ (daemon->itc);
    }
  }

  /* Reset. New value will be set when connections are processed. */
  /* Note: no-op for thread-per-connection as it is always false in that mode. */
  daemon->data_already_pending = false;

  /* Process externally added connection if any */
  if (daemon->have_new)
  {
    if (mhd_log)
    {
      fprintf(mhd_log, "Processing new connections: daemon->have_new=1\n");
      fflush(mhd_log);
    }
    new_connections_list_process_ (daemon);
  }

  /* select connection thread handling type */
  ds = daemon->listen_fd;
  if ( (MHD_INVALID_SOCKET != ds) &&
       (! daemon->was_quiesced) )
  {
    bool need_to_accept;
    if (MHD_SCKT_FD_FITS_FDSET_SETSIZE_ (ds, NULL, fd_setsize))
      need_to_accept = FD_ISSET (ds,
                                 (fd_set *) _MHD_DROP_CONST (read_fd_set));
    else                                       /* Cannot check whether new connection are pending */
      need_to_accept = daemon->listen_nonblk;  /* Try to accept if non-blocking */

    if (mhd_log)
    {
      fprintf(mhd_log, "Listen socket: ds=%d fits_fdset=%d need_to_accept=%d\n",
              (int) ds,
              (int) MHD_SCKT_FD_FITS_FDSET_SETSIZE_ (ds, NULL, fd_setsize),
              (int) need_to_accept);
      fflush(mhd_log);
    }

    if (need_to_accept)
      (void) MHD_accept_connection (daemon);
  }

  if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
  {
    /* do not have a thread per connection, process all connections now */
    struct MHD_Connection *pos;
    for (pos = daemon->connections_tail; NULL != pos; pos = pos->prev)
    {
      MHD_socket cs;
      bool r_ready;
      bool w_ready;
      bool has_err;

      cs = pos->socket_fd;
      if (MHD_INVALID_SOCKET == cs)
        continue;

      if (MHD_SCKT_FD_FITS_FDSET_SETSIZE_ (cs, NULL, fd_setsize))
      {
        /* Note: these calls cast away const; log the fact and the values.
           We preserve the original behaviour exactly. */
        r_ready = FD_ISSET (cs,
                            (fd_set *) _MHD_DROP_CONST (read_fd_set));
        w_ready = FD_ISSET (cs,
                            (fd_set *) _MHD_DROP_CONST (write_fd_set));
        has_err = (NULL != except_fd_set) &&
                  FD_ISSET (cs,
                            (fd_set *) _MHD_DROP_CONST (except_fd_set));

        if (mhd_log)
        {
          fprintf(mhd_log, "Conn: pos=%p cs=%d fdset_fits=1 r_ready=%d w_ready=%d has_err=%d read_fd_set=%p write_fd_set=%p except_fd_set=%p\n",
                  (void *) pos, (int) cs, (int) r_ready, (int) w_ready, (int) has_err,
                  (const void *) read_fd_set, (const void *) write_fd_set, (const void *) except_fd_set);
          fprintf(mhd_log, "  Note: FD_ISSET calls used casts: (fd_set *) _MHD_DROP_CONST(write_fd_set) and similar.\n");
          fflush(mhd_log);
        }
      }
      else
      { /* Cannot check the real readiness */
        r_ready = pos->sk_nonblck;
        w_ready = r_ready;
        has_err = false;

        if (mhd_log)
        {
          fprintf(mhd_log, "Conn: pos=%p cs=%d fdset_fits=0 r_ready=%d (from sk_nonblck)\n",
                  (void *) pos, (int) cs, (int) r_ready);
          fflush(mhd_log);
        }
      }
      call_handlers (pos,
                     r_ready,
                     w_ready,
                     has_err);
    }
  }

#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
  /* handle upgraded HTTPS connections */
  for (urh = daemon->urh_tail; NULL != urh; urh = urhn)
  {
    urhn = urh->prev;
    /* update urh state based on select() output */
    if (mhd_log)
    {
      fprintf(mhd_log, "URH: urh=%p calling urh_from_fdset with read=%p write=%p except=%p fd_setsize=%d\n",
              (void *) urh, (const void *) read_fd_set, (const void *) write_fd_set, (const void *) except_fd_set, fd_setsize);
      fflush(mhd_log);
    }
    urh_from_fdset (urh,
                    read_fd_set,
                    write_fd_set,
                    except_fd_set,
                    fd_setsize);
    /* call generic forwarding function for passing data */
    process_urh (urh);
    /* Finished forwarding? */
    if ( (0 == urh->in_buffer_size) &&
         (0 == urh->out_buffer_size) &&
         (0 == urh->in_buffer_used) &&
         (0 == urh->out_buffer_used) )
    {
      MHD_connection_finish_forward_ (urh->connection);
      urh->clean_ready = true;
      /* Resuming will move connection to cleanup list. */
      MHD_resume_connection (urh->connection);
    }
  }
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */
  MHD_cleanup_connections (daemon);

  if (mhd_log)
  {
    fprintf(mhd_log, "EXIT internal_run_from_select: returning MHD_YES\n");
    fflush(mhd_log);
    fclose(mhd_log);
  }
  return MHD_YES;
}
