_MHD_static_inline bool
is_param_equal (const struct MHD_RqDAuthParam *param,
                const char *const str,
                const size_t str_len)
{
  /* Preserve original assertions */
  mhd_assert (NULL != param->value.str);
  mhd_assert (0 != param->value.len);

  /* Logging to /tmp/logs/1465.txt for control-flow and state inspection. */
  FILE *logf = fopen("/tmp/logs/1465.txt", "a");
  if (NULL == logf) {
    /* Try to create directory if it does not exist; declare mkdir locally to avoid requiring headers here. */
    extern int mkdir(const char *, unsigned int);
    (void) mkdir("/tmp/logs", 0700);
    logf = fopen("/tmp/logs/1465.txt", "a");
  }

  if (logf) {
    fprintf(logf, "is_param_equal entered: param=%p, param->value.str=%p, param->value.len=%zu, param->quoted=%d, str=%p, str_len=%zu\n",
            (const void *) param,
            (const void *) param->value.str,
            (size_t) param->value.len,
            (int) param->quoted,
            (const void *) str,
            (size_t) str_len);
    fflush(logf);
  }

  if (param->quoted) {
    bool result = MHD_str_equal_quoted_bin_n (param->value.str, param->value.len,
                                              str, str_len);
    if (logf) {
      fprintf(logf, "Branch: quoted -> result=%d\n", (int) result);
      fflush(logf);
      fclose(logf);
    }
    return result;
  }

  /* Non-quoted path: preserve original semantics (only compare when lengths match). */
  int cmp_res = 0;
  if (str_len == param->value.len) {
    cmp_res = memcmp (str, param->value.str, str_len);
  }
  bool final_result = (str_len == param->value.len) && (0 == cmp_res);

  if (logf) {
    fprintf(logf, "Branch: non-quoted -> str_len=%zu, param->value.len=%zu, memcmp_res=%d, final=%d\n",
            (size_t) str_len, (size_t) param->value.len, cmp_res, (int) final_result);
    fflush(logf);
    fclose(logf);
  }

  return final_result;
}
