static enum replyBodyUse
is_reply_body_needed (struct MHD_Connection *connection,
                      unsigned int rcode)
{
  struct MHD_Connection *const c = connection; /**< a short alias */
  FILE *logf = fopen("/tmp/logs/6.txt", "a");

  if (logf)
    fprintf(logf, "is_reply_body_needed ENTER: connection=%p rcode=%u\n", (void *) connection, rcode);

#ifdef UPGRADE_SUPPORT
  if (logf)
    fprintf(logf, "compile-time: UPGRADE_SUPPORT defined\n");
#else
  if (logf)
    fprintf(logf, "compile-time: UPGRADE_SUPPORT NOT defined\n");
#endif

  if (c)
    {
      if (logf)
        fprintf(logf, "connection->rq.http_mthd=%d\n", c->rq.http_mthd);
    }
  else
    {
      if (logf)
        fprintf(logf, "connection is NULL\n");
    }

  mhd_assert (100 <= rcode);
  mhd_assert (999 >= rcode);

  if (199 >= rcode)
    {
      if (logf)
        fprintf(logf, "branch: rcode <=199 -> RP_BODY_NONE\n");
      if (logf) fclose(logf);
      return RP_BODY_NONE;
    }

  if (MHD_HTTP_NO_CONTENT == rcode)
    {
      if (logf)
        fprintf(logf, "branch: HTTP_NO_CONTENT -> RP_BODY_NONE\n");
      if (logf) fclose(logf);
      return RP_BODY_NONE;
    }

#if 0
  /* This check is not needed as upgrade handler is used only with code 101 */
#ifdef UPGRADE_SUPPORT
  if (NULL != rp.response->upgrade_handler)
    return RP_BODY_NONE;
#endif /* UPGRADE_SUPPORT */
#else
  /* NOTE: The original upgrade_handler check above is wrapped in #if 0
   * and therefore compiled out. Log this as evidence for unreachable-code
   * analysis. This logging branch does not change function control flow. */
  if (logf)
    fprintf(logf, "NOTE: upgrade_handler check block is compiled out via #if 0; not executed\n");
#endif

#if 0
  /* CONNECT is not supported by MHD */
  /* Successful responses for connect requests are filtered by
   * MHD_queue_response() */
  if ( (MHD_HTTP_MTHD_CONNECT == c->rq.http_mthd) &&
       (2 == rcode / 100) )
    return false; /* Actually pass-through CONNECT is not supported by MHD */
#endif
#else
  /* The CONNECT-handling block above is compiled out in the original
   * source via #if 0. Log that fact so tests can observe it. */
  if (logf)
    fprintf(logf, "NOTE: CONNECT handling block is compiled out via #if 0; not executed\n");
#endif

  /* Reply body headers could be used.
   * Check whether reply body itself must be used. */

  if (MHD_HTTP_MTHD_HEAD == c->rq.http_mthd)
    {
      if (logf)
        fprintf(logf, "branch: HTTP method HEAD -> RP_BODY_HEADERS_ONLY\n");
      if (logf) fclose(logf);
      return RP_BODY_HEADERS_ONLY;
    }

  if (MHD_HTTP_NOT_MODIFIED == rcode)
    {
      if (logf)
        fprintf(logf, "branch: HTTP_NOT_MODIFIED -> RP_BODY_HEADERS_ONLY\n");
      if (logf) fclose(logf);
      return RP_BODY_HEADERS_ONLY;
    }

  /* Reply body must be sent. The body may have zero length, but body size
   * must be indicated by headers ('Content-Length:' or
   * 'Transfer-Encoding: chunked'). */
  if (logf)
    fprintf(logf, "branch: default -> RP_BODY_SEND\n");
  if (logf) fclose(logf);
  return RP_BODY_SEND;
}
