static enum replyBodyUse
is_reply_body_needed (struct MHD_Connection *connection,
                      unsigned int rcode)
{
  struct MHD_Connection *const c = connection; /**< a short alias */
  FILE *log = NULL;

  /* Open log for append; best-effort logging only. */
  log = fopen("/tmp/logs/6.txt", "a");
  if (log)
    fprintf(log, "ENTER is_reply_body_needed: connection=%p rcode=%u\n", (void *)connection, rcode);

  mhd_assert (100 <= rcode);
  mhd_assert (999 >= rcode);

  if (199 >= rcode)
    {
      if (log)
        {
          fprintf(log, "DECISION: rcode <= 199 -> RP_BODY_NONE\n");
          fclose(log);
        }
      return RP_BODY_NONE;
    }

  if (MHD_HTTP_NO_CONTENT == rcode)
    {
      if (log)
        {
          fprintf(log, "DECISION: HTTP 204 (NO_CONTENT) -> RP_BODY_NONE\n");
          fclose(log);
        }
      return RP_BODY_NONE;
    }

  /* The following blocks are disabled in the original source via #if 0.
   * We do not reintroduce them, but we log their existence and the
   * hypothetical conditions so that a tester can observe that such code
   * would be relevant yet is not executed. This provides evidence of the
   * unreachable / compiled-out scenario reported against the project. */
  if (log)
    {
      fprintf(log, "NOTE: upgrade handler check was disabled in source via #if 0\n");
    }

  if (log)
    {
      int method = (c ? (int)c->rq.http_mthd : -1);
      if (c && (MHD_HTTP_MTHD_CONNECT == c->rq.http_mthd) && (2 == rcode / 100))
        {
          /* This branch mirrors the condition guarded by #if 0 in the
           * original source. We only log that it would have been taken. */
          fprintf(log, "HYPOTHETICAL: CONNECT method with 2xx response detected -> would take compiled-out path\n");
        }
      else
        {
          fprintf(log, "INFO: CONNECT-path not applicable: method=%d rcode=%u\n", method, rcode);
        }
    }

  /* Reply body headers could be used.
   * Check whether reply body itself must be used. */
  if (MHD_HTTP_MTHD_HEAD == c->rq.http_mthd)
    {
      if (log)
        {
          fprintf(log, "DECISION: HEAD method -> RP_BODY_HEADERS_ONLY\n");
          fclose(log);
        }
      return RP_BODY_HEADERS_ONLY;
    }

  if (MHD_HTTP_NOT_MODIFIED == rcode)
    {
      if (log)
        {
          fprintf(log, "DECISION: HTTP 304 (NOT_MODIFIED) -> RP_BODY_HEADERS_ONLY\n");
          fclose(log);
        }
      return RP_BODY_HEADERS_ONLY;
    }

  /* Reply body must be sent. The body may have zero length, but body size
   * must be indicated by headers ('Content-Length:' or
   * 'Transfer-Encoding: chunked'). */
  if (log)
    {
      fprintf(log, "DECISION: default -> RP_BODY_SEND\n");
      fclose(log);
    }
  return RP_BODY_SEND;
}
