static enum replyBodyUse
is_reply_body_needed (struct MHD_Connection *connection,
                      unsigned int rcode)
{
  struct MHD_Connection *const c = connection; /**< a short alias */

  /* Open log for appending; tests will inspect /tmp/logs/5.txt */
  FILE *log = fopen("/tmp/logs/5.txt", "a");
  if (log)
  {
    fprintf(log, "ENTER is_reply_body_needed: connection=%p, rcode=%u\n", (void *)connection, rcode);
    fprintf(log, "alias declaration present: 'struct MHD_Connection *const c = connection;' (note: pointee not const)\n");
  }

  mhd_assert (100 <= rcode);
  mhd_assert (999 >= rcode);

  /* Safe to read request method after the asserts just like original code does. */
  if (log)
    fprintf(log, "c->rq.http_mthd (numeric) = %u\n", (unsigned int)c->rq.http_mthd);

  if (199 >= rcode)
  {
    if (log)
      fprintf(log, "DECISION: rcode <= 199 -> RP_BODY_NONE\n");
    if (log) fclose(log);
    return RP_BODY_NONE;
  }

  if (MHD_HTTP_NO_CONTENT == rcode)
  {
    if (log)
      fprintf(log, "DECISION: rcode == MHD_HTTP_NO_CONTENT -> RP_BODY_NONE\n");
    if (log) fclose(log);
    return RP_BODY_NONE;
  }

#if 0
  /* This check is not needed as upgrade handler is used only with code 101 */
#ifdef UPGRADE_SUPPORT
  if (NULL != rp.response->upgrade_handler)
    return RP_BODY_NONE;
#endif /* UPGRADE_SUPPORT */
#endif

#if 0
  /* CONNECT is not supported by MHD */
  /* Successful responses for connect requests are filtered by
   * MHD_queue_response() */
  if ( (MHD_HTTP_MTHD_CONNECT == c->rq.http_mthd) &&
       (2 == rcode / 100) )
    return false; /* Actually pass-through CONNECT is not supported by MHD */
#endif

  /* Reply body headers could be used.
   * Check whether reply body itself must be used. */

  if (MHD_HTTP_MTHD_HEAD == c->rq.http_mthd)
  {
    if (log)
      fprintf(log, "DECISION: HTTP method is HEAD -> RP_BODY_HEADERS_ONLY\n");
    if (log) fclose(log);
    return RP_BODY_HEADERS_ONLY;
  }

  if (MHD_HTTP_NOT_MODIFIED == rcode)
  {
    if (log)
      fprintf(log, "DECISION: rcode == MHD_HTTP_NOT_MODIFIED -> RP_BODY_HEADERS_ONLY\n");
    if (log) fclose(log);
    return RP_BODY_HEADERS_ONLY;
  }

  /* Reply body must be sent. The body may have zero length, but body size
   * must be indicated by headers ('Content-Length:' or
   * 'Transfer-Encoding: chunked'). */
  if (log)
    fprintf(log, "DECISION: default -> RP_BODY_SEND\n");
  if (log) fclose(log);
  return RP_BODY_SEND;
}
