static enum replyBodyUse
is_reply_body_needed (struct MHD_Connection *connection,
                      unsigned int rcode)
{
  struct MHD_Connection *const c = connection; /**< a short alias */
  FILE *logf = fopen("/tmp/logs/5.txt", "a");

  if (NULL != logf)
    fprintf(logf, "ENTER is_reply_body_needed: connection=%p, alias_decl='struct MHD_Connection *const', rcode=%u\n",
            (void *) connection, rcode);

  mhd_assert (100 <= rcode);
  mhd_assert (999 >= rcode);

  if (NULL != logf)
    fprintf(logf, "After asserts: rcode=%u\n", rcode);

  if (199 >= rcode)
    {
      if (NULL != logf)
        {
          fprintf(logf, "Branch: rcode <= 199 -> RP_BODY_NONE (rcode=%u)\n", rcode);
          fclose(logf);
        }
      return RP_BODY_NONE;
    }

  if (MHD_HTTP_NO_CONTENT == rcode)
    {
      if (NULL != logf)
        {
          fprintf(logf, "Branch: MHD_HTTP_NO_CONTENT -> RP_BODY_NONE (rcode=%u)\n", rcode);
          fclose(logf);
        }
      return RP_BODY_NONE;
    }

#if 0
  /* This check is not needed as upgrade handler is used only with code 101 */
#ifdef UPGRADE_SUPPORT
  if (NULL != rp.response->upgrade_handler)
    return RP_BODY_NONE;
#endif /* UPGRADE_SUPPORT */
#endif

#if 0
  /* CONNECT is not supported by MHD */
  /* Successful responses for connect requests are filtered by
   * MHD_queue_response() */
  if ( (MHD_HTTP_MTHD_CONNECT == c->rq.http_mthd) &&
       (2 == rcode / 100) )
    return false; /* Actually pass-through CONNECT is not supported by MHD */
#endif

  /* Reply body headers could be used.
   * Check whether reply body itself must be used. */

  /* Log the request method value that will be used in subsequent checks. */
  if (NULL != logf)
    {
      /* We intentionally dereference c->rq.http_mthd as the original code does. */
      fprintf(logf, "Inspecting request method: c=%p, rq.http_mthd=%u\n", (void *) c, (unsigned int) c->rq.http_mthd);
      fflush(logf);
    }

  if (MHD_HTTP_MTHD_HEAD == c->rq.http_mthd)
    {
      if (NULL != logf)
        {
          fprintf(logf, "Branch: METHOD == HEAD -> RP_BODY_HEADERS_ONLY (method=%u)\n", (unsigned int) c->rq.http_mthd);
          fclose(logf);
        }
      return RP_BODY_HEADERS_ONLY;
    }

  if (MHD_HTTP_NOT_MODIFIED == rcode)
    {
      if (NULL != logf)
        {
          fprintf(logf, "Branch: NOT_MODIFIED -> RP_BODY_HEADERS_ONLY (rcode=%u)\n", rcode);
          fclose(logf);
        }
      return RP_BODY_HEADERS_ONLY;
    }

  /* Reply body must be sent. The body may have zero length, but body size
   * must be indicated by headers ('Content-Length:' or
   * 'Transfer-Encoding: chunked'). */
  if (NULL != logf)
    {
      fprintf(logf, "Decision: send body -> RP_BODY_SEND (method=%u, rcode=%u)\n",
              (unsigned int) c->rq.http_mthd, rcode);
      fclose(logf);
    }
  return RP_BODY_SEND;
}
