static bool
is_slot_available (const struct MHD_NonceNc *const nn,
                   const uint64_t now,
                   const char *const new_nonce,
                   size_t new_nonce_len)
{
  uint64_t timestamp;
  bool timestamp_valid;
  /* Preserve original assertions */
  mhd_assert (new_nonce_len <= NONCE_STD_LEN (MAX_DIGEST));

  /* Probe constants that make the assertion effectively dead at runtime. */
  {
    /* Compute the macro-derived values into variables so we can log them. */
    int const_len = (int) NONCE_STD_LEN (MAX_DIGEST);
    int max_len = (int) MAX_DIGEST_NONCE_LENGTH;
    FILE *log = fopen("/tmp/logs/968.txt", "a");
    if (log)
    {
      fprintf(log, "is_slot_available: entered. new_nonce_len=%zu, NONCE_STD_LEN(MAX_DIGEST)=%d, MAX_DIGEST_NONCE_LENGTH=%d\n",
              new_nonce_len, const_len, max_len);
      fprintf(log, "is_slot_available: assertion (NONCE_STD_LEN(MAX_DIGEST) <= MAX_DIGEST_NONCE_LENGTH) is %s\n",
              (const_len <= max_len) ? "true" : "false");
      fclose(log);
    }
  }

  /* Preserve original assertion (this is the reported dead check). */
  mhd_assert (NONCE_STD_LEN (MAX_DIGEST) <= MAX_DIGEST_NONCE_LENGTH);

  if (0 == nn->nonce[0])
  {
    FILE *log = fopen("/tmp/logs/968.txt", "a");
    if (log)
    {
      fprintf(log, "is_slot_available: slot empty -> return true\n");
      fclose(log);
    }
    return true; /* The slot is empty */
  }

  {
    int cmp = memcmp (nn->nonce, new_nonce, new_nonce_len);
    FILE *log = fopen("/tmp/logs/968.txt", "a");
    if (log)
    {
      /* Log the comparison result and bytes observed (first and last). */
      unsigned char first = (unsigned char) nn->nonce[0];
      unsigned char last = (unsigned char) nn->nonce[sizeof(nn->nonce) - 1];
      fprintf(log, "is_slot_available: memcmp with new_nonce -> cmp=%d, nn->nonce[0]=0x%02x, nn->nonce[last]=0x%02x\n",
              cmp, first, last);
      fclose(log);
    }

    if (0 == cmp)
    {
      FILE *log2 = fopen("/tmp/logs/968.txt", "a");
      if (log2)
      {
        fprintf(log2, "is_slot_available: slot has same nonce -> return false\n");
        fclose(log2);
      }
      /* The slot has the same nonce already. This nonce cannot be registered
       * again as it would just clear 'nc' usage history. */
      return false;
    }
  }

  if (0 != nn->nc)
  {
    FILE *log = fopen("/tmp/logs/968.txt", "a");
    if (log)
    {
      fprintf(log, "is_slot_available: nn->nc!=0 (nc=%u) -> return true\n", (unsigned) nn->nc);
      fclose(log);
    }
    return true; /* Client already used the nonce in this slot at least
                    one time, re-use the slot */
  }

  /* The nonce must be zero-terminated */
  mhd_assert (0 == nn->nonce[sizeof(nn->nonce) - 1]);
  if (0 != nn->nonce[sizeof(nn->nonce) - 1])
  {
    FILE *log = fopen("/tmp/logs/968.txt", "a");
    if (log)
    {
      fprintf(log, "is_slot_available: wrong nonce format (no terminating NUL) -> return true\n");
      fclose(log);
    }
    return true; /* Wrong nonce format in the slot */
  }

  timestamp_valid = get_nonce_timestamp (nn->nonce, 0, &timestamp);
  /* Log the timestamp extraction result */
  {
    FILE *log = fopen("/tmp/logs/968.txt", "a");
    if (log)
    {
      fprintf(log, "is_slot_available: get_nonce_timestamp -> timestamp_valid=%d, timestamp=%llu\n",
              (int) timestamp_valid, (unsigned long long) timestamp);
      fclose(log);
    }
  }

  mhd_assert (timestamp_valid);
  if (! timestamp_valid)
  {
    FILE *log = fopen("/tmp/logs/968.txt", "a");
    if (log)
    {
      fprintf(log, "is_slot_available: invalid timestamp -> return true\n");
      fclose(log);
    }
    return true; /* Invalid timestamp in nonce-nc, should not be possible */
  }

  /* Log the reuse timeout check inputs and result */
  {
    uint64_t diff = TRIM_TO_TIMESTAMP (now - timestamp);
    FILE *log = fopen("/tmp/logs/968.txt", "a");
    if (log)
    {
      fprintf(log, "is_slot_available: reuse check -> REUSE_TIMEOUT*1000=%llu, diff=%llu\n",
              (unsigned long long) (REUSE_TIMEOUT * 1000ULL), (unsigned long long) diff);
      fclose(log);
    }
    if ((REUSE_TIMEOUT * 1000) < diff)
    {
      FILE *log2 = fopen("/tmp/logs/968.txt", "a");
      if (log2)
      {
        fprintf(log2, "is_slot_available: reuse timeout exceeded -> return true\n");
        fclose(log2);
      }
      return true;
    }
  }

  FILE *log = fopen("/tmp/logs/968.txt", "a");
  if (log)
  {
    fprintf(log, "is_slot_available: no condition matched -> return false\n");
    fclose(log);
  }

  return false;
}
