static bool
is_slot_available (const struct MHD_NonceNc *const nn,
                   const uint64_t now,
                   const char *const new_nonce,
                   size_t new_nonce_len)
{
  FILE *log = fopen("/tmp/logs/963.txt", "a");
  if (log)
  {
    fprintf(log, "is_slot_available ENTRY: nn=%p now=%llu new_nonce=%p new_nonce_len=%zu\n",
            (void *) nn, (unsigned long long) now, (void *) new_nonce, new_nonce_len);
  }

  uint64_t timestamp;
  bool timestamp_valid;
  mhd_assert (new_nonce_len <= NONCE_STD_LEN (MAX_DIGEST));
  mhd_assert (NONCE_STD_LEN (MAX_DIGEST) <= MAX_DIGEST_NONCE_LENGTH);

  /* Check empty slot */
  if (0 == nn->nonce[0])
  {
    if (log)
      fprintf(log, "branch: empty slot (nn->nonce[0]==0) -> returning true\n");
    if (log)
      fclose(log);
    return true; /* The slot is empty */
  }

  if (log)
  {
    fprintf(log, "nn->nonce[0]=0x%02x sizeof(nn->nonce)=%zu\n",
            (unsigned char) nn->nonce[0], sizeof (nn->nonce));
    fprintf(log, "about to memcmp with new_nonce_len=%zu (nn=%p new_nonce=%p)\n",
            new_nonce_len, (void *) nn->nonce, (void *) new_nonce);
  }

  /* Compare nonces. This is the place the reported violation may occur. */
  int cmp = memcmp (nn->nonce, new_nonce, new_nonce_len);
  if (log)
    fprintf(log, "memcmp returned %d\n", cmp);

  if (0 == cmp)
  {
    if (log)
      fprintf(log, "branch: same nonce already present -> returning false\n");
    if (log)
      fclose(log);
    /* The slot has the same nonce already. This nonce cannot be registered
     * again as it would just clear 'nc' usage history. */
    return false;
  }

  if (0 != nn->nc)
  {
    if (log)
      fprintf(log, "branch: nn->nc != 0 -> returning true\n");
    if (log)
      fclose(log);
    return true; /* Client already used the nonce in this slot at least
                    one time, re-use the slot */
  }

  /* The nonce must be zero-terminated */
  mhd_assert (0 == nn->nonce[sizeof(nn->nonce) - 1]);
  if (0 != nn->nonce[sizeof(nn->nonce) - 1])
  {
    if (log)
      fprintf(log, "branch: nonce not zero-terminated -> returning true\n");
    if (log)
      fclose(log);
    return true; /* Wrong nonce format in the slot */
  }

  timestamp_valid = get_nonce_timestamp (nn->nonce, 0, &timestamp);
  if (log)
    fprintf(log, "get_nonce_timestamp returned %d timestamp=%llu\n",
            (int) timestamp_valid, (unsigned long long) timestamp);
  mhd_assert (timestamp_valid);
  if (! timestamp_valid)
  {
    if (log)
      fprintf(log, "branch: invalid timestamp -> returning true\n");
    if (log)
      fclose(log);
    return true; /* Invalid timestamp in nonce-nc, should not be possible */
  }

  if ((REUSE_TIMEOUT * 1000) < TRIM_TO_TIMESTAMP (now - timestamp))
  {
    if (log)
      fprintf(log, "branch: reuse timeout exceeded -> returning true\n");
    if (log)
      fclose(log);
    return true;
  }

  if (log)
    fprintf(log, "branch: default -> returning false\n");
  if (log)
    fclose(log);

  return false;
}
