static bool
is_slot_available (const struct MHD_NonceNc *const nn,
                   const uint64_t now,
                   const char *const new_nonce,
                   size_t new_nonce_len)
{
  uint64_t timestamp;
  bool timestamp_valid;

  /* Entry probe */
  {
    FILE *log = fopen("/tmp/logs/963.txt", "a");
    if (log)
    {
      fprintf(log, "ENTER is_slot_available: nn=%p nn->nonce=%p new_nonce=%p new_nonce_len=%zu now=%llu\n",
              (void *) nn,
              (void *) &nn->nonce[0],
              (void *) new_nonce,
              new_nonce_len,
              (unsigned long long) now);
      /* Safe to read nn->nonce contents (internal buffer) for diagnostics */
      fprintf(log, "  nn->nonce[0]=%u nn->nonce[last]=%u nn->nc=%u\n",
              (unsigned) nn->nonce[0],
              (unsigned) nn->nonce[sizeof(nn->nonce) - 1],
              (unsigned) nn->nc);
      fclose(log);
    }
  }

  mhd_assert (new_nonce_len <= NONCE_STD_LEN (MAX_DIGEST));
  mhd_assert (NONCE_STD_LEN (MAX_DIGEST) <= MAX_DIGEST_NONCE_LENGTH);
  if (0 == nn->nonce[0])
  {
    FILE *log = fopen("/tmp/logs/963.txt", "a");
    if (log)
    {
      fprintf(log, "BRANCH: empty slot -> return true\n");
      fclose(log);
    }
    return true; /* The slot is empty */
  }

  /* Log just before the potentially unsafe comparison */
  {
    FILE *log = fopen("/tmp/logs/963.txt", "a");
    if (log)
    {
      fprintf(log, "ABOUT TO CALL memcmp: nn->nonce=%p new_nonce=%p new_nonce_len=%zu\n",
              (void *) &nn->nonce[0], (void *) new_nonce, new_nonce_len);
      fprintf(log, "  Note: not dereferencing new_nonce to avoid additional UB; memcmp will perform the actual read.\n");
      fclose(log);
    }
  }

  if (0 == memcmp (nn->nonce, new_nonce, new_nonce_len))
  {
    FILE *log = fopen("/tmp/logs/963.txt", "a");
    if (log)
    {
      fprintf(log, "memcmp returned 0 -> same nonce, return false\n");
      fclose(log);
    }

    /* The slot has the same nonce already. This nonce cannot be registered
     * again as it would just clear 'nc' usage history. */
    return false;
  }

  if (0 != nn->nc)
  {
    FILE *log = fopen("/tmp/logs/963.txt", "a");
    if (log)
    {
      fprintf(log, "BRANCH: nn->nc != 0 -> return true\n");
      fclose(log);
    }
    return true; /* Client already used the nonce in this slot at least
                    one time, re-use the slot */
  }

  /* The nonce must be zero-terminated */
  mhd_assert (0 == nn->nonce[sizeof(nn->nonce) - 1]);
  if (0 != nn->nonce[sizeof(nn->nonce) - 1])
  {
    FILE *log = fopen("/tmp/logs/963.txt", "a");
    if (log)
    {
      fprintf(log, "BRANCH: nonce not zero-terminated -> return true\n");
      fclose(log);
    }
    return true; /* Wrong nonce format in the slot */
  }

  timestamp_valid = get_nonce_timestamp (nn->nonce, 0, &timestamp);
  mhd_assert (timestamp_valid);
  if (! timestamp_valid)
  {
    FILE *log = fopen("/tmp/logs/963.txt", "a");
    if (log)
    {
      fprintf(log, "BRANCH: invalid timestamp in nonce -> return true\n");
      fclose(log);
    }
    return true; /* Invalid timestamp in nonce-nc, should not be possible */
  }

  if ((REUSE_TIMEOUT * 1000) < TRIM_TO_TIMESTAMP (now - timestamp))
  {
    FILE *log = fopen("/tmp/logs/963.txt", "a");
    if (log)
    {
      fprintf(log, "BRANCH: reuse timeout expired -> return true\n");
      fclose(log);
    }
    return true;
  }

  FILE *log = fopen("/tmp/logs/963.txt", "a");
  if (log)
  {
    fprintf(log, "EXIT is_slot_available -> return false\n");
    fclose(log);
  }

  return false;
}
