static bool
is_slot_available (const struct MHD_NonceNc *const nn,
                   const uint64_t now,
                   const char *const new_nonce,
                   size_t new_nonce_len)
{
  uint64_t timestamp;
  bool timestamp_valid;
  int cmp_res = 0;
  FILE *logf = fopen("/tmp/logs/963.txt", "a");

  if (logf)
  {
    fprintf(logf, "is_slot_available ENTRY: nn=%p now=%llu new_nonce=%p new_nonce_len=%zu sizeof(nn->nonce)=%zu nn->nonce[0]=0x%02x nn->nc=%u\n",
            (void *)nn,
            (unsigned long long) now,
            (const void *) new_nonce,
            new_nonce_len,
            sizeof(nn->nonce),
            (unsigned int)(unsigned char) nn->nonce[0],
            (unsigned int) nn->nc);

    /* dump nn->nonce bytes (safe: within bounds of the object) */
    fprintf(logf, "nn->nonce bytes: ");
    for (size_t i = 0; i < sizeof(nn->nonce); ++i)
      fprintf(logf, "%02x", (unsigned int)(unsigned char) nn->nonce[i]);
    fprintf(logf, "\n");

    fclose(logf);
  }

  mhd_assert (new_nonce_len <= NONCE_STD_LEN (MAX_DIGEST));
  mhd_assert (NONCE_STD_LEN (MAX_DIGEST) <= MAX_DIGEST_NONCE_LENGTH);
  if (0 == nn->nonce[0])
  {
    logf = fopen("/tmp/logs/963.txt", "a");
    if (logf)
    {
      fprintf(logf, "is_slot_available: slot empty -> return true\n");
      fclose(logf);
    }
    return true; /* The slot is empty */
  }

  /* Perform memcmp exactly as original code, but capture and log the result. */
  cmp_res = memcmp (nn->nonce, new_nonce, new_nonce_len);
  logf = fopen("/tmp/logs/963.txt", "a");
  if (logf)
  {
    fprintf(logf, "memcmp(nn->nonce, new_nonce, %zu) = %d\n", new_nonce_len, cmp_res);
    fclose(logf);
  }

  if (0 == cmp_res)
  {
    logf = fopen("/tmp/logs/963.txt", "a");
    if (logf)
    {
      fprintf(logf, "is_slot_available: same nonce already present -> return false\n");
      fclose(logf);
    }

    /* The slot has the same nonce already. This nonce cannot be registered
     * again as it would just clear 'nc' usage history. */
    return false;
  }

  if (0 != nn->nc)
  {
    logf = fopen("/tmp/logs/963.txt", "a");
    if (logf)
    {
      fprintf(logf, "is_slot_available: nn->nc != 0 (=%u) -> return true\n", (unsigned int) nn->nc);
      fclose(logf);
    }

    return true; /* Client already used the nonce in this slot at least
                    one time, re-use the slot */
  }

  /* The nonce must be zero-terminated */
  mhd_assert (0 == nn->nonce[sizeof(nn->nonce) - 1]);
  if (0 != nn->nonce[sizeof(nn->nonce) - 1])
  {
    logf = fopen("/tmp/logs/963.txt", "a");
    if (logf)
    {
      fprintf(logf, "is_slot_available: bad nonce format (no zero terminator) -> return true\n");
      fclose(logf);
    }

    return true; /* Wrong nonce format in the slot */
  }

  timestamp_valid = get_nonce_timestamp (nn->nonce, 0, &timestamp);
  logf = fopen("/tmp/logs/963.txt", "a");
  if (logf)
  {
    fprintf(logf, "get_nonce_timestamp returned %d, timestamp=%llu\n",
            (int) timestamp_valid,
            (unsigned long long) timestamp);
    fclose(logf);
  }

  mhd_assert (timestamp_valid);
  if (! timestamp_valid)
  {
    logf = fopen("/tmp/logs/963.txt", "a");
    if (logf)
    {
      fprintf(logf, "is_slot_available: invalid timestamp -> return true\n");
      fclose(logf);
    }

    return true; /* Invalid timestamp in nonce-nc, should not be possible */
  }

  if ((REUSE_TIMEOUT * 1000) < TRIM_TO_TIMESTAMP (now - timestamp))
  {
    logf = fopen("/tmp/logs/963.txt", "a");
    if (logf)
    {
      fprintf(logf, "is_slot_available: reuse timeout exceeded (now=%llu timestamp=%llu) -> return true\n",
              (unsigned long long) now,
              (unsigned long long) timestamp);
      fclose(logf);
    }

    return true;
  }

  logf = fopen("/tmp/logs/963.txt", "a");
  if (logf)
  {
    fprintf(logf, "is_slot_available: slot NOT available -> return false\n");
    fclose(logf);
  }

  return false;
}
