static enum MHD_ConnKeepAlive
keepalive_possible (struct MHD_Connection *connection)
{
  struct MHD_Connection *const c = connection; /**< a short alias */
  struct MHD_Response *const r = c->rp.response;  /**< a short alias */
  enum MHD_ConnKeepAlive res = MHD_CONN_MUST_CLOSE;
  FILE *log = fopen("/tmp/logs/4325.txt", "a");

  if (log)
  {
    fprintf(log, "keepalive_possible: ENTRY connection=%p c=%p r=%p\n",
            (void *)connection, (void *)c, (void *)r);
    fprintf(log, "state: keepalive=%d stop_with_error=%d discard_request=%d read_closed=%d\n",
            c->keepalive, c->stop_with_error, c->discard_request, c->read_closed);
    fprintf(log, "state: rq.http_ver=%d r->flags=0x%X r->flags_auto=0x%X r->upgrade_handler=%p\n",
            c->rq.http_ver, (unsigned)r->flags, (unsigned)r->flags_auto, (void *)r->upgrade_handler);
  }

  mhd_assert (NULL != r);
  if (MHD_CONN_MUST_CLOSE == c->keepalive)
  {
    if (log) fprintf(log, "branch: c->keepalive == MHD_CONN_MUST_CLOSE -> MHD_CONN_MUST_CLOSE\n");
    res = MHD_CONN_MUST_CLOSE;
    goto done;
  }

#ifdef UPGRADE_SUPPORT
  /* TODO: Move below the next check when MHD stops closing connections
   * when response is queued in first callback */
  if (NULL != r->upgrade_handler)
  {
    if (log)
    {
      fprintf(log, "branch: r->upgrade_handler != NULL -> MHD_CONN_MUST_UPGRADE (will assert some conditions)\n");
      fprintf(log, "assert-check: MHD_IS_HTTP_VER_SUPPORTED(c->rq.http_ver)=%d stop_with_error=%d\n",
              MHD_IS_HTTP_VER_SUPPORTED (c->rq.http_ver), c->stop_with_error);
    }

    /* No "close" token is enforced by 'add_response_header_connection()' */
    mhd_assert (0 == (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
    /* Valid HTTP version is enforced by 'MHD_queue_response()' */
    mhd_assert (MHD_IS_HTTP_VER_SUPPORTED (c->rq.http_ver));
    mhd_assert (! c->stop_with_error);
    res = MHD_CONN_MUST_UPGRADE;
    goto done;
  }
#endif /* UPGRADE_SUPPORT */

  if (log) fprintf(log, "after upgrade check: (!stop_with_error)||discard_request = %d\n",
                  (! c->stop_with_error) || (c->discard_request));
  mhd_assert ( (! c->stop_with_error) || (c->discard_request));
  if ((c->read_closed) || (c->discard_request))
  {
    if (log) fprintf(log, "branch: read_closed || discard_request -> MHD_CONN_MUST_CLOSE\n");
    res = MHD_CONN_MUST_CLOSE;
    goto done;
  }

  if (0 != (r->flags & MHD_RF_HTTP_1_0_COMPATIBLE_STRICT))
  {
    if (log) fprintf(log, "branch: r->flags & MHD_RF_HTTP_1_0_COMPATIBLE_STRICT -> MHD_CONN_MUST_CLOSE\n");
    res = MHD_CONN_MUST_CLOSE;
    goto done;
  }
  if (0 != (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE))
  {
    if (log) fprintf(log, "branch: r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE -> MHD_CONN_MUST_CLOSE\n");
    res = MHD_CONN_MUST_CLOSE;
    goto done;
  }

  if (! MHD_IS_HTTP_VER_SUPPORTED (c->rq.http_ver))
  {
    if (log) fprintf(log, "branch: !MHD_IS_HTTP_VER_SUPPORTED(c->rq.http_ver) -> MHD_CONN_MUST_CLOSE\n");
    res = MHD_CONN_MUST_CLOSE;
    goto done;
  }

  if (MHD_lookup_header_s_token_ci (c,
                                    MHD_HTTP_HEADER_CONNECTION,
                                    "close"))
  {
    if (log) fprintf(log, "branch: lookup header 'Connection: close' -> MHD_CONN_MUST_CLOSE\n");
    res = MHD_CONN_MUST_CLOSE;
    goto done;
  }

  if ((MHD_HTTP_VER_1_0 == connection->rq.http_ver) ||
      (0 != (connection->rp.response->flags & MHD_RF_HTTP_1_0_SERVER)))
  {
    if (MHD_lookup_header_s_token_ci (connection,
                                      MHD_HTTP_HEADER_CONNECTION,
                                      "Keep-Alive"))
    {
      if (log) fprintf(log, "branch: HTTP/1.0 and has 'Keep-Alive' header -> MHD_CONN_USE_KEEPALIVE\n");
      res = MHD_CONN_USE_KEEPALIVE;
      goto done;
    }

    if (log) fprintf(log, "branch: HTTP/1.0 and no 'Keep-Alive' -> MHD_CONN_MUST_CLOSE\n");
    res = MHD_CONN_MUST_CLOSE;
    goto done;
  }

  if (MHD_IS_HTTP_VER_1_1_COMPAT (c->rq.http_ver))
  {
    if (log) fprintf(log, "branch: MHD_IS_HTTP_VER_1_1_COMPAT -> MHD_CONN_USE_KEEPALIVE\n");
    res = MHD_CONN_USE_KEEPALIVE;
    goto done;
  }

  if (log) fprintf(log, "branch: default -> MHD_CONN_MUST_CLOSE\n");
  res = MHD_CONN_MUST_CLOSE;

done:
  if (log)
  {
    fprintf(log, "keepalive_possible: EXIT result=%d\n\n", res);
    fclose(log);
  }
  return res;
}
