static enum MHD_ConnKeepAlive
keepalive_possible (struct MHD_Connection *connection)
{
  struct MHD_Connection *const c = connection; /**< a short alias */
  struct MHD_Response *const r = c->rp.response;  /**< a short alias */

  /* Open a log file to record control flow and relevant state for
     reproducing the Rule 8.13 violation scenario. Append mode to
     avoid clobbering previous runs. */
  FILE *mhd_log = fopen("/tmp/logs/4325.txt", "a");
  if (mhd_log)
  {
    fprintf(mhd_log, "keepalive_possible: entered, connection=%p, c=%p, r=%p\n",
            (void *) connection, (void *) c, (void *) r);
  }

  mhd_assert (NULL != r);
  if (MHD_CONN_MUST_CLOSE == c->keepalive)
  {
    if (mhd_log)
    {
      fprintf(mhd_log, "return: MHD_CONN_MUST_CLOSE (c->keepalive == MHD_CONN_MUST_CLOSE)\n");
      fclose(mhd_log);
    }
    return MHD_CONN_MUST_CLOSE;
  }

#ifdef UPGRADE_SUPPORT
  /* TODO: Move below the next check when MHD stops closing connections
   * when response is queued in first callback */
  if (NULL != r->upgrade_handler)
  {
    /* No "close" token is enforced by 'add_response_header_connection()' */
    mhd_assert (0 == (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
    /* Valid HTTP version is enforced by 'MHD_queue_response()' */
    mhd_assert (MHD_IS_HTTP_VER_SUPPORTED (c->rq.http_ver));
    mhd_assert (! c->stop_with_error);

    if (mhd_log)
    {
      fprintf(mhd_log, "upgrade handler present: r->upgrade_handler=%p\n", (void *) r->upgrade_handler);
      fprintf(mhd_log, "r->flags=0x%08x, r->flags_auto=0x%08x, c->rq.http_ver=%d, c->stop_with_error=%d\n",
              (unsigned) r->flags, (unsigned) r->flags_auto, (int) c->rq.http_ver, (int) c->stop_with_error);
      fprintf(mhd_log, "return: MHD_CONN_MUST_UPGRADE\n");
      fclose(mhd_log);
    }

    return MHD_CONN_MUST_UPGRADE;
  }
#endif /* UPGRADE_SUPPORT */

  if (mhd_log)
  {
    fprintf(mhd_log, "assert check: (!c->stop_with_error) || (c->discard_request) => (!%d) || (%d)\n",
            (int) c->stop_with_error, (int) c->discard_request);
  }

  if ((c->read_closed) || (c->discard_request))
  {
    if (mhd_log)
    {
      fprintf(mhd_log, "return: MHD_CONN_MUST_CLOSE (c->read_closed=%d || c->discard_request=%d)\n",
              (int) c->read_closed, (int) c->discard_request);
      fclose(mhd_log);
    }
    return MHD_CONN_MUST_CLOSE;
  }

  if (0 != (r->flags & MHD_RF_HTTP_1_0_COMPATIBLE_STRICT))
  {
    if (mhd_log)
    {
      fprintf(mhd_log, "return: MHD_CONN_MUST_CLOSE (r->flags & MHD_RF_HTTP_1_0_COMPATIBLE_STRICT)\n");
      fclose(mhd_log);
    }
    return MHD_CONN_MUST_CLOSE;
  }
  if (0 != (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE))
  {
    if (mhd_log)
    {
      fprintf(mhd_log, "return: MHD_CONN_MUST_CLOSE (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE)\n");
      fclose(mhd_log);
    }
    return MHD_CONN_MUST_CLOSE;
  }

  {
    int is_supported = MHD_IS_HTTP_VER_SUPPORTED (c->rq.http_ver);
    if (mhd_log)
      fprintf(mhd_log, "MHD_IS_HTTP_VER_SUPPORTED(c->rq.http_ver) => %d (http_ver=%d)\n",
              is_supported, (int) c->rq.http_ver);
    if (! is_supported)
    {
      if (mhd_log)
      {
        fprintf(mhd_log, "return: MHD_CONN_MUST_CLOSE (http version not supported)\n");
        fclose(mhd_log);
      }
      return MHD_CONN_MUST_CLOSE;
    }
  }

  /* Check for explicit "close" token on Connection header */
  {
    const char *hdr_close = MHD_lookup_header_s_token_ci (c,
                                                          MHD_HTTP_HEADER_CONNECTION,
                                                          "close");
    if (mhd_log)
      fprintf(mhd_log, "MHD_lookup_header_s_token_ci(..., \"close\") => %p\n", (void *) hdr_close);
    if (hdr_close)
    {
      if (mhd_log)
      {
        fprintf(mhd_log, "return: MHD_CONN_MUST_CLOSE (Connection: close present)\n");
        fclose(mhd_log);
      }
      return MHD_CONN_MUST_CLOSE;
    }
  }

  /* HTTP/1.0 special handling: note original uses 'connection' directly here */
  if ((MHD_HTTP_VER_1_0 == connection->rq.http_ver) ||
      (0 != (connection->rp.response->flags & MHD_RF_HTTP_1_0_SERVER)))
  {
    if (mhd_log)
    {
      fprintf(mhd_log, "HTTP/1.0 branch: connection->rq.http_ver=%d, connection->rp.response->flags=0x%08x\n",
              (int) connection->rq.http_ver, (unsigned) connection->rp.response->flags);
    }

    const char *hdr_keepalive = MHD_lookup_header_s_token_ci (connection,
                                                              MHD_HTTP_HEADER_CONNECTION,
                                                              "Keep-Alive");
    if (mhd_log)
      fprintf(mhd_log, "MHD_lookup_header_s_token_ci(..., \"Keep-Alive\") => %p\n", (void *) hdr_keepalive);
    if (hdr_keepalive)
    {
      if (mhd_log)
      {
        fprintf(mhd_log, "return: MHD_CONN_USE_KEEPALIVE (HTTP/1.0 with Keep-Alive header)\n");
        fclose(mhd_log);
      }
      return MHD_CONN_USE_KEEPALIVE;
    }

    if (mhd_log)
    {
      fprintf(mhd_log, "return: MHD_CONN_MUST_CLOSE (HTTP/1.0 without Keep-Alive)\n");
      fclose(mhd_log);
    }
    return MHD_CONN_MUST_CLOSE;
  }

  /* HTTP/1.1 or compatible -> keepalive */
  if (MHD_IS_HTTP_VER_1_1_COMPAT (c->rq.http_ver))
  {
    if (mhd_log)
    {
      fprintf(mhd_log, "return: MHD_CONN_USE_KEEPALIVE (MHD_IS_HTTP_VER_1_1_COMPAT)\n");
      fclose(mhd_log);
    }
    return MHD_CONN_USE_KEEPALIVE;
  }

  if (mhd_log)
  {
    fprintf(mhd_log, "return: MHD_CONN_MUST_CLOSE (default)\n");
    fclose(mhd_log);
  }
  return MHD_CONN_MUST_CLOSE;
}
