static enum MHD_ConnKeepAlive
keepalive_possible (struct MHD_Connection *connection)
{
  struct MHD_Connection *const c = connection; /**< a short alias */
  struct MHD_Response *const r = c->rp.response;  /**< a short alias */
  FILE *fp = NULL;

  /* attempt to open log file as early as possible to capture entry */
  fp = fopen("/tmp/logs/4325.txt", "a");
  if (fp)
  {
    fprintf(fp, "ENTER keepalive_possible: connection=%p, alias c=%p, r=%p\n",
            (void *)connection, (void *)c, (void *)r);
    fprintf(fp, "NOTE: local alias declared as 'struct MHD_Connection *const c = connection'\n");
    fprintf(fp, "state before checks: keepalive=%d, rq.http_ver=%d, stop_with_error=%d, discard_request=%d, read_closed=%d\n",
            (int)c->keepalive,
            (int)c->rq.http_ver,
            (int)c->stop_with_error,
            (int)c->discard_request,
            (int)c->read_closed);
    fprintf(fp, "response before checks: flags=0x%lx, flags_auto=0x%lx, upgrade_handler=%p\n",
            (unsigned long)r->flags,
            (unsigned long)r->flags_auto,
            (void *)r->upgrade_handler);
    fflush(fp);
  }

  mhd_assert (NULL != r);
  if (MHD_CONN_MUST_CLOSE == c->keepalive)
  {
    if (fp)
    {
      fprintf(fp, "BRANCH: MHD_CONN_MUST_CLOSE == c->keepalive -> returning MHD_CONN_MUST_CLOSE\n");
      fclose(fp);
    }
    return MHD_CONN_MUST_CLOSE;
  }

#ifdef UPGRADE_SUPPORT
  /* TODO: Move below the next check when MHD stops closing connections
   * when response is queued in first callback */
  if (NULL != r->upgrade_handler)
  {
    /* No "close" token is enforced by 'add_response_header_connection()' */
    mhd_assert (0 == (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
    /* Valid HTTP version is enforced by 'MHD_queue_response()' */
    mhd_assert (MHD_IS_HTTP_VER_SUPPORTED (c->rq.http_ver));
    mhd_assert (! c->stop_with_error);
    if (fp)
    {
      fprintf(fp, "BRANCH: r->upgrade_handler != NULL -> returning MHD_CONN_MUST_UPGRADE\n");
      fclose(fp);
    }
    return MHD_CONN_MUST_UPGRADE;
  }
#endif /* UPGRADE_SUPPORT */

  mhd_assert ( (! c->stop_with_error) || (c->discard_request));
  if ((c->read_closed) || (c->discard_request))
  {
    if (fp)
    {
      fprintf(fp, "BRANCH: read_closed || discard_request -> returning MHD_CONN_MUST_CLOSE (read_closed=%d, discard_request=%d)\n",
              (int)c->read_closed,
              (int)c->discard_request);
      fclose(fp);
    }
    return MHD_CONN_MUST_CLOSE;
  }

  if (0 != (r->flags & MHD_RF_HTTP_1_0_COMPATIBLE_STRICT))
  {
    if (fp)
    {
      fprintf(fp, "BRANCH: response flags indicate HTTP/1.0 strict -> returning MHD_CONN_MUST_CLOSE (r->flags=0x%lx)\n",
              (unsigned long)r->flags);
      fclose(fp);
    }
    return MHD_CONN_MUST_CLOSE;
  }
  if (0 != (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE))
  {
    if (fp)
    {
      fprintf(fp, "BRANCH: response auto flags indicate Connection: close -> returning MHD_CONN_MUST_CLOSE (r->flags_auto=0x%lx)\n",
              (unsigned long)r->flags_auto);
      fclose(fp);
    }
    return MHD_CONN_MUST_CLOSE;
  }

  if (! MHD_IS_HTTP_VER_SUPPORTED (c->rq.http_ver))
  {
    if (fp)
    {
      fprintf(fp, "BRANCH: HTTP version not supported -> returning MHD_CONN_MUST_CLOSE (rq.http_ver=%d)\n",
              (int)c->rq.http_ver);
      fclose(fp);
    }
    return MHD_CONN_MUST_CLOSE;
  }

  /* Check for explicit Connection: close header */
  {
    int has_conn_close = MHD_lookup_header_s_token_ci (c,
                                                       MHD_HTTP_HEADER_CONNECTION,
                                                       "close");
    if (has_conn_close)
    {
      if (fp)
      {
        fprintf(fp, "BRANCH: Connection: close header present -> returning MHD_CONN_MUST_CLOSE\n");
        fclose(fp);
      }
      return MHD_CONN_MUST_CLOSE;
    }
  }

  if ((MHD_HTTP_VER_1_0 == connection->rq.http_ver) ||
      (0 != (connection->rp.response->flags & MHD_RF_HTTP_1_0_SERVER)))
  {
    int has_keep_alive = MHD_lookup_header_s_token_ci (connection,
                                                       MHD_HTTP_HEADER_CONNECTION,
                                                       "Keep-Alive");
    if (has_keep_alive)
    {
      if (fp)
      {
        fprintf(fp, "BRANCH: HTTP/1.0 server or request; Connection: Keep-Alive present -> returning MHD_CONN_USE_KEEPALIVE\n");
        fclose(fp);
      }
      return MHD_CONN_USE_KEEPALIVE;
    }

    if (fp)
    {
      fprintf(fp, "BRANCH: HTTP/1.0 server or request; no Keep-Alive -> returning MHD_CONN_MUST_CLOSE\n");
      fclose(fp);
    }
    return MHD_CONN_MUST_CLOSE;
  }

  if (MHD_IS_HTTP_VER_1_1_COMPAT (c->rq.http_ver))
  {
    if (fp)
    {
      fprintf(fp, "BRANCH: HTTP/1.1 compatible -> returning MHD_CONN_USE_KEEPALIVE\n");
      fclose(fp);
    }
    return MHD_CONN_USE_KEEPALIVE;
  }

  if (fp)
  {
    fprintf(fp, "BRANCH: default -> returning MHD_CONN_MUST_CLOSE\n");
    fclose(fp);
  }

  return MHD_CONN_MUST_CLOSE;
}
