static enum MHD_ConnKeepAlive
keepalive_possible (struct MHD_Connection *connection)
{
  struct MHD_Connection *const c = connection; /**< a short alias */
  struct MHD_Response *const r = c->rp.response;  /**< a short alias */
  FILE *f = fopen("/tmp/logs/4325.txt", "a");
  if (f)
  {
    fprintf(f, "ENTER keepalive_possible: connection=%p c=%p r=%p\n", (void *)connection, (void *)c, (void *)r);
    fprintf(f, "STATE: c->keepalive=%d c->rq.http_ver=%d c->stop_with_error=%d c->read_closed=%d c->discard_request=%d\n",
            (int) c->keepalive,
            (int) c->rq.http_ver,
            (int) c->stop_with_error,
            (int) c->read_closed,
            (int) c->discard_request);
    fprintf(f, "STATE: r->flags=0x%08x r->flags_auto=0x%08x r->upgrade_handler=%p\n",
            (unsigned int) r->flags,
            (unsigned int) r->flags_auto,
            (void *) r->upgrade_handler);
    fclose(f);
  }

  mhd_assert (NULL != r);
  if (MHD_CONN_MUST_CLOSE == c->keepalive)
  {
    f = fopen("/tmp/logs/4325.txt", "a");
    if (f)
    {
      fprintf(f, "BRANCH: c->keepalive == MHD_CONN_MUST_CLOSE -> return MHD_CONN_MUST_CLOSE\n");
      fclose(f);
    }
    return MHD_CONN_MUST_CLOSE;
  }

#ifdef UPGRADE_SUPPORT
  /* TODO: Move below the next check when MHD stops closing connections
   * when response is queued in first callback */
  if (NULL != r->upgrade_handler)
  {
    /* No "close" token is enforced by 'add_response_header_connection()' */
    mhd_assert (0 == (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
    /* Valid HTTP version is enforced by 'MHD_queue_response()' */
    mhd_assert (MHD_IS_HTTP_VER_SUPPORTED (c->rq.http_ver));
    mhd_assert (! c->stop_with_error);

    f = fopen("/tmp/logs/4325.txt", "a");
    if (f)
    {
      fprintf(f, "BRANCH: r->upgrade_handler != NULL -> return MHD_CONN_MUST_UPGRADE\n");
      fclose(f);
    }

    return MHD_CONN_MUST_UPGRADE;
  }
#endif /* UPGRADE_SUPPORT */

  mhd_assert ( (! c->stop_with_error) || (c->discard_request));
  if ((c->read_closed) || (c->discard_request))
  {
    f = fopen("/tmp/logs/4325.txt", "a");
    if (f)
    {
      fprintf(f, "BRANCH: c->read_closed || c->discard_request -> return MHD_CONN_MUST_CLOSE\n");
      fclose(f);
    }
    return MHD_CONN_MUST_CLOSE;
  }

  if (0 != (r->flags & MHD_RF_HTTP_1_0_COMPATIBLE_STRICT))
  {
    f = fopen("/tmp/logs/4325.txt", "a");
    if (f)
    {
      fprintf(f, "BRANCH: r->flags has MHD_RF_HTTP_1_0_COMPATIBLE_STRICT -> return MHD_CONN_MUST_CLOSE\n");
      fclose(f);
    }
    return MHD_CONN_MUST_CLOSE;
  }
  if (0 != (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE))
  {
    f = fopen("/tmp/logs/4325.txt", "a");
    if (f)
    {
      fprintf(f, "BRANCH: r->flags_auto has MHD_RAF_HAS_CONNECTION_CLOSE -> return MHD_CONN_MUST_CLOSE\n");
      fclose(f);
    }
    return MHD_CONN_MUST_CLOSE;
  }

  if (! MHD_IS_HTTP_VER_SUPPORTED (c->rq.http_ver))
  {
    f = fopen("/tmp/logs/4325.txt", "a");
    if (f)
    {
      fprintf(f, "BRANCH: !MHD_IS_HTTP_VER_SUPPORTED(c->rq.http_ver) -> return MHD_CONN_MUST_CLOSE (http_ver=%d)\n", (int) c->rq.http_ver);
      fclose(f);
    }
    return MHD_CONN_MUST_CLOSE;
  }

  {
    int has_close = MHD_lookup_header_s_token_ci (c,
                                                  MHD_HTTP_HEADER_CONNECTION,
                                                  "close");
    f = fopen("/tmp/logs/4325.txt", "a");
    if (f)
    {
      fprintf(f, "CALL: MHD_lookup_header_s_token_ci(..., \"close\") -> %d\n", has_close);
      fclose(f);
    }
    if (has_close)
    {
      f = fopen("/tmp/logs/4325.txt", "a");
      if (f)
      {
        fprintf(f, "BRANCH: Connection header contains 'close' -> return MHD_CONN_MUST_CLOSE\n");
        fclose(f);
      }
      return MHD_CONN_MUST_CLOSE;
    }
  }

  if ((MHD_HTTP_VER_1_0 == connection->rq.http_ver) ||
      (0 != (connection->rp.response->flags & MHD_RF_HTTP_1_0_SERVER)))
  {
    int has_keepalive = MHD_lookup_header_s_token_ci (connection,
                                                      MHD_HTTP_HEADER_CONNECTION,
                                                      "Keep-Alive");
    f = fopen("/tmp/logs/4325.txt", "a");
    if (f)
    {
      fprintf(f, "BRANCH: HTTP/1.0 or server flag set (http_ver=%d flags=0x%08x) -> Look for 'Keep-Alive' header: %d\n",
              (int) connection->rq.http_ver,
              (unsigned int) connection->rp.response->flags,
              has_keepalive);
      fclose(f);
    }
    if (has_keepalive)
    {
      f = fopen("/tmp/logs/4325.txt", "a");
      if (f)
      {
        fprintf(f, "BRANCH: Found 'Keep-Alive' -> return MHD_CONN_USE_KEEPALIVE\n");
        fclose(f);
      }
      return MHD_CONN_USE_KEEPALIVE;
    }

    f = fopen("/tmp/logs/4325.txt", "a");
    if (f)
    {
      fprintf(f, "BRANCH: Default for HTTP/1.0 path -> return MHD_CONN_MUST_CLOSE\n");
      fclose(f);
    }
    return MHD_CONN_MUST_CLOSE;
  }

  if (MHD_IS_HTTP_VER_1_1_COMPAT (c->rq.http_ver))
  {
    f = fopen("/tmp/logs/4325.txt", "a");
    if (f)
    {
      fprintf(f, "BRANCH: MHD_IS_HTTP_VER_1_1_COMPAT(c->rq.http_ver) -> return MHD_CONN_USE_KEEPALIVE\n");
      fclose(f);
    }
    return MHD_CONN_USE_KEEPALIVE;
  }

  f = fopen("/tmp/logs/4325.txt", "a");
  if (f)
  {
    fprintf(f, "EXIT keepalive_possible: default -> return MHD_CONN_MUST_CLOSE\n");
    fclose(f);
  }

  return MHD_CONN_MUST_CLOSE;
}
