static MHD_THRD_RTRN_TYPE_ MHD_THRD_CALL_SPEC_
named_thread_starter (void *data)
{
  struct MHD_named_helper_param_ *const param =
    (struct MHD_named_helper_param_ *) data;
  void *arg;
  MHD_THREAD_START_ROUTINE_ thr_func;
  FILE *log = fopen("/tmp/logs/510.txt", "a");

  if (log)
    fprintf(log, "named_thread_starter: entry data=%p\n", data);

  if (NULL == data)
  {
    if (log)
      fprintf(log, "named_thread_starter: data is NULL, returning 0\n");
    if (log)
      fclose(log);
    return (MHD_THRD_RTRN_TYPE_) 0;
  }

  if (log)
    fprintf(log, "named_thread_starter: param=%p name=%s\n",
            (void *) param, (param->name != NULL) ? param->name : "(null)");

  MHD_set_cur_thread_name_ (param->name);

  if (log)
    fprintf(log, "named_thread_starter: set thread name to '%s'\n",
            (param->name != NULL) ? param->name : "(null)");

  arg = param->arg;
  thr_func = param->start_routine;

  if (log)
    fprintf(log, "named_thread_starter: before free: data=%p arg=%p thr_func=%p\n",
            data, arg, (void *) thr_func);

  free (data);

  if (log)
  {
    fprintf(log, "named_thread_starter: after free, about to call thr_func=%p with arg=%p\n",
            (void *) thr_func, arg);
    fflush(log);
  }

  /* Preserve original behavior: call the start routine and return its value. */
  MHD_THRD_RTRN_TYPE_ retval = thr_func (arg);

  if (log)
    fprintf(log, "named_thread_starter: thr_func returned; returning to caller\n");
  if (log)
    fclose(log);

  return retval;
}
