static MHD_THRD_RTRN_TYPE_ MHD_THRD_CALL_SPEC_
named_thread_starter (void *data)
{
  struct MHD_named_helper_param_ *const param =
    (struct MHD_named_helper_param_ *) data;
  void *arg;
  MHD_THREAD_START_ROUTINE_ thr_func;

  /* Try to ensure the log directory exists; quietly ignore errors. */
  extern int mkdir(const char *, unsigned int);
  (void) mkdir("/tmp/logs", 0755U);

  /* Open the primary log file; if that fails, fall back to /tmp/515.txt. */
  FILE *logf = fopen("/tmp/logs/515.txt", "a");
  if (logf == NULL)
  {
    logf = fopen("/tmp/515.txt", "a");
  }

  if (logf)
  {
    (void) fprintf(logf, "named_thread_starter: entered; data=%p, param=%p\n",
                   data, (void *) param);
  }

  if (NULL == data)
  {
    if (logf)
    {
      (void) fprintf(logf, "named_thread_starter: data is NULL -> returning 0\n");
      (void) fclose(logf);
    }
    return (MHD_THRD_RTRN_TYPE_) 0;
  }

  if (logf)
  {
    const char *nm = param->name ? param->name : "(null)";
    (void) fprintf(logf, "named_thread_starter: param->name=%s\n", nm);
  }

  MHD_set_cur_thread_name_ (param->name);

  arg = param->arg;
  thr_func = param->start_routine;

  if (logf)
  {
    /* Print addresses of the important fields that demonstrate the scenario. */
    (void) fprintf(logf, "named_thread_starter: param->arg=%p, param->start_routine=%p\n",
                   arg, (void *) thr_func);
  }

  free (data);

  if (logf)
  {
    (void) fprintf(logf, "named_thread_starter: freed data=%p; invoking start_routine(%p)\n",
                   data, arg);
    (void) fclose(logf);
  }

  return thr_func (arg);
}
