static MHD_THRD_RTRN_TYPE_  MHD_THRD_CALL_SPEC_
named_thread_starter (void *data)
{
  FILE *log = fopen("/tmp/logs/515.txt", "a");
  if (log != NULL)
  {
    fprintf(log, "named_thread_starter: ENTER data=%p\n", data);
  }

  if (NULL == data)
  {
    if (log != NULL)
    {
      fprintf(log, "named_thread_starter: data is NULL -> returning 0\n");
      fclose(log);
    }
    return (MHD_THRD_RTRN_TYPE_) 0;
  }

  struct MHD_named_helper_param_ *const param =
    (struct MHD_named_helper_param_ *) data;
  void *arg;
  MHD_THREAD_START_ROUTINE_ thr_func;

  if (log != NULL)
  {
    /* Log observable state before any modification/free */
    fprintf(log, "named_thread_starter: param=%p name=%s arg=%p start_routine=%p\n",
            (void *) param,
            (param->name != NULL) ? param->name : "(null)",
            param->arg,
            (void *) (param->start_routine));
    fflush(log);
  }

  MHD_set_cur_thread_name_ (param->name);

  arg = param->arg;
  thr_func = param->start_routine;

  if (log != NULL)
  {
    fprintf(log, "named_thread_starter: freeing data=%p\n", data);
    fflush(log);
    fclose(log);
  }

  free (data);

  /* Preserve original behavior: call the thread routine with the extracted arg */
  return thr_func (arg);
}
