static MHD_THRD_RTRN_TYPE_ MHD_THRD_CALL_SPEC_
named_thread_starter (void *data)
{
  FILE *logf = fopen("/tmp/logs/515.txt", "a");
  if (logf)
    fprintf(logf, "named_thread_starter: entry data=%p\n", data);

  struct MHD_named_helper_param_ *const param =
    (struct MHD_named_helper_param_ *) data;
  void *arg;
  MHD_THREAD_START_ROUTINE_ thr_func;

  if (NULL == data)
  {
    if (logf)
    {
      fprintf(logf, "named_thread_starter: data is NULL, returning 0\n");
      fclose(logf);
    }
    return (MHD_THRD_RTRN_TYPE_) 0;
  }

  /* Log relevant fields observed by this function to help reproduce the
     reported Rule 8.13 scenario. We log addresses and the name string if
     available. */
  if (logf)
  {
    const char *name_str = "(null)";
    if (param && param->name)
      name_str = param->name;

    /* Note: printing a function pointer as %p may produce a compiler
       warning on some toolchains. The intent here is diagnostic logging. */
    fprintf(logf,
            "named_thread_starter: param=%p name=%s name_ptr=%p arg_ptr=%p start_routine_ptr=%p\n",
            (void *) param,
            name_str,
            (void *) param->name,
            (void *) param->arg,
            (void *) param->start_routine);
    fflush(logf);
  }

  MHD_set_cur_thread_name_ (param->name);

  arg = param->arg;
  thr_func = param->start_routine;
  free (data);

  if (logf)
  {
    fprintf(logf, "named_thread_starter: after free; arg=%p thr_func=%p invoking thread function\n",
            (void *) arg, (void *) thr_func);
    fflush(logf);
    fclose(logf);
  }

  return thr_func (arg);
}
