static struct MHD_Connection *
new_connection_prepare_ (struct MHD_Daemon *daemon,
                         MHD_socket client_socket,
                         const struct sockaddr_storage *addr,
                         socklen_t addrlen,
                         bool external_add,
                         bool non_blck,
                         bool sk_spipe_supprs,
                         enum MHD_tristate sk_is_nonip)
{
  struct MHD_Connection *connection;
  int eno = 0;
  int __mhd_log_saved_errno = errno; /* preserve errno for logging operations */

#ifdef HAVE_MESSAGES
#if _MHD_DEBUG_CONNECT
  MHD_DLOG (daemon,
            _ ("Accepted connection on socket %d.\n"),
            client_socket);
#endif
#endif

  /* Log entry probe */
  {
    FILE * __mhd_log_f = fopen("/tmp/logs/3975.txt", "a");
    if (NULL != __mhd_log_f)
    {
      fprintf(__mhd_log_f, "ENTER new_connection_prepare_: client_socket=%d, addrlen=%u, external_add=%d, non_blck=%d, sk_spipe_supprs=%d, sk_is_nonip=%d\n",
              (int) client_socket, (unsigned) addrlen, (int) external_add, (int) non_blck, (int) sk_spipe_supprs, (int) sk_is_nonip);
      fclose(__mhd_log_f);
    }
    errno = __mhd_log_saved_errno;
  }

  if ( (daemon->connections == daemon->connection_limit) ||
       (MHD_NO == MHD_ip_limit_add (daemon,
                                    addr,
                                    addrlen)) )
  {
    /* above connection limit - reject */
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Server reached connection limit. " \
                 "Closing inbound connection.\n"));
#endif
    MHD_socket_close_chk_ (client_socket);
#if defined(ENFILE) && (ENFILE + 0 != 0)
    errno = ENFILE;
#endif
    return NULL;
  }

  /* apply connection acceptance policy if present */
  if ( (NULL != daemon->apc) &&
       (MHD_NO == daemon->apc (daemon->apc_cls,
                               (const struct sockaddr *) addr,
                               addrlen)) )
  {
#if _MHD_DEBUG_CLOSE
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Connection rejected by application. Closing connection.\n"));
#endif
#endif
    MHD_socket_close_chk_ (client_socket);
    MHD_ip_limit_del (daemon,
                      addr,
                      addrlen);
#if defined(EACCESS) && (EACCESS + 0 != 0)
    errno = EACCESS;
#endif
    return NULL;
  }

  if (NULL == (connection = MHD_calloc_ (1, sizeof (struct MHD_Connection))))
  {
    eno = errno;
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Error allocating memory: %s\n"),
              MHD_strerror_ (errno));
#endif
    MHD_socket_close_chk_ (client_socket);
    MHD_ip_limit_del (daemon,
                      addr,
                      addrlen);
    errno = eno;
    return NULL;
  }

  if (! external_add)
  {
    connection->sk_corked = _MHD_OFF;
    connection->sk_nodelay = _MHD_OFF;
  }
  else
  {
    connection->sk_corked = _MHD_UNKNOWN;
    connection->sk_nodelay = _MHD_UNKNOWN;
  }

  if (0 < addrlen)
  {
    if (NULL == (connection->addr = malloc ((size_t) addrlen)))
    {
      eno = errno;
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Error allocating memory: %s\n"),
                MHD_strerror_ (errno));
#endif
      MHD_socket_close_chk_ (client_socket);
      MHD_ip_limit_del (daemon,
                        addr,
                        addrlen);
      free (connection);
      errno = eno;
      return NULL;
    }
    memcpy (connection->addr,
            addr,
            (size_t) addrlen);
  }
  else
    connection->addr = NULL;
  connection->addr_len = addrlen;
  connection->socket_fd = client_socket;
  connection->sk_nonblck = non_blck;
  connection->is_nonip = sk_is_nonip;
  connection->sk_spipe_suppress = sk_spipe_supprs;
#ifdef MHD_USE_THREADS
  MHD_thread_handle_ID_set_invalid_ (&connection->tid);
#endif /* MHD_USE_THREADS */
  connection->daemon = daemon;
  connection->connection_timeout_ms = daemon->connection_timeout_ms;
  connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
  if (0 != connection->connection_timeout_ms)
    connection->last_activity = MHD_monotonic_msec_counter ();

  if (0 == (daemon->options & MHD_USE_TLS))
  {
    /* set default connection handlers  */
    MHD_set_http_callbacks_ (connection);
  }
  else
  {
#ifdef HTTPS_SUPPORT
#if (GNUTLS_VERSION_NUMBER + 0 >= 0x030500)
    gnutls_init_flags_t
#else
    unsigned int
#endif
    flags;

    flags = GNUTLS_SERVER;
#if (GNUTLS_VERSION_NUMBER + 0 >= 0x030402)
    flags |= GNUTLS_NO_SIGNAL;
#endif /* GNUTLS_VERSION_NUMBER >= 0x030402 */
#if GNUTLS_VERSION_MAJOR >= 3
    flags |= GNUTLS_NONBLOCK;
#endif /* GNUTLS_VERSION_MAJOR >= 3*/
#if (GNUTLS_VERSION_NUMBER + 0 >= 0x030603)
    if (0 != (daemon->options & MHD_USE_POST_HANDSHAKE_AUTH_SUPPORT))
      flags |= GNUTLS_POST_HANDSHAKE_AUTH;
#endif
#if (GNUTLS_VERSION_NUMBER + 0 >= 0x030605)
    if (0 != (daemon->options & MHD_USE_INSECURE_TLS_EARLY_DATA))
      flags |= GNUTLS_ENABLE_EARLY_DATA;
#endif
    connection->tls_state = MHD_TLS_CONN_INIT;
    MHD_set_https_callbacks (connection);
    if ((GNUTLS_E_SUCCESS != gnutls_init (&connection->tls_session, flags)) ||
        (GNUTLS_E_SUCCESS != gnutls_priority_set (connection->tls_session,
                                                  daemon->priority_cache)))
    {
      if (NULL != connection->tls_session)
        gnutls_deinit (connection->tls_session);
      MHD_socket_close_chk_ (client_socket);
      MHD_ip_limit_del (daemon,
                        addr,
                        addrlen);
      if (NULL != connection->addr)
        free (connection->addr);
      free (connection);
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Failed to initialise TLS session.\n"));
#endif
#if defined(EPROTO) && (EPROTO + 0 != 0)
      errno = EPROTO;
#endif
      return NULL;
    }
#if (GNUTLS_VERSION_NUMBER + 0 >= 0x030200)
    if (! daemon->disable_alpn)
    {
      static const char prt1[] = "http/1.1"; /* Registered code for HTTP/1.1 */
      static const char prt2[] = "http/1.0"; /* Registered code for HTTP/1.0 */
      static const gnutls_datum_t prts[2] =
      { {_MHD_DROP_CONST (prt1), MHD_STATICSTR_LEN_ (prt1)},
        {_MHD_DROP_CONST (prt2), MHD_STATICSTR_LEN_ (prt2)} };

      /* Logging probe for ALPN setup and potential const-drop */
      {
        FILE * __mhd_log_f = fopen("/tmp/logs/3975.txt", "a");
        int __mhd_log_e = errno;
        if (NULL != __mhd_log_f)
        {
          /* Print addresses and sizes to document the cast-away-const scenario */
          fprintf(__mhd_log_f, "ALPN: daemon=%p, disable_alpn=%d\n", (void *) daemon, (int) daemon->disable_alpn);
          fprintf(__mhd_log_f, "ALPN: prt1 addr=%p, prt2 addr=%p\n", (const void *) prt1, (const void *) prt2);
          fprintf(__mhd_log_f, "ALPN: prts[0].data=%p, prts[0].size=%u\n", (const void *) prts[0].data, (unsigned) prts[0].size);
          fprintf(__mhd_log_f, "ALPN: prts[1].data=%p, prts[1].size=%u\n", (const void *) prts[1].data, (unsigned) prts[1].size);
          /* Print first bytes (safe) to show content without modifying it */
          if (prts[0].data != NULL)
          {
            const unsigned int __mhd_show = prts[0].size > 16 ? 16U : (unsigned) prts[0].size;
            fprintf(__mhd_log_f, "ALPN: prts[0] first %u bytes: ", __mhd_show);
            for (unsigned i = 0; i < __mhd_show; ++i)
              fprintf(__mhd_log_f, "%02x ", ((unsigned char *) prts[0].data)[i]);
            fprintf(__mhd_log_f, "\n");
          }
          if (prts[1].data != NULL)
          {
            const unsigned int __mhd_show = prts[1].size > 16 ? 16U : (unsigned) prts[1].size;
            fprintf(__mhd_log_f, "ALPN: prts[1] first %u bytes: ", __mhd_show);
            for (unsigned i = 0; i < __mhd_show; ++i)
              fprintf(__mhd_log_f, "%02x ", ((unsigned char *) prts[1].data)[i]);
            fprintf(__mhd_log_f, "\n");
          }
          fclose(__mhd_log_f);
        }
        errno = __mhd_log_e;
      }

      if (GNUTLS_E_SUCCESS !=
          gnutls_alpn_set_protocols (connection->tls_session,
                                     prts,
                                     sizeof(prts) / sizeof(prts[0]),
                                     0 /* | GNUTLS_ALPN_SERVER_PRECEDENCE */))
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("Failed to set ALPN protocols.\n"));
#else  /* ! HAVE_MESSAGES */
        (void) 0; /* Mute compiler warning */
#endif /* ! HAVE_MESSAGES */
        /* Log the failure of gnutls_alpn_set_protocols */
        {
          FILE * __mhd_log_f = fopen("/tmp/logs/3975.txt", "a");
          int __mhd_log_e2 = errno;
          if (NULL != __mhd_log_f)
          {
            fprintf(__mhd_log_f, "ALPN: gnutls_alpn_set_protocols failed for connection=%p\n", (void *) connection);
            fclose(__mhd_log_f);
          }
          errno = __mhd_log_e2;
        }
      }
    }
#endif /* GNUTLS_VERSION_NUMBER >= 0x030200 */
    gnutls_session_set_ptr (connection->tls_session,
                            connection);
    switch (daemon->cred_type)
    {
    /* set needed credentials for certificate authentication. */
    case GNUTLS_CRD_CERTIFICATE:
      gnutls_credentials_set (connection->tls_session,
                              GNUTLS_CRD_CERTIFICATE,
                              daemon->x509_cred);
      break;
    case GNUTLS_CRD_PSK:
      gnutls_credentials_set (connection->tls_session,
                              GNUTLS_CRD_PSK,
                              daemon->psk_cred);
      gnutls_psk_set_server_credentials_function (daemon->psk_cred,
                                                  &psk_gnutls_adapter);
      break;
    case GNUTLS_CRD_ANON:
    case GNUTLS_CRD_SRP:
    case GNUTLS_CRD_IA:
    default:
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Failed to setup TLS credentials: " \
                   "unknown credential type %d.\n"),
                daemon->cred_type);
#endif
      gnutls_deinit (connection->tls_session);
      MHD_socket_close_chk_ (client_socket);
      MHD_ip_limit_del (daemon,
                        addr,
                        addrlen);
      if (NULL != connection->addr)
        free (connection->addr);
      free (connection);
      MHD_PANIC (_ ("Unknown credential type.\n"));
#if defined(EINVAL) && (EINVAL + 0 != 0)
      errno = EINVAL;
#endif
      return NULL;
    }
#if (GNUTLS_VERSION_NUMBER + 0 >= 0x030109) && ! defined(_WIN64)
    gnutls_transport_set_int (connection->tls_session,
                              (int) (client_socket));
#else  /* GnuTLS before 3.1.9 or Win x64 */
    gnutls_transport_set_ptr (connection->tls_session,
                              (gnutls_transport_ptr_t) \
                              (intptr_t) client_socket);
#endif /* GnuTls before 3.1.9 or Win x64 */
#ifdef MHD_TLSLIB_NEED_PUSH_FUNC
    gnutls_transport_set_push_function (connection->tls_session,
                                        MHD_tls_push_func_);
#endif /* MHD_TLSLIB_NEED_PUSH_FUNC */
    if (daemon->https_mem_trust)
      gnutls_certificate_server_set_request (connection->tls_session,
                                             GNUTLS_CERT_REQUEST);
#else  /* ! HTTPS_SUPPORT */
    MHD_socket_close_chk_ (client_socket);
    MHD_ip_limit_del (daemon,
                      addr,
                      addrlen);
    free (connection->addr);
    free (connection);
    MHD_PANIC (_ ("TLS connection on non-TLS daemon.\n"));
#if 0
    /* Unreachable code */
    eno = EINVAL;
    return NULL;
#endif
#endif /* ! HTTPS_SUPPORT */
  }

  return connection;
}
