static enum MHD_Result
new_connection_process_ (struct MHD_Daemon *daemon,
                         struct MHD_Connection *connection)
{
  int eno = 0;

  mhd_assert (connection->daemon == daemon);

#ifdef MHD_USE_THREADS
  /* Function manipulate connection and timeout DL-lists,
   * must be called only within daemon thread. */
  mhd_assert ( (! MHD_D_IS_USING_THREADS_ (daemon)) || \
               MHD_thread_handle_ID_is_current_thread_ (daemon->tid) );
  mhd_assert (NULL == daemon->worker_pool);
#endif /* MHD_USE_THREADS */

  /* Log entry to help reproduce/trace the Rule 8.13 scenario. */
  {
    FILE *mhd_log_f = fopen("/tmp/logs/555.txt", "a");
    if (mhd_log_f)
    {
      fprintf (mhd_log_f,
               "entered new_connection_process_: daemon=%p connection=%p socket_fd=%d\n",
               (void *) daemon,
               (void *) connection,
               connection ? connection->socket_fd : -1);
      fflush (mhd_log_f);
      fclose (mhd_log_f);
    }
  }

  /* Allocate memory pool in the processing thread so
   * intensively used memory area is allocated in "good"
   * (for the thread) memory region. It is important with
   * NUMA and/or complex cache hierarchy. */
  connection->pool = MHD_pool_create (daemon->pool_size);
  if (NULL == connection->pool)
  { /* 'pool' creation failed */
#ifdef HAVE_MESSAGES
    {
      /* Capture the format string pointer and the translated text so other
         agents can inspect what is being passed through the translation macro
         and into MHD_DLOG. This helps reproduce the case where _() returns
         a non-const char * while the string is effectively const. */
      const char *mhd_probe_fmt_1 = _ ("Error allocating memory: %s\n");

      FILE *mhd_log_f = fopen("/tmp/logs/555.txt", "a");
      if (mhd_log_f)
      {
        fprintf (mhd_log_f,
                 "MHD_DLOG point: pool creation failed; fmt_ptr=%p fmt=\"%s\" arg(Strerror errno)=%s\n",
                 (void *) mhd_probe_fmt_1,
                 mhd_probe_fmt_1 ? mhd_probe_fmt_1 : "(null)",
                 MHD_strerror_ (errno) ? MHD_strerror_ (errno) : "(null)");
        fflush (mhd_log_f);
        fclose (mhd_log_f);
      }

      MHD_DLOG (daemon,
                mhd_probe_fmt_1,
                MHD_strerror_ (errno));
    }
#endif
#if defined(ENOMEM) && (ENOMEM + 0 != 0)
    eno = ENOMEM;
#endif
    (void) 0; /* Mute possible compiler warning */
  }
  else
  { /* 'pool' creation succeed */
    MHD_mutex_lock_chk_ (&daemon->cleanup_connection_mutex);
    /* Firm check under lock. */
    if (daemon->connections >= daemon->connection_limit)
    { /* Connections limit */
      MHD_mutex_unlock_chk_ (&daemon->cleanup_connection_mutex);
#ifdef HAVE_MESSAGES
      {
        const char *mhd_probe_fmt_2 = _ ("Server reached connection limit. "
                                         "Closing inbound connection.\n");

        FILE *mhd_log_f = fopen("/tmp/logs/555.txt", "a");
        if (mhd_log_f)
        {
          fprintf (mhd_log_f,
                   "MHD_DLOG point: connection limit; fmt_ptr=%p fmt=\"%s\"\n",
                   (void *) mhd_probe_fmt_2,
                   mhd_probe_fmt_2 ? mhd_probe_fmt_2 : "(null)");
          fflush (mhd_log_f);
          fclose (mhd_log_f);
        }

        MHD_DLOG (daemon,
                  mhd_probe_fmt_2);
      }
#endif
#if defined(ENFILE) && (ENFILE + 0 != 0)
      eno = ENFILE;
#endif
      (void) 0; /* Mute possible compiler warning */
    }
    else
    { /* Have space for new connection */
      daemon->connections++;
      DLL_insert (daemon->connections_head,
                  daemon->connections_tail,
                  connection);
      if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
      {
        XDLL_insert (daemon->normal_timeout_head,
                     daemon->normal_timeout_tail,
                     connection);
      }
      MHD_mutex_unlock_chk_ (&daemon->cleanup_connection_mutex);

      MHD_connection_set_initial_state_ (connection);

      if (NULL != daemon->notify_connection)
        daemon->notify_connection (daemon->notify_connection_cls,
                                   connection,
                                   &connection->socket_context,
                                   MHD_CONNECTION_NOTIFY_STARTED);
#ifdef MHD_USE_THREADS
      if (MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
      {
        mhd_assert (! MHD_D_IS_USING_EPOLL_ (daemon));
        if (! MHD_create_named_thread_ (&connection->tid,
                                        "MHD-connection",
                                        daemon->thread_stack_size,
                                        &thread_main_handle_connection,
                                        connection))
        {
          eno = errno;
#ifdef HAVE_MESSAGES
#ifdef EAGAIN
          if (EAGAIN == eno)
          {
            const char *mhd_probe_fmt_3 = _ ("Failed to create a new thread because it would "
                                             "have exceeded the system limit on the number of "
                                             "threads or no system resources available.\n");

            FILE *mhd_log_f = fopen("/tmp/logs/555.txt", "a");
            if (mhd_log_f)
            {
              fprintf (mhd_log_f,
                       "MHD_DLOG point: thread create EAGAIN; fmt_ptr=%p fmt=\"%s\" eno=%d\n",
                       (void *) mhd_probe_fmt_3,
                       mhd_probe_fmt_3 ? mhd_probe_fmt_3 : "(null)",
                       eno);
              fflush (mhd_log_f);
              fclose (mhd_log_f);
            }

            MHD_DLOG (daemon,
                      mhd_probe_fmt_3);
          }
          else
#endif /* EAGAIN */
          {
            const char *mhd_probe_fmt_4 = _ ("Failed to create a thread: %s\n");

            FILE *mhd_log_f = fopen("/tmp/logs/555.txt", "a");
            if (mhd_log_f)
            {
              fprintf (mhd_log_f,
                       "MHD_DLOG point: thread create failed; fmt_ptr=%p fmt=\"%s\" arg(Strerror eno)=%s eno=%d\n",
                       (void *) mhd_probe_fmt_4,
                       mhd_probe_fmt_4 ? mhd_probe_fmt_4 : "(null)",
                       MHD_strerror_ (eno) ? MHD_strerror_ (eno) : "(null)",
                       eno);
              fflush (mhd_log_f);
              fclose (mhd_log_f);
            }

            MHD_DLOG (daemon,
                      mhd_probe_fmt_4,
                      MHD_strerror_ (eno));
          }
#endif /* HAVE_MESSAGES */
        }
        else               /* New thread has been created successfully */
          return MHD_YES;  /* *** Function success exit point *** */
      }
      else
#else  /* ! MHD_USE_THREADS */
      if (1)
#endif /* ! MHD_USE_THREADS */
      { /* No 'thread-per-connection' */
#ifdef MHD_USE_THREADS
        connection->tid = daemon->tid;
#endif /* MHD_USE_THREADS */
#ifdef EPOLL_SUPPORT
        if (MHD_D_IS_USING_EPOLL_ (daemon))
        {
          if (0 == (daemon->options & MHD_USE_TURBO))
          {
            struct epoll_event event;

            event.events = EPOLLIN | EPOLLOUT | EPOLLPRI | EPOLLET | EPOLLRDHUP;
            event.data.ptr = connection;
            if (0 != epoll_ctl (daemon->epoll_fd,
                                EPOLL_CTL_ADD,
                                connection->socket_fd,
                                &event))
            {
              eno = errno;
#ifdef HAVE_MESSAGES
              {
                const char *mhd_probe_fmt_5 = _ ("Call to epoll_ctl failed: %s\n");

                FILE *mhd_log_f = fopen("/tmp/logs/555.txt", "a");
                if (mhd_log_f)
                {
                  fprintf (mhd_log_f,
                           "MHD_DLOG point: epoll_ctl failed; fmt_ptr=%p fmt=\"%s\" arg(socket err)=%s eno=%d\n",
                           (void *) mhd_probe_fmt_5,
                           mhd_probe_fmt_5 ? mhd_probe_fmt_5 : "(null)",
                           MHD_socket_last_strerr_ () ? MHD_socket_last_strerr_ () : "(null)",
                           eno);
                  fflush (mhd_log_f);
                  fclose (mhd_log_f);
                }

                MHD_DLOG (daemon,
                          mhd_probe_fmt_5,
                          MHD_socket_last_strerr_ ());
              }
#endif
            }
            else
            { /* 'socket_fd' has been added to 'epool' */
              connection->epoll_state |= MHD_EPOLL_STATE_IN_EPOLL_SET;

              return MHD_YES;  /* *** Function success exit point *** */
            }
          }
          else
          {
            connection->epoll_state |= MHD_EPOLL_STATE_READ_READY
                                       | MHD_EPOLL_STATE_WRITE_READY
                                       | MHD_EPOLL_STATE_IN_EREADY_EDLL;
            EDLL_insert (daemon->eready_head,
                         daemon->eready_tail,
                         connection);

            return MHD_YES;  /* *** Function success exit point *** */
          }
        }
        else /* No 'epoll' */
#endif /* EPOLL_SUPPORT */
        return MHD_YES;    /* *** Function success exit point *** */
      }

      /* ** Below is a cleanup path ** */
      if (NULL != daemon->notify_connection)
        daemon->notify_connection (daemon->notify_connection_cls,
                                   connection,
                                   &connection->socket_context,
                                   MHD_CONNECTION_NOTIFY_CLOSED);
      MHD_mutex_lock_chk_ (&daemon->cleanup_connection_mutex);
      if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
      {
        XDLL_remove (daemon->normal_timeout_head,
                     daemon->normal_timeout_tail,
                     connection);
      }
      DLL_remove (daemon->connections_head,
                  daemon->connections_tail,
                  connection);
      daemon->connections--;
      MHD_mutex_unlock_chk_ (&daemon->cleanup_connection_mutex);
    }
    MHD_pool_destroy (connection->pool);
  }
  /* Free resources allocated before the call of this functions */
#ifdef HTTPS_SUPPORT
  if (NULL != connection->tls_session)
    gnutls_deinit (connection->tls_session);
#endif /* HTTPS_SUPPORT */
  MHD_ip_limit_del (daemon,
                    connection->addr,
                    connection->addr_len);
  if (NULL != connection->addr)
    free (connection->addr);
  MHD_socket_close_chk_ (connection->socket_fd);
  free (connection);
  if (0 != eno)
    errno = eno;
#ifdef EINVAL
  else
    errno = EINVAL;
#endif /* EINVAL */
  return MHD_NO;  /* *** Function failure exit point *** */
}
