static enum MHD_Result
new_connection_process_ (struct MHD_Daemon *daemon,
                         struct MHD_Connection *connection)
{
  int eno = 0;

  /* Logging helper: append information to /tmp/logs/555.txt. */
  do {
    FILE *mhd_log_file = fopen("/tmp/logs/555.txt", "a");
    if (mhd_log_file)
    {
      fprintf (mhd_log_file, "ENTER new_connection_process_: daemon=%p connection=%p\n",
               (void *) daemon, (void *) connection);
      if (connection)
      {
        fprintf (mhd_log_file,
                 "  initial state: connection->socket_fd=%d connection->addr=%p addr_len=%zu socket_context_addr=%p pool=%p\n",
                 connection->socket_fd,
                 (void *) connection->addr,
                 (size_t) connection->addr_len,
                 (void *) &connection->socket_context,
                 (void *) connection->pool);
      }
      fclose (mhd_log_file);
    }
  } while (0);

  mhd_assert (connection->daemon == daemon);

#ifdef MHD_USE_THREADS
  /* Function manipulate connection and timeout DL-lists,
   * must be called only within daemon thread. */
  mhd_assert ( (! MHD_D_IS_USING_THREADS_ (daemon)) || \
               MHD_thread_handle_ID_is_current_thread_ (daemon->tid) );
  mhd_assert (NULL == daemon->worker_pool);
#endif /* MHD_USE_THREADS */

  /* Allocate memory pool in the processing thread so
   * intensively used memory area is allocated in "good"
   * (for the thread) memory region. It is important with
   * NUMA and/or complex cache hierarchy. */
  connection->pool = MHD_pool_create (daemon->pool_size);
  if (NULL == connection->pool)
  { /* 'pool' creation failed */
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Error allocating memory: %s\n"),
              MHD_strerror_ (errno));
#endif
#if defined(ENOMEM) && (ENOMEM + 0 != 0)
    eno = ENOMEM;
#endif
    (void) 0; /* Mute possible compiler warning */

    do {
      FILE *mhd_log_file = fopen("/tmp/logs/555.txt", "a");
      if (mhd_log_file)
      {
        fprintf (mhd_log_file, "POOL_CREATE_FAILED: connection=%p daemon=%p errno=%d\n",
                 (void *) connection, (void *) daemon, errno);
        fclose (mhd_log_file);
      }
    } while (0);
  }
  else
  { /* 'pool' creation succeed */
    MHD_mutex_lock_chk_ (&daemon->cleanup_connection_mutex);
    /* Firm check under lock. */
    if (daemon->connections >= daemon->connection_limit)
    { /* Connections limit */
      MHD_mutex_unlock_chk_ (&daemon->cleanup_connection_mutex);
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Server reached connection limit. "
                   "Closing inbound connection.\n"));
#endif
#if defined(ENFILE) && (ENFILE + 0 != 0)
      eno = ENFILE;
#endif
      (void) 0; /* Mute possible compiler warning */

      do {
        FILE *mhd_log_file = fopen("/tmp/logs/555.txt", "a");
        if (mhd_log_file)
        {
          fprintf (mhd_log_file,
                   "CONN_LIMIT_REACHED: connection=%p daemon=%p connections=%zu limit=%zu\n",
                   (void *) connection, (void *) daemon,
                   (size_t) daemon->connections, (size_t) daemon->connection_limit);
          fclose (mhd_log_file);
        }
      } while (0);
    }
    else
    { /* Have space for new connection */
      daemon->connections++;
      DLL_insert (daemon->connections_head,
                  daemon->connections_tail,
                  connection);
      if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
      {
        XDLL_insert (daemon->normal_timeout_head,
                     daemon->normal_timeout_tail,
                     connection);
      }
      MHD_mutex_unlock_chk_ (&daemon->cleanup_connection_mutex);

      MHD_connection_set_initial_state_ (connection);

      if (NULL != daemon->notify_connection)
      {
        /* Log details right before calling notify_connection for STARTED. */
        do {
          FILE *mhd_log_file = fopen("/tmp/logs/555.txt", "a");
          if (mhd_log_file)
          {
            fprintf (mhd_log_file,
                     "ABOUT_TO_NOTIFY_STARTED: notify_connection=%p notify_connection_cls=%p connection=%p socket_context_addr=%p event=%d\n",
                     (void *) daemon->notify_connection,
                     (void *) daemon->notify_connection_cls,
                     (void *) connection,
                     (void *) &connection->socket_context,
                     (int) MHD_CONNECTION_NOTIFY_STARTED);
            /* Attempt to capture some connection state useful to reproduce rule 8.13 scenario */
            fprintf (mhd_log_file,
                     "  connection->socket_fd=%d connection->epoll_state=0x%08x connection->tid=%p\n",
                     connection->socket_fd,
                     (unsigned int) connection->epoll_state,
                     (void *) (uintptr_t) connection->tid);
            fclose (mhd_log_file);
          }
        } while (0);

        /* Actual notify call (preserve original behavior) */
        daemon->notify_connection (daemon->notify_connection_cls,
                                   connection,
                                   &connection->socket_context,
                                   MHD_CONNECTION_NOTIFY_STARTED);

        do {
          FILE *mhd_log_file = fopen("/tmp/logs/555.txt", "a");
          if (mhd_log_file)
          {
            fprintf (mhd_log_file,
                     "NOTIFIED_STARTED_RETURNED: notify_connection=%p connection=%p\n",
                     (void *) daemon->notify_connection,
                     (void *) connection);
            fclose (mhd_log_file);
          }
        } while (0);
      }
#ifdef MHD_USE_THREADS
      if (MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
      {
        mhd_assert (! MHD_D_IS_USING_EPOLL_ (daemon));
        if (! MHD_create_named_thread_ (&connection->tid,
                                        "MHD-connection",
                                        daemon->thread_stack_size,
                                        &thread_main_handle_connection,
                                        connection))
        {
          eno = errno;
#ifdef HAVE_MESSAGES
#ifdef EAGAIN
          if (EAGAIN == eno)
            MHD_DLOG (daemon,
                      _ ("Failed to create a new thread because it would "
                         "have exceeded the system limit on the number of "
                         "threads or no system resources available.\n"));
          else
#endif /* EAGAIN */
          MHD_DLOG (daemon,
                    _ ("Failed to create a thread: %s\n"),
                    MHD_strerror_ (eno));
#endif /* HAVE_MESSAGES */
        }
        else               /* New thread has been created successfully */
          return MHD_YES;  /* *** Function success exit point *** */
      }
      else
#else  /* ! MHD_USE_THREADS */
      if (1)
#endif /* ! MHD_USE_THREADS */
      { /* No 'thread-per-connection' */
#ifdef MHD_USE_THREADS
        connection->tid = daemon->tid;
#endif /* MHD_USE_THREADS */
#ifdef EPOLL_SUPPORT
        if (MHD_D_IS_USING_EPOLL_ (daemon))
        {
          if (0 == (daemon->options & MHD_USE_TURBO))
          {
            struct epoll_event event;

            event.events = EPOLLIN | EPOLLOUT | EPOLLPRI | EPOLLET | EPOLLRDHUP;
            event.data.ptr = connection;
            if (0 != epoll_ctl (daemon->epoll_fd,
                                EPOLL_CTL_ADD,
                                connection->socket_fd,
                                &event))
            {
              eno = errno;
#ifdef HAVE_MESSAGES
              MHD_DLOG (daemon,
                        _ ("Call to epoll_ctl failed: %s\n"),
                        MHD_socket_last_strerr_ ());
#endif
            }
            else
            { /* 'socket_fd' has been added to 'epool' */
              connection->epoll_state |= MHD_EPOLL_STATE_IN_EPOLL_SET;

              do {
                FILE *mhd_log_file = fopen("/tmp/logs/555.txt", "a");
                if (mhd_log_file)
                {
                  fprintf (mhd_log_file,
                           "EPOLL_ADD_SUCCESS: connection=%p epoll_fd=%d socket_fd=%d\n",
                           (void *) connection, daemon->epoll_fd, connection->socket_fd);
                  fclose (mhd_log_file);
                }
              } while (0);

              return MHD_YES;  /* *** Function success exit point *** */
            }
          }
          else
          {
            connection->epoll_state |= MHD_EPOLL_STATE_READ_READY
                                       | MHD_EPOLL_STATE_WRITE_READY
                                       | MHD_EPOLL_STATE_IN_EREADY_EDLL;
            EDLL_insert (daemon->eready_head,
                         daemon->eready_tail,
                         connection);

            do {
              FILE *mhd_log_file = fopen("/tmp/logs/555.txt", "a");
              if (mhd_log_file)
              {
                fprintf (mhd_log_file,
                         "TURBO_EPOLL_EMIT: connection=%p epoll_state=0x%08x\n",
                         (void *) connection, (unsigned int) connection->epoll_state);
                fclose (mhd_log_file);
              }
            } while (0);

            return MHD_YES;  /* *** Function success exit point *** */
          }
        }
        else /* No 'epoll' */
#endif /* EPOLL_SUPPORT */
        return MHD_YES;    /* *** Function success exit point *** */
      }

      /* ** Below is a cleanup path ** */
      if (NULL != daemon->notify_connection)
      {
        /* Log details right before calling notify_connection for CLOSED. */
        do {
          FILE *mhd_log_file = fopen("/tmp/logs/555.txt", "a");
          if (mhd_log_file)
          {
            fprintf (mhd_log_file,
                     "ABOUT_TO_NOTIFY_CLOSED: notify_connection=%p notify_connection_cls=%p connection=%p socket_context_addr=%p event=%d\n",
                     (void *) daemon->notify_connection,
                     (void *) daemon->notify_connection_cls,
                     (void *) connection,
                     (void *) &connection->socket_context,
                     (int) MHD_CONNECTION_NOTIFY_CLOSED);
            fprintf (mhd_log_file,
                     "  connection->socket_fd=%d connection->epoll_state=0x%08x connection->tid=%p\n",
                     connection->socket_fd,
                     (unsigned int) connection->epoll_state,
                     (void *) (uintptr_t) connection->tid);
            fclose (mhd_log_file);
          }
        } while (0);

        /* Actual notify call (preserve original behavior) */
        daemon->notify_connection (daemon->notify_connection_cls,
                                   connection,
                                   &connection->socket_context,
                                   MHD_CONNECTION_NOTIFY_CLOSED);

        do {
          FILE *mhd_log_file = fopen("/tmp/logs/555.txt", "a");
          if (mhd_log_file)
          {
            fprintf (mhd_log_file,
                     "NOTIFIED_CLOSED_RETURNED: notify_connection=%p connection=%p\n",
                     (void *) daemon->notify_connection,
                     (void *) connection);
            fclose (mhd_log_file);
          }
        } while (0);
      }
      MHD_mutex_lock_chk_ (&daemon->cleanup_connection_mutex);
      if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
      {
        XDLL_remove (daemon->normal_timeout_head,
                     daemon->normal_timeout_tail,
                     connection);
      }
      DLL_remove (daemon->connections_head,
                  daemon->connections_tail,
                  connection);
      daemon->connections--;
      MHD_mutex_unlock_chk_ (&daemon->cleanup_connection_mutex);
    }
    MHD_pool_destroy (connection->pool);
  }
  /* Free resources allocated before the call of this functions */
#ifdef HTTPS_SUPPORT
  if (NULL != connection->tls_session)
    gnutls_deinit (connection->tls_session);
#endif /* HTTPS_SUPPORT */
  MHD_ip_limit_del (daemon,
                    connection->addr,
                    connection->addr_len);
  if (NULL != connection->addr)
    free (connection->addr);
  MHD_socket_close_chk_ (connection->socket_fd);
  free (connection);

  do {
    FILE *mhd_log_file = fopen("/tmp/logs/555.txt", "a");
    if (mhd_log_file)
    {
      fprintf (mhd_log_file, "EXIT new_connection_process_: daemon=%p final_errno=%d\n",
               (void *) daemon, eno);
      fclose (mhd_log_file);
    }
  } while (0);

  if (0 != eno)
    errno = eno;
#ifdef EINVAL
  else
    errno = EINVAL;
#endif /* EINVAL */
  return MHD_NO;  /* *** Function failure exit point *** */
}
