static enum MHD_Result
new_connection_process_ (struct MHD_Daemon *daemon,
                         struct MHD_Connection *connection)
{
  int eno = 0;
  /* Preserve errno across our logging calls so we do not perturb caller-visible
     error state used later in this function. */
  int _mhd_saved_errno = errno;

  mhd_assert (connection->daemon == daemon);

#ifdef MHD_USE_THREADS
  /* Function manipulate connection and timeout DL-lists,
   * must be called only within daemon thread. */
  mhd_assert ( (! MHD_D_IS_USING_THREADS_ (daemon)) || \
               MHD_thread_handle_ID_is_current_thread_ (daemon->tid) );
  mhd_assert (NULL == daemon->worker_pool);
#endif /* MHD_USE_THREADS */

  /* Minimal entry probe so test harness can detect calls to this function. */
  {
    int _mhd_log_errno = errno;
    FILE * _mhd_logf = fopen("/tmp/logs/555.txt", "a");
    if (NULL != _mhd_logf)
    {
      fprintf(_mhd_logf, "new_connection_process_: entry daemon=%p connection=%p errno=%d\n",
              (void *) daemon, (void *) connection, _mhd_log_errno);
      fclose(_mhd_logf);
    }
    errno = _mhd_log_errno;
  }

  /* Allocate memory pool in the processing thread so
   * intensively used memory area is allocated in "good"
   * (for the thread) memory region. It is important with
   * NUMA and/or complex cache hierarchy. */
  connection->pool = MHD_pool_create (daemon->pool_size);
  if (NULL == connection->pool)
  { /* 'pool' creation failed */
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Error allocating memory: %s\n"),
              MHD_strerror_ (errno));
#endif
    /* Log the pool creation failure and errno so we can correlate later logs. */
    {
      int _mhd_log_errno = errno;
      FILE * _mhd_logf = fopen("/tmp/logs/555.txt", "a");
      if (NULL != _mhd_logf)
      {
        fprintf(_mhd_logf, "new_connection_process_: pool creation failed connection=%p pool=NULL errno=%d\n",
                (void *) connection, _mhd_log_errno);
        fclose(_mhd_logf);
      }
      errno = _mhd_log_errno;
    }
#if defined(ENOMEM) && (ENOMEM + 0 != 0)
    eno = ENOMEM;
#endif
    (void) 0; /* Mute possible compiler warning */
  }
  else
  { /* 'pool' creation succeed */
    MHD_mutex_lock_chk_ (&daemon->cleanup_connection_mutex);
    /* Firm check under lock. */
    if (daemon->connections >= daemon->connection_limit)
    { /* Connections limit */
      MHD_mutex_unlock_chk_ (&daemon->cleanup_connection_mutex);
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Server reached connection limit. "
                   "Closing inbound connection.\n"));
#endif
      /* Log that we hit the connection limit. */
      {
        int _mhd_log_errno = errno;
        FILE * _mhd_logf = fopen("/tmp/logs/555.txt", "a");
        if (NULL != _mhd_logf)
        {
          fprintf(_mhd_logf, "new_connection_process_: connection limit reached daemon=%p connection=%p connections=%u limit=%u errno=%d\n",
                  (void *) daemon, (void *) connection,
                  (unsigned) daemon->connections, (unsigned) daemon->connection_limit, _mhd_log_errno);
          fclose(_mhd_logf);
        }
        errno = _mhd_log_errno;
      }
#if defined(ENFILE) && (ENFILE + 0 != 0)
      eno = ENFILE;
#endif
      (void) 0; /* Mute possible compiler warning */
    }
    else
    { /* Have space for new connection */
      daemon->connections++;
      DLL_insert (daemon->connections_head,
                  daemon->connections_tail,
                  connection);
      if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
      {
        XDLL_insert (daemon->normal_timeout_head,
                     daemon->normal_timeout_tail,
                     connection);
      }
      MHD_mutex_unlock_chk_ (&daemon->cleanup_connection_mutex);

      MHD_connection_set_initial_state_ (connection);

      if (NULL != daemon->notify_connection)
        daemon->notify_connection (daemon->notify_connection_cls,
                                   connection,
                                   &connection->socket_context,
                                   MHD_CONNECTION_NOTIFY_STARTED);
#ifdef MHD_USE_THREADS
      if (MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
      {
        mhd_assert (! MHD_D_IS_USING_EPOLL_ (daemon));
        if (! MHD_create_named_thread_ (&connection->tid,
                                        "MHD-connection",
                                        daemon->thread_stack_size,
                                        &thread_main_handle_connection,
                                        connection))
        {
          eno = errno;
#ifdef HAVE_MESSAGES
#ifdef EAGAIN
          if (EAGAIN == eno)
            MHD_DLOG (daemon,
                      _ ("Failed to create a new thread because it would "
                         "have exceeded the system limit on the number of "
                         "threads or no system resources available.\n"));
          else
#endif /* EAGAIN */
          MHD_DLOG (daemon,
                    _ ("Failed to create a thread: %s\n"),
                    MHD_strerror_ (eno));
#endif /* HAVE_MESSAGES */
          /* Log thread creation failure */
          {
            int _mhd_log_errno = errno;
            FILE * _mhd_logf = fopen("/tmp/logs/555.txt", "a");
            if (NULL != _mhd_logf)
            {
              fprintf(_mhd_logf, "new_connection_process_: thread create failed daemon=%p connection=%p eno=%d errno=%d\n",
                      (void *) daemon, (void *) connection, eno, _mhd_log_errno);
              /* Also record the strerror result used in the DLOG for correlation. */
              fprintf(_mhd_logf, "new_connection_process_: thread create strerror: %s\n",
                      MHD_strerror_ (eno));
              fclose(_mhd_logf);
            }
            errno = _mhd_log_errno;
          }
        }
        else               /* New thread has been created successfully */
          return MHD_YES;  /* *** Function success exit point *** */
      }
      else
#else  /* ! MHD_USE_THREADS */
      if (1)
#endif /* ! MHD_USE_THREADS */
      { /* No 'thread-per-connection' */
#ifdef MHD_USE_THREADS
        connection->tid = daemon->tid;
#endif /* MHD_USE_THREADS */
#ifdef EPOLL_SUPPORT
        if (MHD_D_IS_USING_EPOLL_ (daemon))
        {
          if (0 == (daemon->options & MHD_USE_TURBO))
          {
            struct epoll_event event;

            event.events = EPOLLIN | EPOLLOUT | EPOLLPRI | EPOLLET | EPOLLRDHUP;
            event.data.ptr = connection;
            if (0 != epoll_ctl (daemon->epoll_fd,
                                EPOLL_CTL_ADD,
                                connection->socket_fd,
                                &event))
            {
              eno = errno;
#ifdef HAVE_MESSAGES
              MHD_DLOG (daemon,
                        _ ("Call to epoll_ctl failed: %s\n"),
                        MHD_socket_last_strerr_ ());
#endif
              /* Special probe to capture the exact pointer and string returned
                 by MHD_socket_last_strerr_() at the moment of the failure. This
                 is used to reproduce and provide evidence for the const-qualifier
                 violation scenario: the function may be declared to return
                 'char *' but actually point to immutable data. */
              {
                int _mhd_log_errno = errno;
                char * _mhd_sock_err = MHD_socket_last_strerr_ ();
                FILE * _mhd_logf = fopen("/tmp/logs/555.txt", "a");
                if (NULL != _mhd_logf)
                {
                  fprintf(_mhd_logf, "new_connection_process_: epoll_ctl failed daemon=%p connection=%p eno=%d errno=%d\n",
                          (void *) daemon, (void *) connection, eno, _mhd_log_errno);
                  fprintf(_mhd_logf, "new_connection_process_: MHD_socket_last_strerr_() -> ptr=%p\n",
                          (void *) _mhd_sock_err);
                  if (NULL != _mhd_sock_err)
                  {
                    /* Print the string returned (if non-NULL). This helps
                       to determine whether the returned pointer points to a
                       read-only/static message (e.g., literal) even though the
                       declared return type might be non-const. */
                    fprintf(_mhd_logf, "new_connection_process_: MHD_socket_last_strerr_() -> string=\"%s\"\n",
                            _mhd_sock_err);
                  }
                  else
                  {
                    fprintf(_mhd_logf, "new_connection_process_: MHD_socket_last_strerr_() -> string=(NULL)\n");
                  }
                  fclose(_mhd_logf);
                }
                errno = _mhd_log_errno;
              }
            }
            else
            { /* 'socket_fd' has been added to 'epool' */
              connection->epoll_state |= MHD_EPOLL_STATE_IN_EPOLL_SET;

              return MHD_YES;  /* *** Function success exit point *** */
            }
          }
          else
          {
            connection->epoll_state |= MHD_EPOLL_STATE_READ_READY
                                       | MHD_EPOLL_STATE_WRITE_READY
                                       | MHD_EPOLL_STATE_IN_EREADY_EDLL;
            EDLL_insert (daemon->eready_head,
                         daemon->eready_tail,
                         connection);

            return MHD_YES;  /* *** Function success exit point *** */
          }
        }
        else /* No 'epoll' */
#endif /* EPOLL_SUPPORT */
        return MHD_YES;    /* *** Function success exit point *** */
      }

      /* ** Below is a cleanup path ** */
      if (NULL != daemon->notify_connection)
        daemon->notify_connection (daemon->notify_connection_cls,
                                   connection,
                                   &connection->socket_context,
                                   MHD_CONNECTION_NOTIFY_CLOSED);
      MHD_mutex_lock_chk_ (&daemon->cleanup_connection_mutex);
      if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
      {
        XDLL_remove (daemon->normal_timeout_head,
                     daemon->normal_timeout_tail,
                     connection);
      }
      DLL_remove (daemon->connections_head,
                  daemon->connections_tail,
                  connection);
      daemon->connections--;
      MHD_mutex_unlock_chk_ (&daemon->cleanup_connection_mutex);
    }
    MHD_pool_destroy (connection->pool);
  }
  /* Free resources allocated before the call of this functions */
#ifdef HTTPS_SUPPORT
  if (NULL != connection->tls_session)
    gnutls_deinit (connection->tls_session);
#endif /* HTTPS_SUPPORT */
  MHD_ip_limit_del (daemon,
                    connection->addr,
                    connection->addr_len);
  if (NULL != connection->addr)
    free (connection->addr);
  MHD_socket_close_chk_ (connection->socket_fd);
  free (connection);

  /* Log final cleanup and exit path. */
  {
    int _mhd_log_errno = errno;
    FILE * _mhd_logf = fopen("/tmp/logs/555.txt", "a");
    if (NULL != _mhd_logf)
    {
      fprintf(_mhd_logf, "new_connection_process_: cleanup done returning failure eno=%d final_errno=%d\n",
              eno, _mhd_log_errno);
      fclose(_mhd_logf);
    }
    errno = _mhd_log_errno;
  }

  if (0 != eno)
    errno = eno;
#ifdef EINVAL
  else
    errno = EINVAL;
#endif /* EINVAL */

  /* Restore preserved errno for callers who might rely on it.
     (Note: errno has been set above based on eno/EINVAL to match original behavior.) */
  (void) _mhd_saved_errno; /* keep the saved value visible to avoid unused warning */

  return MHD_NO;  /* *** Function failure exit point *** */
}
