static void
new_connections_list_process_ (struct MHD_Daemon *daemon)
{
  struct MHD_Connection *local_head;
  struct MHD_Connection *local_tail;
  mhd_assert (daemon->have_new);
  mhd_assert (MHD_D_IS_THREAD_SAFE_ (daemon));

  /* Open log file for probing control flow and state. */
  {
    FILE *log = fopen("/tmp/logs/1758.txt", "a");
    if (NULL != log)
      {
        fprintf (log, "new_connections_list_process_: entry daemon=%p have_new=%d\n",
                 (void *) daemon, (int) daemon->have_new);
        fclose (log);
      }
  }

  /* Detach DL-list of new connections from the daemon for
   * following local processing. */
  MHD_mutex_lock_chk_ (&daemon->new_connections_mutex);
  {
    FILE *log = fopen("/tmp/logs/1758.txt", "a");
    if (NULL != log)
      {
        fprintf (log, "new_connections_list_process_: locked new_connections_mutex\n");
        fclose (log);
      }
  }
  mhd_assert (NULL != daemon->new_connections_head);
  local_head = daemon->new_connections_head;
  local_tail = daemon->new_connections_tail;
  daemon->new_connections_head = NULL;
  daemon->new_connections_tail = NULL;
  daemon->have_new = false;
  MHD_mutex_unlock_chk_ (&daemon->new_connections_mutex);
  {
    FILE *log = fopen("/tmp/logs/1758.txt", "a");
    if (NULL != log)
      {
        fprintf (log, "new_connections_list_process_: detached list local_head=%p local_tail=%p have_new set to false\n",
                 (void *) local_head, (void *) local_tail);
        fclose (log);
      }
  }

  (void) local_head; /* Mute compiler warning */
  /* Log occurrence of the no-op statement above to provide evidence that it executed. */
  {
    FILE *log = fopen("/tmp/logs/1758.txt", "a");
    if (NULL != log)
      {
        fprintf (log, "new_connections_list_process_: executed '(void) local_head;' for local_head=%p (no-op)\n",
                 (void *) local_head);
        fclose (log);
      }
  }

  /* Process new connections in FIFO order. */
  do
  {
    struct MHD_Connection *c;   /**< Currently processed connection */

    c = local_tail;
    {
      FILE *log = fopen("/tmp/logs/1758.txt", "a");
      if (NULL != log)
        {
          fprintf (log, "new_connections_list_process_: about to DLL_remove with local_head=%p local_tail=%p c=%p\n",
                   (void *) local_head, (void *) local_tail, (void *) c);
          fclose (log);
        }
    }

    DLL_remove (local_head,
                local_tail,
                c);
    mhd_assert (daemon == c->daemon);
    if (MHD_NO == new_connection_process_ (daemon, c))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Failed to start serving new connection.\n"));
#endif
      {
        FILE *log = fopen("/tmp/logs/1758.txt", "a");
        if (NULL != log)
          {
            fprintf (log, "new_connections_list_process_: new_connection_process_ returned MHD_NO for c=%p daemon=%p\n",
                     (void *) c, (void *) daemon);
            fclose (log);
          }
      }
      (void) 0;
    }
    else
    {
      FILE *log = fopen("/tmp/logs/1758.txt", "a");
      if (NULL != log)
        {
          fprintf (log, "new_connections_list_process_: new_connection_process_ succeeded for c=%p daemon=%p\n",
                   (void *) c, (void *) daemon);
          fclose (log);
        }
    }
  } while (NULL != local_tail);

  {
    FILE *log = fopen("/tmp/logs/1758.txt", "a");
    if (NULL != log)
      {
        fprintf (log, "new_connections_list_process_: exit daemon=%p\n", (void *) daemon);
        fclose (log);
      }
  }

}
