static bool
parse_dauth_params (const char *str,
                    const size_t str_len,
                    struct MHD_RqDAuth *pdauth)
{
  /* Logging probe */
  FILE *log = fopen("/tmp/logs/3103.txt", "a");
  if (log)
  {
    fprintf(log, "ENTER parse_dauth_params: str=%p, str_len=%zu\n", (const void *)str, str_len);
    /* Print a short prefix of the input safely */
    fprintf(log, "input prefix: \"");
    for (size_t jj = 0; jj < str_len && jj < 64; ++jj)
    {
      unsigned char c = (unsigned char)str[jj];
      if (c >= 0x20 && c < 0x7f)
        fputc(c, log);
      else
        fprintf(log, "\\x%02x", c);
    }
    fprintf(log, "\"\n");
    fflush(log);
  }

  /* The tokens */
  static const struct _MHD_cstr_w_len nonce_tk = _MHD_S_STR_W_LEN ("nonce");
  static const struct _MHD_cstr_w_len opaque_tk = _MHD_S_STR_W_LEN ("opaque");
  static const struct _MHD_cstr_w_len algorithm_tk =
    _MHD_S_STR_W_LEN ("algorithm");
  static const struct _MHD_cstr_w_len response_tk =
    _MHD_S_STR_W_LEN ("response");
  static const struct _MHD_cstr_w_len username_tk =
    _MHD_S_STR_W_LEN ("username");
  static const struct _MHD_cstr_w_len username_ext_tk =
    _MHD_S_STR_W_LEN ("username*");
  static const struct _MHD_cstr_w_len realm_tk = _MHD_S_STR_W_LEN ("realm");
  static const struct _MHD_cstr_w_len uri_tk = _MHD_S_STR_W_LEN ("uri");
  static const struct _MHD_cstr_w_len qop_tk = _MHD_S_STR_W_LEN ("qop");
  static const struct _MHD_cstr_w_len cnonce_tk = _MHD_S_STR_W_LEN ("cnonce");
  static const struct _MHD_cstr_w_len nc_tk = _MHD_S_STR_W_LEN ("nc");
  static const struct _MHD_cstr_w_len userhash_tk =
    _MHD_S_STR_W_LEN ("userhash");
  /* The locally processed parameters */
  struct MHD_RqDAuthParam userhash;
  struct MHD_RqDAuthParam algorithm;
  /* Indexes */
  size_t i;
  size_t p;
  /* The list of the tokens.
     The order of the elements matches the next array. */
  static const struct _MHD_cstr_w_len *const tk_names[] = {
    &nonce_tk,          /* 0 */
    &opaque_tk,         /* 1 */
    &algorithm_tk,      /* 2 */
    &response_tk,       /* 3 */
    &username_tk,       /* 4 */
    &username_ext_tk,   /* 5 */
    &realm_tk,          /* 6 */
    &uri_tk,            /* 7 */
    &qop_tk,            /* 8 */
    &cnonce_tk,         /* 9 */
    &nc_tk,             /* 10 */
    &userhash_tk        /* 11 */
  };
  /* The list of the parameters.
     The order of the elements matches the previous array. */
  struct MHD_RqDAuthParam *params[sizeof(tk_names) / sizeof(tk_names[0])];

  params[0 ] = &(pdauth->nonce);           /* 0 */
  params[1 ] = &(pdauth->opaque);          /* 1 */
  params[2 ] = &algorithm;                 /* 2 */
  params[3 ] = &(pdauth->response);        /* 3 */
  params[4 ] = &(pdauth->username);        /* 4 */
  params[5 ] = &(pdauth->username_ext);    /* 5 */
  params[6 ] = &(pdauth->realm);           /* 6 */
  params[7 ] = &(pdauth->uri);             /* 7 */
  params[8 ] = &(pdauth->qop_raw);         /* 8 */
  params[9 ] = &(pdauth->cnonce);          /* 9 */
  params[10] = &(pdauth->nc);              /* 10 */
  params[11] = &userhash;                  /* 11 */

  mhd_assert ((sizeof(tk_names) / sizeof(tk_names[0])) == \
              (sizeof(params) / sizeof(params[0])));
  memset (&userhash, 0, sizeof(userhash));
  memset (&algorithm, 0, sizeof(algorithm));
  i = 0;

  /* Skip all whitespaces at start */
  while (i < str_len && (' ' == str[i] || '\t' == str[i]))
    i++;

  if (log) { fprintf(log, "after initial whitespace skip: i=%zu\n", i); fflush(log); }

  while (str_len > i)
  {
    size_t left;
    mhd_assert (' ' != str[i]);
    mhd_assert ('\t' != str[i]);

    left = str_len - i;
    if (log) { fprintf(log, "top of main while: i=%zu, left=%zu, next_char=%s\n", i, left, (i < str_len ? "in-bounds" : "OOB")); fflush(log); }

    if ('=' == str[i])
    {
      if (log) { fprintf(log, "RETURN false: '=' as first char at i=%zu\n", i); fclose(log); }
      return false; /* The equal sign is not allowed as the first character */
    }
    for (p = 0; p < (sizeof(tk_names) / sizeof(tk_names[0])); ++p)
    {
      const struct _MHD_cstr_w_len *const tk_name = tk_names[p];
      struct MHD_RqDAuthParam *const param = params[p];
      if ( (tk_name->len <= left) &&
           MHD_str_equal_caseless_bin_n_ (str + i, tk_name->str,
                                          tk_name->len) &&
           ((tk_name->len == left) ||
            ('=' == str[i + tk_name->len]) ||
            (' ' == str[i + tk_name->len]) ||
            ('\t' == str[i + tk_name->len]) ||
            (',' == str[i + tk_name->len]) ||
            (';' == str[i + tk_name->len])) )
      {
        size_t value_start;
        size_t value_len;
        bool quoted; /* Only mark as "quoted" if backslash-escape used */

        if (log) { fprintf(log, "matched token p=%zu name=\"%s\" at i=%zu left=%zu\n", p, tk_name->str, i, left); fflush(log); }

        if (tk_name->len == left)
        {
          if (log) { fprintf(log, "RETURN false: no '=' after parameter name (tk_name->len==left) at i=%zu\n", i); fclose(log); }
          return false; /* No equal sign after parameter name, broken data */
        }

        quoted = false;
        i += tk_name->len;
        /* Skip all whitespaces before '=' */
        while (str_len > i && (' ' == str[i] || '\t' == str[i]))
          i++;
        if (log) { fprintf(log, "after skipping pre-'=' whitespace: i=%zu\n", i); fflush(log); }
        if ((i == str_len) || ('=' != str[i]))
        {
          if (log) { fprintf(log, "RETURN false: no '=' found after token at i=%zu (i==str_len:%d, char=''%c'')\n", i, (int)(i==str_len), (i < str_len ? str[i] : '?')); fclose(log); }
          return false; /* No equal sign, broken data */
        }
        i++;
        /* Skip all whitespaces after '=' */
        while (str_len > i && (' ' == str[i] || '\t' == str[i]))
          i++;
        if (log) { fprintf(log, "after skipping post-'=' whitespace: i=%zu\n", i); fflush(log); }
        if ((str_len > i) && ('"' == str[i]))
        { /* Value is in quotation marks */
          i++; /* Advance after the opening quote */
          value_start = i;
          while (str_len > i && '"' != str[i])
          {
            if ('\\' == str[i])
            {
              i++;
              quoted = true; /* Have escaped chars */
            }
            if (0 == str[i])
            {
              if (log) { fprintf(log, "RETURN false: binary zero in quoted parameter at i=%zu\n", i); fclose(log); }
              return false; /* Binary zero in parameter value */
            }
            i++;
          }
          if (str_len <= i)
          {
            if (log) { fprintf(log, "RETURN false: no closing quote (str_len=%zu, i=%zu)\n", str_len, i); fclose(log); }
            return false; /* No closing quote */
          }
          mhd_assert ('"' == str[i]);
          value_len = i - value_start;
          i++; /* Advance after the closing quote */
          if (log) { fprintf(log, "parsed quoted value: start=%zu len=%zu i(after)=%zu quoted=%d\n", value_start, value_len, i, (int)quoted); fflush(log); }
        }
        else
        {
          value_start = i;
          while (str_len > i && ',' != str[i] &&
                 ' ' != str[i] && '\t' != str[i] && ';' != str[i])
          {
            if (0 == str[i])
            {
              if (log) { fprintf(log, "RETURN false: binary zero in unquoted parameter at i=%zu\n", i); fclose(log); }
              return false;  /* Binary zero in parameter value */
            }
            i++;
          }

          /* Probe right before the potentially unsafe access in the original code. */
          if (log)
          {
            fprintf(log, "before semicolon-check: i=%zu, str_len=%zu (will perform if (';' == str[i]) as original code)\n", i, str_len);
            if (i < str_len)
            {
              unsigned char c = (unsigned char)str[i];
              if (c >= 0x20 && c < 0x7f)
                fprintf(log, "char at str[i]= '%c' (0x%02x)\n", c, c);
              else
                fprintf(log, "char at str[i]= 0x%02x\n", c);
            }
            else
            {
              fprintf(log, "char at str[i] is OOB (i >= str_len). The following original check will read str[i] and may be OOB.\n");
            }
            fflush(log);
          }

          if (';' == str[i])
          {
            if (log) { fprintf(log, "RETURN false: semicolon in parameter value at i=%zu\n", i); fclose(log); }
            return false;  /* Semicolon in parameter value */
          }
          value_len = i - value_start;
          if (log) { fprintf(log, "parsed unquoted value: start=%zu len=%zu i(after)=%zu\n", value_start, value_len, i); fflush(log); }
        }
        /* Skip all whitespaces after parameter value */
        while (str_len > i && (' ' == str[i] || '\t' == str[i]))
          i++;
        if ((str_len > i) && (',' != str[i]))
        {
          if (log) { fprintf(log, "RETURN false: garbage after parameter value at i=%zu char=%c\n", i, (i < str_len ? str[i] : '?')); fclose(log); }
          return false; /* Garbage after parameter value */
        }

        /* Have valid parameter name and value */
        mhd_assert (! quoted || 0 != value_len);
        param->value.str = str + value_start;
        param->value.len = value_len;
        param->quoted = quoted;

        if (log)
        {
          fprintf(log, "stored param p=%zu value.len=%zu quoted=%d\n", p, value_len, (int)quoted);
          fflush(log);
        }

        break; /* Found matching parameter name */
      }
    }
    if (p == (sizeof(tk_names) / sizeof(tk_names[0])))
    {
      /* No matching parameter name */
      while (str_len > i && ',' != str[i])
      {
        if ((0 == str[i]) || (';' == str[i]))
        {
          if (log) { fprintf(log, "RETURN false: not allowed char in unknown parameter parsing at i=%zu char=%c\n", i, (i < str_len ? str[i] : '?')); fclose(log); }
          return false; /* Not allowed characters */
        }
        if ('"' == str[i])
        { /* Skip quoted part */
          i++; /* Advance after the opening quote */
          while (str_len > i && '"' != str[i])
          {
            if (0 == str[i])
            {
              if (log) { fprintf(log, "RETURN false: binary zero in unknown-parameter quoted section at i=%zu\n", i); fclose(log); }
              return false;  /* Binary zero is not allowed */
            }
            if ('\\' == str[i])
              i++;           /* Skip escaped char */
            i++;
          }
          if (str_len <= i)
          {
            if (log) { fprintf(log, "RETURN false: no closing quote in unknown-parameter parsing (str_len=%zu,i=%zu)\n", str_len, i); fclose(log); }
            return false; /* No closing quote */
          }
          mhd_assert ('"' == str[i]);
        }
        i++;
      }
    }
    mhd_assert (str_len == i || ',' == str[i]);
    if (str_len > i)
      i++; /* Advance after ',' */
    /* Skip all whitespaces before next parameter name */
    while (i < str_len && (' ' == str[i] || '\t' == str[i]))
      i++;
    if (log) { fprintf(log, "end of main-iteration: i=%zu\n", i); fflush(log); }
  }

  /* Postprocess values */

  if (NULL != userhash.value.str)
  {
    if (userhash.quoted)
      pdauth->userhash =
        MHD_str_equal_caseless_quoted_s_bin_n (userhash.value.str, \
                                               userhash.value.len, \
                                               "true");
    else
      pdauth->userhash =
        MHD_str_equal_caseless_s_bin_n_ ("true", userhash.value.str, \
                                         userhash.value.len);

    if (log) { fprintf(log, "postprocess: userhash present quoted=%d value.len=%zu\n", (int)userhash.quoted, userhash.value.len); fflush(log); }
  }
  else
  {
    pdauth->userhash = false;
    if (log) { fprintf(log, "postprocess: userhash absent\n"); fflush(log); }
  }

  pdauth->algo3 = get_rq_dauth_algo (&algorithm);
  pdauth->qop = get_rq_dauth_qop (&pdauth->qop_raw);

  if (log) { fprintf(log, "EXIT parse_dauth_params: returning true\n"); fclose(log); }
  return true;
}
