static enum MHD_Result
post_process_multipart (struct MHD_PostProcessor *pp,
                        const char *post_data,
                        size_t post_data_len)
{
  char *buf;
  size_t max;
  size_t ioff;
  size_t poff;
  int state_changed;
  FILE *log = fopen("/tmp/logs/1663.txt", "a");

  if (log)
  {
    fprintf(log, "post_process_multipart ENTER: pp=%p post_data_len=%zu buffer_pos=%zu buffer_size=%zu state=%d skip_rn=%d\n",
            (void *)pp, post_data_len, pp->buffer_pos, pp->buffer_size, (int)pp->state, (int)pp->skip_rn);
    fflush(log);
  }

  buf = (char *) &pp[1];
  ioff = 0;
  poff = 0;
  state_changed = 1;
  while ( (poff < post_data_len) ||
          ( (pp->buffer_pos > 0) &&
            (0 != state_changed) ) )
  {
    /* first, move as much input data
       as possible to our internal buffer */
    max = pp->buffer_size - pp->buffer_pos;
    if (max > post_data_len - poff)
      max = post_data_len - poff;
    if (log)
    {
      fprintf(log, "memcpy: poff=%zu max=%zu pp->buffer_pos(before)=%zu\n", poff, max, pp->buffer_pos);
      fflush(log);
    }
    memcpy (&buf[pp->buffer_pos],
            &post_data[poff],
            max);
    poff += max;
    pp->buffer_pos += max;
    if (log)
    {
      fprintf(log, "after memcpy: poff=%zu pp->buffer_pos=%zu ioff=%zu\n", poff, pp->buffer_pos, ioff);
      fflush(log);
    }
    if ( (0 == max) &&
         (0 == state_changed) &&
         (poff < post_data_len) )
    {
      pp->state = PP_Error;
      if (log)
      {
        fprintf(log, "ERROR: out of memory branch hit: max=0 state_changed=0 poff=%zu post_data_len=%zu\n",
                poff, post_data_len);
        fflush(log);
        fclose(log);
      }
      return MHD_NO;            /* out of memory */
    }
    state_changed = 0;

    /* first state machine for '\r'-'\n' and '--' handling */
    switch (pp->skip_rn)
    {
    case RN_Inactive:
      break;
    case RN_OptN:
      if (buf[0] == '\n')
      {
        ioff++;
        if (log) { fprintf(log, "RN_OptN: saw LF at buf[0], ioff=%zu\n", ioff); fflush(log); }
        pp->skip_rn = RN_Inactive;
        goto AGAIN;
      }
    /* fall-through! */
    case RN_Dash:
      if (buf[0] == '-')
      {
        ioff++;
        if (log) { fprintf(log, "RN_Dash: saw '-' at buf[0], ioff=%zu\n", ioff); fflush(log); }
        pp->skip_rn = RN_Dash2;
        goto AGAIN;
      }
      pp->skip_rn = RN_Full;
    /* fall-through! */
    case RN_Full:
      if (buf[0] == '\r')
      {
        if ( (pp->buffer_pos > 1) &&
             ('\n' == buf[1]) )
        {
          pp->skip_rn = RN_Inactive;
          ioff += 2;
          if (log) { fprintf(log, "RN_Full: saw CRLF, ioff=%zu buffer_pos=%zu\n", ioff, pp->buffer_pos); fflush(log); }
        }
        else
        {
          pp->skip_rn = RN_OptN;
          ioff++;
          if (log) { fprintf(log, "RN_Full: saw CR (no LF yet), ioff=%zu buffer_pos=%zu\n", ioff, pp->buffer_pos); fflush(log); }
        }
        goto AGAIN;
      }
      if (buf[0] == '\n')
      {
        ioff++;
        if (log) { fprintf(log, "RN_Full: saw lone LF, ioff=%zu\n", ioff); fflush(log); }
        pp->skip_rn = RN_Inactive;
        goto AGAIN;
      }
      pp->skip_rn = RN_Inactive;
      pp->state = PP_Error;
      if (log)
      {
        fprintf(log, "ERROR: no '\\r\\n' found, buf[0]=0x%02x buffer_pos=%zu ioff=%zu\n", (unsigned char)buf[0], pp->buffer_pos, ioff);
        fflush(log);
        fclose(log);
      }
      return MHD_NO;            /* no '\r\n' */
    case RN_Dash2:
      if (buf[0] == '-')
      {
        ioff++;
        pp->skip_rn = RN_Full;
        pp->state = pp->dash_state;
        if (log) { fprintf(log, "RN_Dash2: saw second '-', ioff=%zu new state=%d\n", ioff, (int)pp->state); fflush(log); }
        goto AGAIN;
      }
      pp->state = PP_Error;
      break;
    }

    /* main state engine */
    switch (pp->state)
    {
    case PP_Error:
      if (log) { fprintf(log, "PP_Error: returning MHD_NO\n"); fflush(log); fclose(log); }
      return MHD_NO;
    case PP_Done:
      /* did not expect to receive more data */
      pp->state = PP_Error;
      if (log) { fprintf(log, "PP_Done: unexpected more data, setting PP_Error and returning MHD_NO\n"); fflush(log); fclose(log); }
      return MHD_NO;
    case PP_Init:
      /**
       * Per RFC2046 5.1.1 NOTE TO IMPLEMENTORS, consume anything
       * prior to the first multipart boundary:
       *
       * > There appears to be room for additional information prior
       * > to the first boundary delimiter line and following the
       * > final boundary delimiter line.  These areas should
       * > generally be left blank, and implementations must ignore
       * > anything that appears before the first boundary delimiter
       * > line or after the last one.
       */
      if (log) { fprintf(log, "PP_Init: calling find_boundary with blen=%zu ioff=%zu\n", pp->blen, ioff); fflush(log); }
      (void) find_boundary (pp,
                            pp->boundary,
                            pp->blen,
                            &ioff,
                            PP_ProcessEntryHeaders,
                            PP_Done);
      break;
    case PP_NextBoundary:
      if (MHD_NO == find_boundary (pp,
                                   pp->boundary,
                                   pp->blen,
                                   &ioff,
                                   PP_ProcessEntryHeaders,
                                   PP_Done))
      {
        if (pp->state == PP_Error)
        {
          if (log) { fprintf(log, "PP_NextBoundary: find_boundary -> MHD_NO and pp->state==PP_Error, returning MHD_NO\n"); fflush(log); fclose(log); }
          return MHD_NO;
        }
        goto END;
      }
      break;
    case PP_ProcessEntryHeaders:
      pp->must_ikvi = true;
      if (MHD_NO ==
          process_multipart_headers (pp,
                                     &ioff,
                                     PP_PerformCheckMultipart))
      {
        if (pp->state == PP_Error)
        {
          if (log) { fprintf(log, "PP_ProcessEntryHeaders: process_multipart_headers -> MHD_NO and PP_Error, returning MHD_NO\n"); fflush(log); fclose(log); }
          return MHD_NO;
        }
        else
          goto END;
      }
      state_changed = 1;
      break;
    case PP_PerformCheckMultipart:
      if ( (NULL != pp->content_type) &&
           (MHD_str_equal_caseless_n_ (pp->content_type,
                                       "multipart/mixed",
                                       MHD_STATICSTR_LEN_ ("multipart/mixed"))))
      {
        pp->nested_boundary = strstr (pp->content_type,
                                      "boundary=");
        if (NULL == pp->nested_boundary)
        {
          pp->state = PP_Error;
          if (log) { fprintf(log, "PP_PerformCheckMultipart: nested boundary not found, PP_Error -> returning MHD_NO\n"); fflush(log); fclose(log); }
          return MHD_NO;
        }
        pp->nested_boundary =
          strdup (&pp->nested_boundary[MHD_STATICSTR_LEN_ ("boundary=")]);
        if (NULL == pp->nested_boundary)
        {
          /* out of memory */
          pp->state = PP_Error;
          if (log) { fprintf(log, "PP_PerformCheckMultipart: strdup failed -> out of memory -> returning MHD_NO\n"); fflush(log); fclose(log); }
          return MHD_NO;
        }
        /* free old content type, we will need that field
           for the content type of the nested elements */
        free (pp->content_type);
        pp->content_type = NULL;
        pp->nlen = strlen (pp->nested_boundary);
        pp->state = PP_Nested_Init;
        state_changed = 1;
        break;
      }
      pp->state = PP_ProcessValueToBoundary;
      pp->value_offset = 0;
      state_changed = 1;
      break;
    case PP_ProcessValueToBoundary:
      if (MHD_NO == process_value_to_boundary (pp,
                                               &ioff,
                                               pp->boundary,
                                               pp->blen,
                                               PP_PerformCleanup,
                                               PP_Done))
      {
        if (pp->state == PP_Error)
        {
          if (log) { fprintf(log, "PP_ProcessValueToBoundary: process_value_to_boundary -> MHD_NO and PP_Error, returning MHD_NO\n"); fflush(log); fclose(log); }
          return MHD_NO;
        }
        break;
      }
      break;
    case PP_PerformCleanup:
      /* clean up state of one multipart form-data element! */
      pp->have = NE_none;
      free_unmarked (pp);
      if (NULL != pp->nested_boundary)
      {
        free (pp->nested_boundary);
        pp->nested_boundary = NULL;
      }
      pp->state = PP_ProcessEntryHeaders;
      state_changed = 1;
      break;
    case PP_Nested_Init:
      if (NULL == pp->nested_boundary)
      {
        pp->state = PP_Error;
        if (log) { fprintf(log, "PP_Nested_Init: nested_boundary NULL -> PP_Error -> returning MHD_NO\n"); fflush(log); fclose(log); }
        return MHD_NO;
      }
      if (MHD_NO == find_boundary (pp,
                                   pp->nested_boundary,
                                   pp->nlen,
                                   &ioff,
                                   PP_Nested_PerformMarking,
                                   PP_NextBoundary /* or PP_Error? */))
      {
        if (pp->state == PP_Error)
        {
          if (log) { fprintf(log, "PP_Nested_Init: find_boundary -> MHD_NO and PP_Error -> returning MHD_NO\n"); fflush(log); fclose(log); }
          return MHD_NO;
        }
        goto END;
      }
      break;
    case PP_Nested_PerformMarking:
      /* remember what headers were given
         globally */
      pp->have = NE_none;
      if (NULL != pp->content_name)
        pp->have |= NE_content_name;
      if (NULL != pp->content_type)
        pp->have |= NE_content_type;
      if (NULL != pp->content_filename)
        pp->have |= NE_content_filename;
      if (NULL != pp->content_transfer_encoding)
        pp->have |= NE_content_transfer_encoding;
      pp->state = PP_Nested_ProcessEntryHeaders;
      state_changed = 1;
      break;
    case PP_Nested_ProcessEntryHeaders:
      pp->value_offset = 0;
      if (MHD_NO ==
          process_multipart_headers (pp,
                                     &ioff,
                                     PP_Nested_ProcessValueToBoundary))
      {
        if (pp->state == PP_Error)
        {
          if (log) { fprintf(log, "PP_Nested_ProcessEntryHeaders: process_multipart_headers -> MHD_NO and PP_Error -> returning MHD_NO\n"); fflush(log); fclose(log); }
          return MHD_NO;
        }
        else
          goto END;
      }
      state_changed = 1;
      break;
    case PP_Nested_ProcessValueToBoundary:
      if (MHD_NO == process_value_to_boundary (pp,
                                               &ioff,
                                               pp->nested_boundary,
                                               pp->nlen,
                                               PP_Nested_PerformCleanup,
                                               PP_NextBoundary))
      {
        if (pp->state == PP_Error)
        {
          if (log) { fprintf(log, "PP_Nested_ProcessValueToBoundary: process_value_to_boundary -> MHD_NO and PP_Error -> returning MHD_NO\n"); fflush(log); fclose(log); }
          return MHD_NO;
        }
        break;
      }
      break;
    case PP_Nested_PerformCleanup:
      free_unmarked (pp);
      pp->state = PP_Nested_ProcessEntryHeaders;
      state_changed = 1;
      break;
    case PP_ProcessKey:
    case PP_ProcessValue:
    case PP_Callback:
    default:
      if (log) { fprintf(log, "PANIC: internal error state=%d\n", (int)pp->state); fflush(log); fclose(log); }
      MHD_PANIC (_ ("internal error.\n")); /* should never happen! */
    }
AGAIN:
    if (ioff > 0)
    {
      /* Log memmove parameters before performing the operation */
      if (log)
      {
        size_t move_len = pp->buffer_pos - ioff;
        fprintf(log, "memmove AT AGAIN: ioff=%zu buffer_pos=%zu move_len=%zu (&buf[ioff] index=%zu)\n",
                ioff, pp->buffer_pos, move_len, ioff);
        fflush(log);
      }
      memmove (buf,
               &buf[ioff],
               pp->buffer_pos - ioff);
      pp->buffer_pos -= ioff;
      if (log) { fprintf(log, "after memmove AT AGAIN: new buffer_pos=%zu\n", pp->buffer_pos); fflush(log); }
      ioff = 0;
      state_changed = 1;
    }
  }
END:
  if (0 != ioff)
  {
    if (log)
    {
      size_t move_len = pp->buffer_pos - ioff;
      fprintf(log, "memmove AT END: ioff=%zu buffer_pos=%zu move_len=%zu\n", ioff, pp->buffer_pos, move_len);
      fflush(log);
    }
    memmove (buf,
             &buf[ioff],
             pp->buffer_pos - ioff);
    pp->buffer_pos -= ioff;
  }
  if (poff < post_data_len)
  {
    pp->state = PP_Error;
    if (log) { fprintf(log, "FINAL ERROR: poff (%zu) < post_data_len (%zu). Setting PP_Error and returning MHD_NO\n", poff, post_data_len); fflush(log); fclose(log); }
    return MHD_NO;              /* serious error */
  }
  if (log)
  {
    fprintf(log, "post_process_multipart EXIT: success pp=%p pp->buffer_pos=%zu poff=%zu post_data_len=%zu state=%d\n",
            (void *)pp, pp->buffer_pos, poff, post_data_len, (int)pp->state);
    fflush(log);
    fclose(log);
  }
  return MHD_YES;
}
