static enum MHD_Result
post_process_multipart (struct MHD_PostProcessor *pp,
                        const char *post_data,
                        size_t post_data_len)
{
  char *buf;
  size_t max;
  size_t ioff;
  size_t poff;
  int state_changed;

  /* ENTRY LOG */
  {
    FILE *log = fopen("/tmp/logs/1665.txt", "a");
    if (NULL != log)
    {
      fprintf(log, "ENTER post_process_multipart: pp=%p post_data_len=%zu\n",
              (void *) pp, post_data_len);
      if (NULL != pp)
      {
        fprintf(log, "  initial: buffer_pos=%zu buffer_size=%zu state=%d skip_rn=%d blen=%zu boundary=%p\n",
                pp->buffer_pos, pp->buffer_size, (int) pp->state, (int) pp->skip_rn, pp->blen, (void *) pp->boundary);
        if (NULL != pp->boundary)
        {
          size_t sl = pp->blen > 64 ? 64 : pp->blen;
          fprintf(log, "  boundary (len=%zu) preview='%.*s'\n", pp->blen, (int) sl, pp->boundary);
        }
        if (NULL != pp->nested_boundary)
        {
          size_t sl2 = pp->nlen > 64 ? 64 : pp->nlen;
          fprintf(log, "  nested_boundary (len=%zu) preview='%.*s' ptr=%p\n",
                  pp->nlen, (int) sl2, pp->nested_boundary, (void *) pp->nested_boundary);
        }
      }
      fclose(log);
    }
  }

  buf = (char *) &pp[1];
  ioff = 0;
  poff = 0;
  state_changed = 1;
  while ( (poff < post_data_len) ||
          ( (pp->buffer_pos > 0) &&
            (0 != state_changed) ) )
  {
    /* LOG loop entry */
    {
      FILE *log = fopen("/tmp/logs/1665.txt", "a");
      if (NULL != log)
      {
        fprintf(log, "LOOP: poff=%zu post_data_len=%zu buffer_pos=%zu ioff=%zu state=%d skip_rn=%d\n",
                poff, post_data_len, pp->buffer_pos, ioff, (int) pp->state, (int) pp->skip_rn);
        if (pp->buffer_pos > 0)
        {
          /* preview first few bytes in buffer */
          size_t pv = pp->buffer_pos > 16 ? 16 : pp->buffer_pos;
          fprintf(log, "  buffer preview (len=%zu): ", pv);
          for (size_t xi = 0; xi < pv; ++xi)
            fprintf(log, "%02x ", (unsigned char) buf[xi]);
          fprintf(log, "\n");
        }
        fclose(log);
      }
    }

    /* first, move as much input data
       as possible to our internal buffer */
    max = pp->buffer_size - pp->buffer_pos;
    if (max > post_data_len - poff)
      max = post_data_len - poff;
    memcpy (&buf[pp->buffer_pos],
            &post_data[poff],
            max);
    poff += max;
    pp->buffer_pos += max;
    if ( (0 == max) &&
         (0 == state_changed) &&
         (poff < post_data_len) )
    {
      {
        FILE *log = fopen("/tmp/logs/1665.txt", "a");
        if (NULL != log)
        {
          fprintf(log, "ERROR: out of memory condition: max=0 state_changed=0 poff=%zu post_data_len=%zu\n",
                  poff, post_data_len);
          fclose(log);
        }
      }
      pp->state = PP_Error;
      return MHD_NO;            /* out of memory */
    }
    state_changed = 0;

    /* first state machine for '\r'-'\n' and '--' handling */
    switch (pp->skip_rn)
    {
    case RN_Inactive:
      break;
    case RN_OptN:
      if (buf[0] == '\n')
      {
        ioff++;
        pp->skip_rn = RN_Inactive;
        goto AGAIN;
      }
    /* fall-through! */
    case RN_Dash:
      if (buf[0] == '-')
      {
        ioff++;
        pp->skip_rn = RN_Dash2;
        goto AGAIN;
      }
      pp->skip_rn = RN_Full;
    /* fall-through! */
    case RN_Full:
      if (buf[0] == '\r')
      {
        if ( (pp->buffer_pos > 1) &&
             ('\n' == buf[1]) )
        {
          pp->skip_rn = RN_Inactive;
          ioff += 2;
        }
        else
        {
          pp->skip_rn = RN_OptN;
          ioff++;
        }
        goto AGAIN;
      }
      if (buf[0] == '\n')
      {
        ioff++;
        pp->skip_rn = RN_Inactive;
        goto AGAIN;
      }
      pp->skip_rn = RN_Inactive;
      pp->state = PP_Error;
      {
        FILE *log = fopen("/tmp/logs/1665.txt", "a");
        if (NULL != log)
        {
          fprintf(log, "ERROR: expected CRLF but got %02x (buf[0])\n", (unsigned char) buf[0]);
          fclose(log);
        }
      }
      return MHD_NO;            /* no '\r\n' */
    case RN_Dash2:
      if (buf[0] == '-')
      {
        ioff++;
        pp->skip_rn = RN_Full;
        pp->state = pp->dash_state;
        goto AGAIN;
      }
      pp->state = PP_Error;
      break;
    }

    /* main state engine */
    switch (pp->state)
    {
    case PP_Error:
      {
        FILE *log = fopen("/tmp/logs/1665.txt", "a");
        if (NULL != log)
        {
          fprintf(log, "RETURN: PP_Error encountered, returning MHD_NO\n");
          fclose(log);
        }
      }
      return MHD_NO;
    case PP_Done:
      /* did not expect to receive more data */
      pp->state = PP_Error;
      {
        FILE *log = fopen("/tmp/logs/1665.txt", "a");
        if (NULL != log)
        {
          fprintf(log, "ERROR: PP_Done but got more data -> set PP_Error and return MHD_NO\n");
          fclose(log);
        }
      }
      return MHD_NO;
    case PP_Init:
      /**
       * Per RFC2046 5.1.1 NOTE TO IMPLEMENTORS, consume anything
       * prior to the first multipart boundary:
       *
       * > There appears to be room for additional information prior
       * > to the first boundary delimiter line and following the
       * > final boundary delimiter line.  These areas should
       * > generally be left blank, and implementations must ignore
       * > anything that appears before the first boundary delimiter
       * > line or after the last one.
       */
      {
        FILE *log = fopen("/tmp/logs/1665.txt", "a");
        if (NULL != log)
        {
          fprintf(log, "ACTION: PP_Init -> find_boundary with boundary ptr=%p blen=%zu\n",
                  (void *) pp->boundary, pp->blen);
          if (pp->boundary)
          {
            size_t sl = pp->blen > 80 ? 80 : pp->blen;
            fprintf(log, "  boundary preview: '%.*s'\n", (int) sl, pp->boundary);
          }
          fclose(log);
        }
      }
      (void) find_boundary (pp,
                            pp->boundary,
                            pp->blen,
                            &ioff,
                            PP_ProcessEntryHeaders,
                            PP_Done);
      break;
    case PP_NextBoundary:
      if (MHD_NO == find_boundary (pp,
                                   pp->boundary,
                                   pp->blen,
                                   &ioff,
                                   PP_ProcessEntryHeaders,
                                   PP_Done))
      {
        if (pp->state == PP_Error)
        {
          FILE *log = fopen("/tmp/logs/1665.txt", "a");
          if (NULL != log)
          {
            fprintf(log, "find_boundary returned MHD_NO and pp->state==PP_Error\n");
            fclose(log);
          }
          return MHD_NO;
        }
        goto END;
      }
      break;
    case PP_ProcessEntryHeaders:
      pp->must_ikvi = true;
      if (MHD_NO ==
          process_multipart_headers (pp,
                                     &ioff,
                                     PP_PerformCheckMultipart))
      {
        if (pp->state == PP_Error)
        {
          FILE *log = fopen("/tmp/logs/1665.txt", "a");
          if (NULL != log)
          {
            fprintf(log, "process_multipart_headers returned MHD_NO and pp->state==PP_Error\n");
            fclose(log);
          }
          return MHD_NO;
        }
        else
          goto END;
      }
      state_changed = 1;
      break;
    case PP_PerformCheckMultipart:
      if ( (NULL != pp->content_type) &&
           (MHD_str_equal_caseless_n_ (pp->content_type,
                                       "multipart/mixed",
                                       MHD_STATICSTR_LEN_ ("multipart/mixed"))))
      {
        pp->nested_boundary = strstr (pp->content_type,
                                      "boundary=");
        if (NULL == pp->nested_boundary)
        {
          pp->state = PP_Error;
          {
            FILE *log = fopen("/tmp/logs/1665.txt", "a");
            if (NULL != log)
            {
              fprintf(log, "ERROR: multipart/mixed but no nested boundary in content_type (%p)\n",
                      (void *) pp->content_type);
              fclose(log);
            }
          }
          return MHD_NO;
        }
        pp->nested_boundary =
          strdup (&pp->nested_boundary[MHD_STATICSTR_LEN_ ("boundary=")]);
        if (NULL == pp->nested_boundary)
        {
          /* out of memory */
          pp->state = PP_Error;
          return MHD_NO;
        }
        /* free old content type, we will need that field
           for the content type of the nested elements */
        free (pp->content_type);
        pp->content_type = NULL;
        pp->nlen = strlen (pp->nested_boundary);
        pp->state = PP_Nested_Init;
        state_changed = 1;
        break;
      }
      pp->state = PP_ProcessValueToBoundary;
      pp->value_offset = 0;
      state_changed = 1;
      break;
    case PP_ProcessValueToBoundary:
      /* LOG before calling process_value_to_boundary */
      {
        FILE *log = fopen("/tmp/logs/1665.txt", "a");
        if (NULL != log)
        {
          fprintf(log, "ACTION: PP_ProcessValueToBoundary -> call process_value_to_boundary with boundary=%p blen=%zu ioff=%zu\n",
                  (void *) pp->boundary, pp->blen, ioff);
          if (pp->boundary)
          {
            size_t sl = pp->blen > 80 ? 80 : pp->blen;
            fprintf(log, "  boundary preview: '%.*s'\n", (int) sl, pp->boundary);
          }
          fclose(log);
        }
      }
      if (MHD_NO == process_value_to_boundary (pp,
                                               &ioff,
                                               pp->boundary,
                                               pp->blen,
                                               PP_PerformCleanup,
                                               PP_Done))
      {
        /* LOG after failure */
        if (pp->state == PP_Error)
        {
          FILE *log = fopen("/tmp/logs/1665.txt", "a");
          if (NULL != log)
          {
            fprintf(log, "process_value_to_boundary returned MHD_NO and set PP_Error (boundary=%p blen=%zu)\n",
                    (void *) pp->boundary, pp->blen);
            fclose(log);
          }
          return MHD_NO;
        }
        break;
      }
      break;
    case PP_PerformCleanup:
      /* clean up state of one multipart form-data element! */
      pp->have = NE_none;
      free_unmarked (pp);
      if (NULL != pp->nested_boundary)
      {
        free (pp->nested_boundary);
        pp->nested_boundary = NULL;
      }
      pp->state = PP_ProcessEntryHeaders;
      state_changed = 1;
      break;
    case PP_Nested_Init:
      if (NULL == pp->nested_boundary)
      {
        pp->state = PP_Error;
        return MHD_NO;
      }
      if (MHD_NO == find_boundary (pp,
                                   pp->nested_boundary,
                                   pp->nlen,
                                   &ioff,
                                   PP_Nested_PerformMarking,
                                   PP_NextBoundary /* or PP_Error? */))
      {
        if (pp->state == PP_Error)
          return MHD_NO;
        goto END;
      }
      break;
    case PP_Nested_PerformMarking:
      /* remember what headers were given
         globally */
      pp->have = NE_none;
      if (NULL != pp->content_name)
        pp->have |= NE_content_name;
      if (NULL != pp->content_type)
        pp->have |= NE_content_type;
      if (NULL != pp->content_filename)
        pp->have |= NE_content_filename;
      if (NULL != pp->content_transfer_encoding)
        pp->have |= NE_content_transfer_encoding;
      pp->state = PP_Nested_ProcessEntryHeaders;
      state_changed = 1;
      break;
    case PP_Nested_ProcessEntryHeaders:
      pp->value_offset = 0;
      if (MHD_NO ==
          process_multipart_headers (pp,
                                     &ioff,
                                     PP_Nested_ProcessValueToBoundary))
      {
        if (pp->state == PP_Error)
          return MHD_NO;
        else
          goto END;
      }
      state_changed = 1;
      break;
    case PP_Nested_ProcessValueToBoundary:
      /* LOG before calling process_value_to_boundary for nested */
      {
        FILE *log = fopen("/tmp/logs/1665.txt", "a");
        if (NULL != log)
        {
          fprintf(log, "ACTION: PP_Nested_ProcessValueToBoundary -> call process_value_to_boundary with nested_boundary=%p nlen=%zu ioff=%zu\n",
                  (void *) pp->nested_boundary, pp->nlen, ioff);
          if (pp->nested_boundary)
          {
            size_t sl = pp->nlen > 80 ? 80 : pp->nlen;
            fprintf(log, "  nested_boundary preview: '%.*s'\n", (int) sl, pp->nested_boundary);
          }
          fclose(log);
        }
      }
      if (MHD_NO == process_value_to_boundary (pp,
                                               &ioff,
                                               pp->nested_boundary,
                                               pp->nlen,
                                               PP_Nested_PerformCleanup,
                                               PP_NextBoundary))
      {
        if (pp->state == PP_Error)
          return MHD_NO;
        break;
      }
      break;
    case PP_Nested_PerformCleanup:
      free_unmarked (pp);
      pp->state = PP_Nested_ProcessEntryHeaders;
      state_changed = 1;
      break;
    case PP_ProcessKey:
    case PP_ProcessValue:
    case PP_Callback:
    default:
      MHD_PANIC (_ ("internal error.\n")); /* should never happen! */
    }
AGAIN:
    if (ioff > 0)
    {
      memmove (buf,
               &buf[ioff],
               pp->buffer_pos - ioff);
      pp->buffer_pos -= ioff;
      ioff = 0;
      state_changed = 1;
    }
  }
END:
  if (0 != ioff)
  {
    memmove (buf,
             &buf[ioff],
             pp->buffer_pos - ioff);
    pp->buffer_pos -= ioff;
  }
  if (poff < post_data_len)
  {
    {
      FILE *log = fopen("/tmp/logs/1665.txt", "a");
      if (NULL != log)
      {
        fprintf(log, "FINAL ERROR: poff (%zu) < post_data_len (%zu) -> set PP_Error and return MHD_NO\n",
                poff, post_data_len);
        fclose(log);
      }
    }
    pp->state = PP_Error;
    return MHD_NO;              /* serious error */
  }

  {
    FILE *log = fopen("/tmp/logs/1665.txt", "a");
    if (NULL != log)
    {
      fprintf(log, "EXIT post_process_multipart: success, returning MHD_YES pp=%p\n", (void *) pp);
      fclose(log);
    }
  }
  return MHD_YES;
}
