static enum MHD_Result
post_process_urlencoded (struct MHD_PostProcessor *pp,
                         const char *post_data,
                         size_t post_data_len)
{
  char *kbuf = (char *) &pp[1];
  size_t poff;
  const char *start_key = NULL;
  const char *end_key = NULL;
  const char *start_value = NULL;
  const char *end_value = NULL;
  const char *last_escape = NULL;

  /* Logging probe: open file and log entry & initial state */
  {
    FILE *fp = fopen("/tmp/logs/2573.txt", "a");
    if (NULL != fp)
    {
      fprintf(fp, "ENTER post_process_urlencoded: pp=%p post_data=%p post_data_len=%zu initial_state=%d\n",
              (void *)pp, (const void *)post_data, post_data_len, (int)pp->state);
      fclose(fp);
    }
  }

  mhd_assert (PP_Callback != pp->state);

  poff = 0;
  while ( ( (poff < post_data_len) ||
            (pp->state == PP_Callback) ) &&
          (pp->state != PP_Error) )
  {
    switch (pp->state)
    {
    case PP_Error:
      /* clearly impossible as per while loop invariant */
      abort ();
      break; /* Unreachable */
    case PP_Init:
      /* initial phase */
      mhd_assert (NULL == start_key);
      mhd_assert (NULL == end_key);
      mhd_assert (NULL == start_value);
      mhd_assert (NULL == end_value);
      switch (post_data[poff])
      {
      case '=':
        /* Case: (no key)'=' */
        /* Empty key with value */
        pp->state = PP_Error;
        continue;
      case '&':
        /* Case: (no key)'&' */
        /* Empty key without value */
        poff++;
        continue;
      case '\n':
      case '\r':
        /* Case: (no key)'\n' or (no key)'\r' */
        pp->state = PP_Done;
        poff++;
        break;
      default:
        /* normal character, key start, advance! */
        pp->state = PP_ProcessKey;
        start_key = &post_data[poff];
        pp->must_ikvi = true;
        poff++;
        continue;
      }
      break; /* end PP_Init */
    case PP_ProcessKey:
      /* key phase */
      mhd_assert (NULL == start_value);
      mhd_assert (NULL == end_value);
      mhd_assert (NULL != start_key || 0 == poff);
      mhd_assert (0 != poff || NULL == start_key);
      mhd_assert (NULL == end_key);
      switch (post_data[poff])
      {
      case '=':
        /* Case: 'key=' */
        if (0 != poff)
          end_key = &post_data[poff];
        poff++;
        pp->state = PP_ProcessValue;
        break;
      case '&':
        /* Case: 'key&' */
        if (0 != poff)
          end_key = &post_data[poff];
        poff++;
        pp->state = PP_Callback;
        /* Log transition: key without value -> callback */
        {
          FILE *fp = fopen("/tmp/logs/2573.txt", "a");
          if (NULL != fp)
          {
            fprintf(fp, "PP_ProcessKey -> PP_Callback at poff=%zu start_key=%p end_key=%p start_value=%p end_value=%p must_ikvi=%d\n",
                    poff, (const void *)start_key, (const void *)end_key,
                    (const void *)start_value, (const void *)end_value, (int)pp->must_ikvi);
            fclose(fp);
          }
        }
        break;
      case '\n':
      case '\r':
        /* Case: 'key\n' or 'key\r' */
        if (0 != poff)
          end_key = &post_data[poff];
        /* No advance here, 'PP_Done' will be selected by next 'PP_Init' phase */
        pp->state = PP_Callback;
        break;
      default:
        /* normal character, advance! */
        if (0 == poff)
          start_key = post_data;
        poff++;
        break;
      }
      mhd_assert (NULL == end_key || NULL != start_key);
      break; /* end PP_ProcessKey */
    case PP_ProcessValue:
      if (NULL == start_value)
        start_value = &post_data[poff];
      switch (post_data[poff])
      {
      case '=':
        /* case 'key==' */
        pp->state = PP_Error;
        continue;
      case '&':
        /* case 'value&' */
        end_value = &post_data[poff];
        poff++;
        if (pp->must_ikvi ||
            (start_value != end_value) )
        {
          pp->state = PP_Callback;
          /* Log transition: value ended by '&' leading to callback */
          {
            FILE *fp = fopen("/tmp/logs/2573.txt", "a");
            if (NULL != fp)
            {
              fprintf(fp, "PP_ProcessValue -> PP_Callback via '&' at poff=%zu start_value=%p end_value=%p must_ikvi=%d\n",
                      poff, (const void *)start_value, (const void *)end_value, (int)pp->must_ikvi);
              fclose(fp);
            }
          }
        }
        else
        {
          pp->buffer_pos = 0;
          pp->value_offset = 0;
          pp->state = PP_Init;
          start_value = NULL;
          end_value = NULL;
        }
        continue;
      case '\n':
      case '\r':
        /* Case: 'value\n' or 'value\r' */
        end_value = &post_data[poff];
        if (pp->must_ikvi ||
            (start_value != end_value) )
        {
          pp->state = PP_Callback; /* No poff advance here to set PP_Done in the next iteration */
          /* Log transition: value ended by newline leading to callback */
          {
            FILE *fp = fopen("/tmp/logs/2573.txt", "a");
            if (NULL != fp)
            {
              fprintf(fp, "PP_ProcessValue -> PP_Callback via newline at poff=%zu start_value=%p end_value=%p must_ikvi=%d\n",
                      poff, (const void *)start_value, (const void *)end_value, (int)pp->must_ikvi);
              fclose(fp);
            }
          }
        }
        else
        {
          poff++;
          pp->state = PP_Done;
        }
        break;
      case '%':
        last_escape = &post_data[poff];
        poff++;
        break;
      case '0':
      case '1':
      case '2':
      case '3':
      case '4':
      case '5':
      case '6':
      case '7':
      case '8':
      case '9':
        /* character, may be part of escaping */
        poff++;
        continue;
      default:
        /* normal character, no more escaping! */
        last_escape = NULL;
        poff++;
        continue;
      }
      break; /* end PP_ProcessValue */
    case PP_Done:
      switch (post_data[poff])
      {
      case '\n':
      case '\r':
        poff++;
        continue;
      }
      /* unexpected data at the end, fail! */
      pp->state = PP_Error;
      break;
    case PP_Callback:
      mhd_assert ((NULL != end_key) || (NULL == start_key));
      if (1)
      {
        const size_t key_len = (size_t) (end_key - start_key);
        mhd_assert (end_key >= start_key);
        if (0 != key_len)
        {
          if ( (pp->buffer_pos + key_len >= pp->buffer_size) ||
               (pp->buffer_pos + key_len < pp->buffer_pos) )
          {
            /* key too long, cannot parse! */
            pp->state = PP_Error;
            continue;
          }
          /* compute key, if we have not already */
          memcpy (&kbuf[pp->buffer_pos],
                  start_key,
                  key_len);
          pp->buffer_pos += key_len;
          start_key = NULL;
          end_key = NULL;
          pp->must_unescape_key = true;
        }
      }
#ifdef _DEBUG
      else
        mhd_assert (0 != pp->buffer_pos);
#endif /* _DEBUG */
      if (pp->must_unescape_key)
      {
        kbuf[pp->buffer_pos] = '\0'; /* 0-terminate key */
        MHD_unescape_plus (kbuf);
        MHD_http_unescape (kbuf);
        pp->must_unescape_key = false;
      }

      /* Logging before calling process_value: capture pointers and state */
      {
        FILE *fp = fopen("/tmp/logs/2573.txt", "a");
        if (NULL != fp)
        {
          fprintf(fp, "PP_Callback invoked: pp=%p state=%d poff=%zu start_key=%p end_key=%p start_value=%p end_value=%p last_escape=%p buffer_pos=%zu buffer_size=%zu must_ikvi=%d must_unescape_key=%d value_offset=%zu\n",
                  (void *)pp, (int)pp->state, poff,
                  (const void *)start_key, (const void *)end_key,
                  (const void *)start_value, (const void *)end_value,
                  (const void *)last_escape, pp->buffer_pos, pp->buffer_size,
                  (int)pp->must_ikvi, (int)pp->must_unescape_key, pp->value_offset);

          if (NULL == start_value && NULL == end_value)
          {
            fprintf(fp, "PP_Callback: start_value==NULL and end_value==NULL (potential violation scenario).\n");
          }
          fclose(fp);
        }
      }

      process_value (pp,
                     start_value,
                     end_value,
                     NULL);

      /* Log result after process_value returns */
      {
        FILE *fp = fopen("/tmp/logs/2573.txt", "a");
        if (NULL != fp)
        {
          fprintf(fp, "after process_value: pp=%p state=%d poff=%zu start_value=%p end_value=%p buffer_pos=%zu value_offset=%zu\n",
                  (void *)pp, (int)pp->state, poff,
                  (const void *)start_value, (const void *)end_value,
                  pp->buffer_pos, pp->value_offset);
          fclose(fp);
        }
      }

      if (PP_Error == pp->state)
        continue;
      pp->value_offset = 0;
      start_value = NULL;
      end_value = NULL;
      pp->buffer_pos = 0;
      pp->state = PP_Init;
      break;
    case PP_NextBoundary:
    case PP_ProcessEntryHeaders:
    case PP_PerformCheckMultipart:
    case PP_ProcessValueToBoundary:
    case PP_PerformCleanup:
    case PP_Nested_Init:
    case PP_Nested_PerformMarking:
    case PP_Nested_ProcessEntryHeaders:
    case PP_Nested_ProcessValueToBoundary:
    case PP_Nested_PerformCleanup:
    default:
      MHD_PANIC (_ ("internal error.\n")); /* should never happen! */
    }
    mhd_assert ((end_key == NULL) || (start_key != NULL));
    mhd_assert ((end_value == NULL) || (start_value != NULL));
  }

  mhd_assert (PP_Callback != pp->state);

  if (PP_Error == pp->state)
  {
    /* State in error, returning failure */
    /* Log error return */
    {
      FILE *fp = fopen("/tmp/logs/2573.txt", "a");
      if (NULL != fp)
      {
        fprintf(fp, "EXIT post_process_urlencoded: PP_Error return pp=%p state=%d\n", (void *)pp, (int)pp->state);
        fclose(fp);
      }
    }
    return MHD_NO;
  }

  /* save remaining data for next iteration */
  if (NULL != start_key)
  {
    size_t key_len;
    mhd_assert ((PP_ProcessKey == pp->state) || (NULL != end_key));
    if (NULL == end_key)
      end_key = &post_data[poff];
    mhd_assert (end_key >= start_key);
    key_len = (size_t) (end_key - start_key);
    mhd_assert (0 != key_len); /* it must be always non-zero here */
    if (pp->buffer_pos + key_len >= pp->buffer_size)
    {
      pp->state = PP_Error;
      return MHD_NO;
    }
    memcpy (&kbuf[pp->buffer_pos],
            start_key,
            key_len);
    pp->buffer_pos += key_len;
    pp->must_unescape_key = true;
    start_key = NULL;
    end_key = NULL;
  }
  if ( (NULL != start_value) &&
       (PP_ProcessValue == pp->state) )
  {
    /* compute key, if we have not already */
    if (pp->must_unescape_key)
    {
      kbuf[pp->buffer_pos] = '\0'; /* 0-terminate key */
      MHD_unescape_plus (kbuf);
      MHD_http_unescape (kbuf);
      pp->must_unescape_key = false;
    }
    if (NULL == end_value)
      end_value = &post_data[poff];
    if ( (NULL != last_escape) &&
         (2 < (end_value - last_escape)) )
      last_escape = NULL;

    /* Log the call to process_value for remaining data */
    {
      FILE *fp = fopen("/tmp/logs/2573.txt", "a");
      if (NULL != fp)
      {
        fprintf(fp, "final block call: before process_value with start_value=%p end_value=%p last_escape=%p pp=%p state=%d\n",
                (const void *)start_value, (const void *)end_value, (const void *)last_escape, (void *)pp, (int)pp->state);
        fclose(fp);
      }
    }

    process_value (pp,
                   start_value,
                   end_value,
                   last_escape);
    pp->must_ikvi = false;
  }
  if (PP_Error == pp->state)
  {
    /* State in error, returning failure */
    {
      FILE *fp = fopen("/tmp/logs/2573.txt", "a");
      if (NULL != fp)
      {
        fprintf(fp, "EXIT post_process_urlencoded: PP_Error after final processing pp=%p state=%d\n", (void *)pp, (int)pp->state);
        fclose(fp);
      }
    }
    return MHD_NO;
  }

  /* Log normal successful exit */
  {
    FILE *fp = fopen("/tmp/logs/2573.txt", "a");
    if (NULL != fp)
    {
      fprintf(fp, "EXIT post_process_urlencoded: success pp=%p state=%d\n", (void *)pp, (int)pp->state);
      fclose(fp);
    }
  }
  return MHD_YES;
}
