static void
post_send_setopt (struct MHD_Connection *connection,
                  bool plain_send_next,
                  bool push_data)
{
  /* Try to buffer data if not sending the final piece.
   * Final piece is indicated by push_data == true. */
  const bool buffer_data = (! push_data);
  FILE *log = fopen("/tmp/logs/2996.txt", "a");
  if (log)
  {
    fprintf(log, "post_send_setopt entered: this=%p connection=%p push_data=%d buffer_data=%d plain_send_next=%d\n",
            (void *)__builtin_return_address(0), (void *)connection, (int)push_data, (int)buffer_data, (int)plain_send_next);
    if (connection)
      fprintf(log, "initial states: is_nonip=%d sk_corked=%d sk_nodelay=%d\n",
              (int)connection->is_nonip, (int)connection->sk_corked, (int)connection->sk_nodelay);
    fflush(log);
  }

  if (_MHD_YES == connection->is_nonip)
  {
    if (log)
    {
      fprintf(log, "early return: is_nonip == _MHD_YES\n");
      fclose(log);
    }
    return;
  }
  if (buffer_data)
  {
    if (log)
    {
      fprintf(log, "early return: buffer_data true (not final piece)\n");
      fclose(log);
    }
    return; /* Nothing to do after send(). */
  }

#ifndef MHD_USE_MSG_MORE
  (void) plain_send_next; /* Mute compiler warning */
#endif /* ! MHD_USE_MSG_MORE */

  /* Need to push data. */
#ifdef MHD_TCP_CORK_NOPUSH
#ifdef _MHD_CORK_RESET_PUSH_DATA_ALWAYS
#ifdef _MHD_NODELAY_SET_PUSH_DATA_ALWAYS
#ifdef MHD_USE_MSG_MORE
  if (_MHD_OFF == connection->sk_corked)
  {
    if (_MHD_ON == connection->sk_nodelay)
    {
      if (log)
      {
        fprintf(log, "return: socket uncorked and nodelay on -> data already pushed by send()\n");
        fclose(log);
      }
      return; /* Data was already pushed by send(). */
    }
  }
  /* This is Linux kernel. There are options:
   * * Push the data by setting of TCP_NODELAY (without change
   *   of the cork on the socket),
   * * Push the data by resetting of TCP_CORK.
   * The optimal choice depends on the next final send functions
   * used on the same socket. If TCP_NODELAY wasn't set then push
   * data by setting TCP_NODELAY (TCP_NODELAY will not be removed
   * and is needed to push the data by send() without MSG_MORE).
   * If send()/sendmsg() will be used next than push data by
   * resetting of TCP_CORK so next send without MSG_MORE will push
   * data to the network (without additional sys-call to push data).
   * If next final send function will not support MSG_MORE (like
   * sendfile() or TLS-connection) than push data by setting
   * TCP_NODELAY so socket will remain corked (no additional
   * sys-call before next send()). */
  if ((_MHD_ON != connection->sk_nodelay) ||
      (! plain_send_next))
  {
    bool tmp_res = MHD_connection_set_nodelay_state_ (connection, true);
    if (log)
    {
      fprintf(log, "MHD_connection_set_nodelay_state_(..., true) returned %d\n", (int)tmp_res);
      fflush(log);
    }
    if (tmp_res)
    {
      if (log)
      {
        fprintf(log, "return: Data has been pushed by TCP_NODELAY\n");
        fclose(log);
      }
      return; /* Data has been pushed by TCP_NODELAY. */
    }
    /* Failed to set TCP_NODELAY for the socket.
     * Really unlikely to happen on TCP connections. */
    tmp_res = MHD_connection_set_cork_state_ (connection, false);
    if (log)
    {
      fprintf(log, "MHD_connection_set_cork_state_(..., false) returned %d\n", (int)tmp_res);
      fflush(log);
    }
    if (tmp_res)
    {
      if (log)
      {
        fprintf(log, "return: Data has been pushed by uncorking the socket\n");
        fclose(log);
      }
      return; /* Data has been pushed by uncorking the socket. */
    }
    /* Failed to uncork the socket.
     * Really unlikely to happen on TCP connections. */

    /* The socket cannot be uncorked, no way to push data */
    if (log)
    {
      fprintf(log, "failed: cannot set nodelay or uncork -> socket cannot be uncorked\n");
      fflush(log);
    }
  }
  else
  {
    bool tmp_res = MHD_connection_set_cork_state_ (connection, false);
    if (log)
    {
      fprintf(log, "MHD_connection_set_cork_state_(..., false) returned %d\n", (int)tmp_res);
      fflush(log);
    }
    if (tmp_res)
    {
      if (log)
      {
        fprintf(log, "return: Data has been pushed by uncorking the socket\n");
        fclose(log);
      }
      return; /* Data has been pushed by uncorking the socket. */
    }
    /* Failed to uncork the socket.
     * Really unlikely to happen on TCP connections. */
    tmp_res = MHD_connection_set_nodelay_state_ (connection, true);
    if (log)
    {
      fprintf(log, "MHD_connection_set_nodelay_state_(..., true) returned %d\n", (int)tmp_res);
      fflush(log);
    }
    if (tmp_res)
    {
      if (log)
      {
        fprintf(log, "return: Data has been pushed by TCP_NODELAY\n");
        fclose(log);
      }
      return; /* Data has been pushed by TCP_NODELAY. */
    }
    /* Failed to set TCP_NODELAY for the socket.
     * Really unlikely to happen on TCP connections. */

    /* The socket cannot be uncorked, no way to push data */
    if (log)
    {
      fprintf(log, "failed: cannot uncork and cannot set nodelay -> socket remains corked\n");
      fflush(log);
    }
  }
#else  /* ! MHD_USE_MSG_MORE */
  /* Use setting of TCP_NODELAY here to avoid sys-call
   * for corking the socket during sending of the next response. */
  {
    bool tmp_res = MHD_connection_set_nodelay_state_ (connection, true);
    if (log)
    {
      fprintf(log, "MHD_connection_set_nodelay_state_(..., true) returned %d\n", (int)tmp_res);
      fflush(log);
    }
    if (tmp_res)
    {
      if (log)
      {
        fprintf(log, "return: Data was pushed by TCP_NODELAY\n");
        fclose(log);
      }
      return; /* Data was pushed by TCP_NODELAY. */
    }
  }
  /* Failed to set TCP_NODELAY for the socket.
   * Really unlikely to happen on TCP connections. */
  {
    bool tmp_res = MHD_connection_set_cork_state_ (connection, false);
    if (log)
    {
      fprintf(log, "MHD_connection_set_cork_state_(..., false) returned %d\n", (int)tmp_res);
      fflush(log);
    }
    if (tmp_res)
    {
      if (log)
      {
        fprintf(log, "return: Data was pushed by uncorking the socket\n");
        fclose(log);
      }
      return; /* Data was pushed by uncorking the socket. */
    }
  }
  /* Failed to uncork the socket.
   * Really unlikely to happen on TCP connections. */

  /* The socket remains corked, no way to push data */
  if (log)
  {
    fprintf(log, "failed: cannot set nodelay and cannot uncork -> socket remains corked (MSG_MORE not used)\n");
    fflush(log);
  }
#endif /* ! MHD_USE_MSG_MORE */
#else  /* ! _MHD_NODELAY_SET_PUSH_DATA_ALWAYS */
  {
    bool tmp_res = MHD_connection_set_cork_state_ (connection, false);
    if (log)
    {
      fprintf(log, "MHD_connection_set_cork_state_(..., false) returned %d\n", (int)tmp_res);
      fflush(log);
    }
    if (tmp_res)
    {
      if (log)
      {
        fprintf(log, "return: Data was pushed by uncorking the socket (nodeay not set push data always)\n");
        fclose(log);
      }
      return; /* Data was pushed by uncorking the socket. */
    }
  }
  /* Failed to uncork the socket.
   * Really unlikely to happen on TCP connections. */
  if (log)
  {
    fprintf(log, "failed: cannot uncork (nodeay not set push data always) -> socket remains corked\n");
    fflush(log);
  }
  if (log)
  {
    fprintf(log, "return: Socket remains corked, no way to push data (nodeay not set push data always)\n");
    fclose(log);
  }
  return; /* Socket remains corked, no way to push data */
#endif /* ! _MHD_NODELAY_SET_PUSH_DATA_ALWAYS */
#else  /* ! _MHD_CORK_RESET_PUSH_DATA_ALWAYS */
  /* This is a typical *BSD or Darwin kernel. */

  if (_MHD_OFF == connection->sk_corked)
  {
    if (_MHD_ON == connection->sk_nodelay)
    {
      if (log)
      {
        fprintf(log, "return: uncorked and nodelay on (BSD/Darwin) -> data already pushed by send()\n");
        fclose(log);
      }
      return; /* Data was already pushed by send(). */
    }

    /* Unlikely to reach this code.
     * TCP_NODELAY should be turned on before send(). */
    {
      bool tmp_res = MHD_connection_set_nodelay_state_ (connection, true);
      if (log)
      {
        fprintf(log, "MHD_connection_set_nodelay_state_(..., true) returned %d (BSD/Darwin)\n", (int)tmp_res);
        fflush(log);
      }
      if (tmp_res)
      {
        /* TCP_NODELAY has been set on uncorked socket.
         * Use zero-send to push the data. */
        bool zs = zero_send_ (connection);
        if (log)
        {
          fprintf(log, "zero_send_ returned %d after setting nodelay (BSD/Darwin)\n", (int)zs);
          fflush(log);
        }
        if (zs)
        {
          if (log)
          {
            fprintf(log, "return: The data has been pushed by zero-send (BSD/Darwin)\n");
            fclose(log);
          }
          return; /* The data has been pushed by zero-send. */
        }
      }
    }

    /* Failed to push the data by all means. */
    /* There is nothing left to try. */
    if (log)
    {
      fprintf(log, "failed: uncorked but could not push data (BSD/Darwin)\n");
      fflush(log);
    }
  }
  else
  {
#ifdef _MHD_CORK_RESET_PUSH_DATA
    enum MHD_tristate old_cork_state = connection->sk_corked;
#endif /* _MHD_CORK_RESET_PUSH_DATA */
    /* The socket is corked or cork state is unknown. */

    {
      bool tmp_res = MHD_connection_set_cork_state_ (connection, false);
      if (log)
      {
        fprintf(log, "MHD_connection_set_cork_state_(..., false) returned %d (BSD/Darwin)\n", (int)tmp_res);
        fflush(log);
      }
      if (tmp_res)
      {
#ifdef _MHD_CORK_RESET_PUSH_DATA
        /* FreeBSD kernel */
        if (_MHD_OFF == old_cork_state)
        {
          if (log)
          {
            fprintf(log, "return: Data has been pushed by uncorking the socket (FreeBSD case old_cork_state off)\n");
            fclose(log);
          }
          return; /* Data has been pushed by uncorking the socket. */
        }
#endif /* _MHD_CORK_RESET_PUSH_DATA */

        /* Unlikely to reach this code.
         * The data should be pushed by uncorking (FreeBSD) or
         * the socket should be uncorked before send(). */
        if ((_MHD_ON == connection->sk_nodelay) ||
            (MHD_connection_set_nodelay_state_ (connection, true)))
        {
          /* TCP_NODELAY is turned ON on uncorked socket.
           * Use zero-send to push the data. */
          bool zs = zero_send_ (connection);
          if (log)
          {
            fprintf(log, "zero_send_ returned %d after uncorking (BSD/Darwin)\n", (int)zs);
            fflush(log);
          }
          if (zs)
          {
            if (log)
            {
              fprintf(log, "return: The data has been pushed by zero-send (after uncorking)\n");
              fclose(log);
            }
            return; /* The data has been pushed by zero-send. */
          }
        }
      }
    }
    /* The socket remains corked. Data cannot be pushed. */
    if (log)
    {
      fprintf(log, "failed: socket remains corked (BSD/Darwin) -> cannot push data\n");
      fflush(log);
    }
  }
#endif /* ! _MHD_CORK_RESET_PUSH_DATA_ALWAYS */
#else  /* ! MHD_TCP_CORK_NOPUSH */
  /* Corking is not supported. Buffering is controlled
   * by TCP_NODELAY only. */
  mhd_assert (_MHD_ON != connection->sk_corked);
  if (_MHD_ON == connection->sk_nodelay)
  {
    if (log)
    {
      fprintf(log, "return: TCP_NODELAY already on (no cork support) -> data already pushed\n");
      fclose(log);
    }
    return; /* Data was already pushed by send(). */
  }

  /* Unlikely to reach this code.
   * TCP_NODELAY should be turned on before send(). */
  {
    bool tmp_res = MHD_connection_set_nodelay_state_ (connection, true);
    if (log)
    {
      fprintf(log, "MHD_connection_set_nodelay_state_(..., true) returned %d (no cork support)\n", (int)tmp_res);
      fflush(log);
    }
    if (tmp_res)
    {
      /* TCP_NODELAY has been set.
       * Use zero-send to push the data. */
      bool zs = zero_send_ (connection);
      if (log)
      {
        fprintf(log, "zero_send_ returned %d (no cork support)\n", (int)zs);
        fflush(log);
      }
      if (zs)
      {
        if (log)
        {
          fprintf(log, "return: The data has been pushed by zero-send (no cork support)\n");
          fclose(log);
        }
        return; /* The data has been pushed by zero-send. */
      }
    }
  }

  /* Failed to push the data. */
  if (log)
  {
    fprintf(log, "failed: no cork support and could not push data\n");
    fflush(log);
  }
#endif /* ! MHD_TCP_CORK_NOPUSH */
#ifdef HAVE_MESSAGES
  MHD_DLOG (connection->daemon,
            _ ("Failed to push the data from buffers to the network. "
               "Client may experience some delay "
               "(usually in range 200ms - 5 sec).\n"));
#endif /* HAVE_MESSAGES */
  if (log)
  {
    fprintf(log, "final return: reached logging/cleanup path -> returning\n");
    fclose(log);
  }
  return;
}
