static void
post_send_setopt (struct MHD_Connection *connection,
                  bool plain_send_next,
                  bool push_data)
{
  /* Try to buffer data if not sending the final piece.
   * Final piece is indicated by push_data == true. */
  const bool buffer_data = (! push_data);
  FILE *___mhd_log_f = NULL;

  /* Log entry */
  ___mhd_log_f = fopen("/tmp/logs/2998.txt", "a");
  if (___mhd_log_f)
  {
    fprintf(___mhd_log_f, "ENTER post_send_setopt: connection=%p plain_send_next=%d push_data=%d buffer_data=%d\n",
            (void *) connection, (int) plain_send_next, (int) push_data, (int) buffer_data);
    fclose(___mhd_log_f);
    ___mhd_log_f = NULL;
  }

  if (_MHD_YES == connection->is_nonip)
  {
    ___mhd_log_f = fopen("/tmp/logs/2998.txt", "a");
    if (___mhd_log_f)
    {
      fprintf(___mhd_log_f, "RETURN: connection->is_nonip == _MHD_YES (connection=%p)\n", (void *) connection);
      fclose(___mhd_log_f);
      ___mhd_log_f = NULL;
    }
    return;
  }
  if (buffer_data)
  {
    ___mhd_log_f = fopen("/tmp/logs/2998.txt", "a");
    if (___mhd_log_f)
    {
      fprintf(___mhd_log_f, "RETURN: buffer_data == true (nothing to do after send) connection=%p\n", (void *) connection);
      fclose(___mhd_log_f);
      ___mhd_log_f = NULL;
    }
    return; /* Nothing to do after send(). */
  }

#ifndef MHD_USE_MSG_MORE
  /* Log that plain_send_next is being explicitly discarded in the no-MSG_MORE build. */
  ___mhd_log_f = fopen("/tmp/logs/2998.txt", "a");
  if (___mhd_log_f)
  {
    fprintf(___mhd_log_f, "MHD_USE_MSG_MORE not defined: discarding plain_send_next (value=%d) (connection=%p)\n",
            (int) plain_send_next, (void *) connection);
    fclose(___mhd_log_f);
    ___mhd_log_f = NULL;
  }
  (void) plain_send_next; /* Mute compiler warning */
#endif /* ! MHD_USE_MSG_MORE */

  /* Need to push data. */
#ifdef MHD_TCP_CORK_NOPUSH
#ifdef _MHD_CORK_RESET_PUSH_DATA_ALWAYS
#ifdef _MHD_NODELAY_SET_PUSH_DATA_ALWAYS
#ifdef MHD_USE_MSG_MORE
  if (_MHD_OFF == connection->sk_corked)
  {
    if (_MHD_ON == connection->sk_nodelay)
    {
      ___mhd_log_f = fopen("/tmp/logs/2998.txt", "a");
      if (___mhd_log_f)
      {
        fprintf(___mhd_log_f, "RETURN: socket uncorked and nodelay ON; data already pushed by send (connection=%p)\n",
                (void *) connection);
        fclose(___mhd_log_f);
        ___mhd_log_f = NULL;
      }
      return; /* Data was already pushed by send(). */
    }
  }
  /* This is Linux kernel. There are options:
   * * Push the data by setting of TCP_NODELAY (without change
   *   of the cork on the socket),
   * * Push the data by resetting of TCP_CORK.
   * The optimal choice depends on the next final send functions
   * used on the same socket. If TCP_NODELAY wasn't set then push
   * data by setting TCP_NODELAY (TCP_NODELAY will not be removed
   * and is needed to push the data by send() without MSG_MORE).
   * If send()/sendmsg() will be used next than push data by
   * resetting of TCP_CORK so next send without MSG_MORE will push
   * data to the network (without additional sys-call to push data).
   * If next final send function will not support MSG_MORE (like
   * sendfile() or TLS-connection) than push data by setting
   * TCP_NODELAY so socket will remain corked (no additional
   * sys-call before next send()). */
  if ((_MHD_ON != connection->sk_nodelay) ||
      (! plain_send_next))
  {
    if (MHD_connection_set_nodelay_state_ (connection, true))
    {
      ___mhd_log_f = fopen("/tmp/logs/2998.txt", "a");
      if (___mhd_log_f)
      {
        fprintf(___mhd_log_f, "RETURN: MHD_connection_set_nodelay_state_ succeeded (pushed by TCP_NODELAY) (connection=%p)\n",
                (void *) connection);
        fclose(___mhd_log_f);
        ___mhd_log_f = NULL;
      }
      return; /* Data has been pushed by TCP_NODELAY. */
    }
    /* Failed to set TCP_NODELAY for the socket.
     * Really unlikely to happen on TCP connections. */
    if (MHD_connection_set_cork_state_ (connection, false))
    {
      ___mhd_log_f = fopen("/tmp/logs/2998.txt", "a");
      if (___mhd_log_f)
      {
        fprintf(___mhd_log_f, "RETURN: MHD_connection_set_cork_state_ succeeded (pushed by uncorking) (connection=%p)\n",
                (void *) connection);
        fclose(___mhd_log_f);
        ___mhd_log_f = NULL;
      }
      return; /* Data has been pushed by uncorking the socket. */
    }
    /* Failed to uncork the socket.
     * Really unlikely to happen on TCP connections. */

    /* The socket cannot be uncorked, no way to push data */
  }
  else
  {
    if (MHD_connection_set_cork_state_ (connection, false))
    {
      ___mhd_log_f = fopen("/tmp/logs/2998.txt", "a");
      if (___mhd_log_f)
      {
        fprintf(___mhd_log_f, "RETURN: uncork succeeded in else branch (pushed by uncorking) (connection=%p)\n",
                (void *) connection);
        fclose(___mhd_log_f);
        ___mhd_log_f = NULL;
      }
      return; /* Data has been pushed by uncorking the socket. */
    }
    /* Failed to uncork the socket.
     * Really unlikely to happen on TCP connections. */
    if (MHD_connection_set_nodelay_state_ (connection, true))
    {
      ___mhd_log_f = fopen("/tmp/logs/2998.txt", "a");
      if (___mhd_log_f)
      {
        fprintf(___mhd_log_f, "RETURN: set_nodelay succeeded in else branch (pushed by TCP_NODELAY) (connection=%p)\n",
                (void *) connection);
        fclose(___mhd_log_f);
        ___mhd_log_f = NULL;
      }
      return; /* Data has been pushed by TCP_NODELAY. */
    }
    /* Failed to set TCP_NODELAY for the socket.
     * Really unlikely to happen on TCP connections. */

    /* The socket cannot be uncorked, no way to push data */
  }
#else  /* ! MHD_USE_MSG_MORE */
  /* Use setting of TCP_NODELAY here to avoid sys-call
   * for corking the socket during sending of the next response. */
  if (MHD_connection_set_nodelay_state_ (connection, true))
  {
    ___mhd_log_f = fopen("/tmp/logs/2998.txt", "a");
    if (___mhd_log_f)
    {
      fprintf(___mhd_log_f, "RETURN: MHD_connection_set_nodelay_state_ succeeded in !MHD_USE_MSG_MORE block (pushed by TCP_NODELAY) (connection=%p)\n",
              (void *) connection);
      fclose(___mhd_log_f);
      ___mhd_log_f = NULL;
    }
    return; /* Data was pushed by TCP_NODELAY. */
  }
  /* Failed to set TCP_NODELAY for the socket.
   * Really unlikely to happen on TCP connections. */
  if (MHD_connection_set_cork_state_ (connection, false))
  {
    ___mhd_log_f = fopen("/tmp/logs/2998.txt", "a");
    if (___mhd_log_f)
    {
      fprintf(___mhd_log_f, "RETURN: MHD_connection_set_cork_state_ succeeded in !MHD_USE_MSG_MORE block (pushed by uncorking) (connection=%p)\n",
              (void *) connection);
      fclose(___mhd_log_f);
      ___mhd_log_f = NULL;
    }
    return; /* Data was pushed by uncorking the socket. */
  }
  /* Failed to uncork the socket.
   * Really unlikely to happen on TCP connections. */

  /* The socket remains corked, no way to push data */
#endif /* ! MHD_USE_MSG_MORE */
#else  /* ! _MHD_NODELAY_SET_PUSH_DATA_ALWAYS */
  if (MHD_connection_set_cork_state_ (connection, false))
  {
    ___mhd_log_f = fopen("/tmp/logs/2998.txt", "a");
    if (___mhd_log_f)
    {
      fprintf(___mhd_log_f, "RETURN: MHD_connection_set_cork_state_ succeeded when !_MHD_NODELAY_SET_PUSH_DATA_ALWAYS (pushed by uncorking) (connection=%p)\n",
              (void *) connection);
      fclose(___mhd_log_f);
      ___mhd_log_f = NULL;
    }
    return; /* Data was pushed by uncorking the socket. */
  }
  /* Failed to uncork the socket.
   * Really unlikely to happen on TCP connections. */
  ___mhd_log_f = fopen("/tmp/logs/2998.txt", "a");
  if (___mhd_log_f)
  {
    fprintf(___mhd_log_f, "RETURN: socket remains corked, no way to push data (!_MHD_NODELAY_SET_PUSH_DATA_ALWAYS) (connection=%p)\n",
            (void *) connection);
    fclose(___mhd_log_f);
    ___mhd_log_f = NULL;
  }
  return; /* Socket remains corked, no way to push data */
#endif /* ! _MHD_NODELAY_SET_PUSH_DATA_ALWAYS */
#else  /* ! _MHD_CORK_RESET_PUSH_DATA_ALWAYS */
  /* This is a typical *BSD or Darwin kernel. */

  if (_MHD_OFF == connection->sk_corked)
  {
    if (_MHD_ON == connection->sk_nodelay)
    {
      ___mhd_log_f = fopen("/tmp/logs/2998.txt", "a");
      if (___mhd_log_f)
      {
        fprintf(___mhd_log_f, "RETURN: uncorked and nodelay ON in BSD branch (data already pushed) (connection=%p)\n",
                (void *) connection);
        fclose(___mhd_log_f);
        ___mhd_log_f = NULL;
      }
      return; /* Data was already pushed by send(). */
    }

    /* Unlikely to reach this code.
     * TCP_NODELAY should be turned on before send(). */
    if (MHD_connection_set_nodelay_state_ (connection, true))
    {
      /* TCP_NODELAY has been set on uncorked socket.
       * Use zero-send to push the data. */
      if (zero_send_ (connection))
      {
        ___mhd_log_f = fopen("/tmp/logs/2998.txt", "a");
        if (___mhd_log_f)
        {
          fprintf(___mhd_log_f, "RETURN: zero_send_ succeeded after setting nodelay (BSD branch) (connection=%p)\n",
                  (void *) connection);
          fclose(___mhd_log_f);
          ___mhd_log_f = NULL;
        }
        return; /* The data has been pushed by zero-send. */
      }
    }

    /* Failed to push the data by all means. */
    /* There is nothing left to try. */
  }
  else
  {
#ifdef _MHD_CORK_RESET_PUSH_DATA
    enum MHD_tristate old_cork_state = connection->sk_corked;
#endif /* _MHD_CORK_RESET_PUSH_DATA */
    /* The socket is corked or cork state is unknown. */

    if (MHD_connection_set_cork_state_ (connection, false))
    {
#ifdef _MHD_CORK_RESET_PUSH_DATA
      /* FreeBSD kernel */
      if (_MHD_OFF == old_cork_state)
      {
        ___mhd_log_f = fopen("/tmp/logs/2998.txt", "a");
        if (___mhd_log_f)
        {
          fprintf(___mhd_log_f, "RETURN: uncork succeeded and old_cork_state==_MHD_OFF (data pushed) (connection=%p)\n",
                  (void *) connection);
          fclose(___mhd_log_f);
          ___mhd_log_f = NULL;
        }
        return; /* Data has been pushed by uncorking the socket. */
      }
#endif /* _MHD_CORK_RESET_PUSH_DATA */

      /* Unlikely to reach this code.
       * The data should be pushed by uncorking (FreeBSD) or
       * the socket should be uncorked before send(). */
      if ((_MHD_ON == connection->sk_nodelay) ||
          (MHD_connection_set_nodelay_state_ (connection, true)))
      {
        /* TCP_NODELAY is turned ON on uncorked socket.
         * Use zero-send to push the data. */
        if (zero_send_ (connection))
        {
          ___mhd_log_f = fopen("/tmp/logs/2998.txt", "a");
          if (___mhd_log_f)
          {
            fprintf(___mhd_log_f, "RETURN: zero_send_ succeeded after uncork/nodelay in BSD branch (connection=%p)\n",
                    (void *) connection);
            fclose(___mhd_log_f);
            ___mhd_log_f = NULL;
          }
          return; /* The data has been pushed by zero-send. */
        }
      }
    }
    /* The socket remains corked. Data cannot be pushed. */
  }
#endif /* ! _MHD_CORK_RESET_PUSH_DATA_ALWAYS */
#else  /* ! MHD_TCP_CORK_NOPUSH */
  /* Corking is not supported. Buffering is controlled
   * by TCP_NODELAY only. */
  mhd_assert (_MHD_ON != connection->sk_corked);
  if (_MHD_ON == connection->sk_nodelay)
  {
    ___mhd_log_f = fopen("/tmp/logs/2998.txt", "a");
    if (___mhd_log_f)
    {
      fprintf(___mhd_log_f, "RETURN: corking not supported and nodelay ON (data already pushed) (connection=%p)\n",
              (void *) connection);
      fclose(___mhd_log_f);
      ___mhd_log_f = NULL;
    }
    return; /* Data was already pushed by send(). */
  }

  /* Unlikely to reach this code.
   * TCP_NODELAY should be turned on before send(). */
  if (MHD_connection_set_nodelay_state_ (connection, true))
  {
    /* TCP_NODELAY has been set.
     * Use zero-send to push the data. */
    if (zero_send_ (connection))
    {
      ___mhd_log_f = fopen("/tmp/logs/2998.txt", "a");
      if (___mhd_log_f)
      {
        fprintf(___mhd_log_f, "RETURN: zero_send_ succeeded after set nodelay in no-cork branch (connection=%p)\n",
                (void *) connection);
        fclose(___mhd_log_f);
        ___mhd_log_f = NULL;
      }
      return; /* The data has been pushed by zero-send. */
    }
  }

  /* Failed to push the data. */
#endif /* ! MHD_TCP_CORK_NOPUSH */
#ifdef HAVE_MESSAGES
  MHD_DLOG (connection->daemon,
            _ ("Failed to push the data from buffers to the network. "
               "Client may experience some delay "
               "(usually in range 200ms - 5 sec).\n"));
#endif /* HAVE_MESSAGES */

  ___mhd_log_f = fopen("/tmp/logs/2998.txt", "a");
  if (___mhd_log_f)
  {
    fprintf(___mhd_log_f, "RETURN: failed to push data by any method (connection=%p)\n", (void *) connection);
    fclose(___mhd_log_f);
    ___mhd_log_f = NULL;
  }
  return;
}
