static bool
process_interim_params (struct MHD_Daemon *d,
                        const struct sockaddr **ppsockaddr,
                        socklen_t *psockaddr_len,
                        struct MHD_InterimParams_ *params)
{
  /* Probe: log entry and key input state */
  {
    FILE *fp = fopen("/tmp/logs/3228.txt", "a");
    if (fp)
    {
      fprintf (fp, "ENTER process_interim_params: this=%p, d=%p, params=%p\n", (void *) &process_interim_params, (void *) d, (void *) params);
      fprintf (fp, "  params->fdset_size_set=%d, params->fdset_size=%d, params->listen_fd_set=%d, params->listen_fd=%lld\n",
               (int) params->fdset_size_set, (int) params->fdset_size, (int) params->listen_fd_set, (long long) params->listen_fd);
      fclose (fp);
    }
  }

  if (params->fdset_size_set)
  {
    if (0 >= params->fdset_size)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (d,
                _ ("MHD_OPTION_APP_FD_SETSIZE value (%d) is not positive.\n"),
                params->fdset_size);
#endif /* HAVE_MESSAGES */
      /* Log the negative/zero fdset_size rejection */
      {
        FILE *fp = fopen("/tmp/logs/3228.txt", "a");
        if (fp)
        {
          fprintf (fp, "BRANCH: fdset_size <= 0 (fdset_size=%d) -> return false\n", params->fdset_size);
          fclose (fp);
        }
      }
      return false;
    }
    if (MHD_D_IS_USING_THREADS_ (d))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (d,
                _ ("MHD_OPTION_APP_FD_SETSIZE is ignored for daemon started " \
                   "with MHD_USE_INTERNAL_POLLING_THREAD.\n"));
#endif /* HAVE_MESSAGES */
      /* Violation scenario probe: the no-op statement that was flagged as dead code. */
      {
        FILE *fp = fopen("/tmp/logs/3228.txt", "a");
        if (fp)
        {
          fprintf (fp, "BRANCH: MHD_D_IS_USING_THREADS_ taken (d=%p). About to execute (void)0; no-op. params->fdset_size=%d\n",
                   (void *) d, params->fdset_size);
          fclose (fp);
        }
      }
      (void) 0;
      /* Log after executing the no-op to demonstrate it was reached */
      {
        FILE *fp = fopen("/tmp/logs/3228.txt", "a");
        if (fp)
        {
          fprintf (fp, "AFTER NO-OP: (void)0; executed in MHD_D_IS_USING_THREADS_ branch\n");
          fclose (fp);
        }
      }
    }
    else if (MHD_D_IS_USING_POLL_ (d))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (d,
                _ ("MHD_OPTION_APP_FD_SETSIZE is ignored for daemon started " \
                   "with MHD_USE_POLL.\n"));
#endif /* HAVE_MESSAGES */
      /* Log the poll branch and its no-op */
      {
        FILE *fp = fopen("/tmp/logs/3228.txt", "a");
        if (fp)
        {
          fprintf (fp, "BRANCH: MHD_D_IS_USING_POLL_ taken (d=%p). About to execute (void)0; no-op. params->fdset_size=%d\n",
                   (void *) d, params->fdset_size);
          fclose (fp);
        }
      }
      (void) 0;
      {
        FILE *fp = fopen("/tmp/logs/3228.txt", "a");
        if (fp)
        {
          fprintf (fp, "AFTER NO-OP: (void)0; executed in MHD_D_IS_USING_POLL_ branch\n");
          fclose (fp);
        }
      }
    }
    else
    { /* The daemon without internal threads, external sockets polling */
#ifndef HAS_FD_SETSIZE_OVERRIDABLE
      if (((int) FD_SETSIZE) != params->fdset_size)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (d,
                  _ ("MHD_OPTION_APP_FD_SETSIZE value (%d) does not match " \
                     "the platform FD_SETSIZE value (%d) and this platform " \
                     "does not support overriding of FD_SETSIZE.\n"),
                  params->fdset_size, (int) FD_SETSIZE);
#endif /* HAVE_MESSAGES */
        /* Log the mismatch and rejection */
        {
          FILE *fp = fopen("/tmp/logs/3228.txt", "a");
          if (fp)
          {
            fprintf (fp, "BRANCH: FD_SETSIZE mismatch: params->fdset_size=%d, FD_SETSIZE=%d -> return false\n",
                     params->fdset_size, (int) FD_SETSIZE);
            fclose (fp);
          }
        }
        return false;
      }
#else  /* HAS_FD_SETSIZE_OVERRIDABLE */
      d->fdset_size = params->fdset_size;
      d->fdset_size_set_by_app = true;
      {
        FILE *fp = fopen("/tmp/logs/3228.txt", "a");
        if (fp)
        {
          fprintf (fp, "ACTION: fdset_size overridden: d->fdset_size=%d, fdset_size_set_by_app=1\n", d->fdset_size);
          fclose (fp);
        }
      }
#endif /* HAS_FD_SETSIZE_OVERRIDABLE */
    }
  }

  if (params->listen_fd_set)
  {
    if (MHD_INVALID_SOCKET == params->listen_fd)
    {
      (void) 0; /* Use MHD-created socket */
      /* Log that the sentinel no-op path for MHD-created socket was taken */
      {
        FILE *fp = fopen("/tmp/logs/3228.txt", "a");
        if (fp)
        {
          fprintf (fp, "BRANCH: listen_fd_set && listen_fd == MHD_INVALID_SOCKET -> use MHD-created socket (no-op executed)\n");
          fclose (fp);
        }
      }
    }
#ifdef HAS_SIGNED_SOCKET
    else if (0 > params->listen_fd)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (d,
                _ ("The value provided for MHD_OPTION_LISTEN_SOCKET " \
                   "is invalid.\n"));
#endif /* HAVE_MESSAGES */
      /* Log invalid listen_fd */
      {
        FILE *fp = fopen("/tmp/logs/3228.txt", "a");
        if (fp)
        {
          fprintf (fp, "BRANCH: listen_fd negative (invalid) -> return false (listen_fd=%lld)\n", (long long) params->listen_fd);
          fclose (fp);
        }
      }
      return false;
    }
#endif /* HAS_SIGNED_SOCKET */
    else if (0 != (d->options & MHD_USE_NO_LISTEN_SOCKET))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (d,
                _ ("MHD_OPTION_LISTEN_SOCKET specified for daemon "
                   "with MHD_USE_NO_LISTEN_SOCKET flag set.\n"));
#endif /* HAVE_MESSAGES */
      {
        FILE *fp = fopen("/tmp/logs/3228.txt", "a");
        if (fp)
        {
          fprintf (fp, "BRANCH: listen_fd specified but MHD_USE_NO_LISTEN_SOCKET flag set -> close socket and return false (listen_fd=%lld)\n", (long long) params->listen_fd);
          fclose (fp);
        }
      }
      (void) MHD_socket_close_ (params->listen_fd);
      return false;
    }
    else
    {
      d->listen_fd = params->listen_fd;
      d->listen_is_unix = _MHD_UNKNOWN;
#ifdef MHD_USE_GETSOCKNAME
      d->port = 0;  /* Force use of autodetection */
#endif /* MHD_USE_GETSOCKNAME */
      {
        FILE *fp = fopen("/tmp/logs/3228.txt", "a");
        if (fp)
        {
          fprintf (fp, "ACTION: adopt provided listen_fd=%lld, listen_is_unix=%d\n", (long long) d->listen_fd, (int) d->listen_is_unix);
          fclose (fp);
        }
      }
    }
  }

  mhd_assert (! params->server_addr_len_set || params->pserver_addr_set);
  if (params->pserver_addr_set)
  {
    if (NULL == params->pserver_addr)
    {
      /* The size must be zero if set */
      if (params->server_addr_len_set && (0 != params->server_addr_len))
        return false;
      /* Ignore parameter if it is NULL */
      {
        FILE *fp = fopen("/tmp/logs/3228.txt", "a");
        if (fp)
        {
          fprintf (fp, "BRANCH: pserver_addr_set but pserver_addr == NULL -> ignored (server_addr_len_set=%d, server_addr_len=%u)\n",
                   (int) params->server_addr_len_set, (unsigned) params->server_addr_len);
          fclose (fp);
        }
      }
    }
    else if (MHD_INVALID_SOCKET != d->listen_fd)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (d,
                _ ("MHD_OPTION_LISTEN_SOCKET cannot be used together with " \
                   "MHD_OPTION_SOCK_ADDR_LEN or MHD_OPTION_SOCK_ADDR.\n"));
#endif /* HAVE_MESSAGES */
      {
        FILE *fp = fopen("/tmp/logs/3228.txt", "a");
        if (fp)
        {
          fprintf (fp, "BRANCH: pserver_addr provided but listen_fd already set -> return false\n");
          fclose (fp);
        }
      }
      return false;
    }
    else if (0 != (d->options & MHD_USE_NO_LISTEN_SOCKET))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (d,
                _ ("MHD_OPTION_SOCK_ADDR_LEN or MHD_OPTION_SOCK_ADDR " \
                   "specified for daemon with MHD_USE_NO_LISTEN_SOCKET " \
                   "flag set.\n"));
#endif /* HAVE_MESSAGES */
      if (MHD_INVALID_SOCKET != d->listen_fd)
      {
        (void) MHD_socket_close_ (params->listen_fd);
        params->listen_fd = MHD_INVALID_SOCKET;
      }
      {
        FILE *fp = fopen("/tmp/logs/3228.txt", "a");
        if (fp)
        {
          fprintf (fp, "BRANCH: pserver_addr specified but MHD_USE_NO_LISTEN_SOCKET set -> close and return false\n");
          fclose (fp);
        }
      }
      return false;
    }
    else
    {
      *ppsockaddr = params->pserver_addr;
      if (params->server_addr_len_set)
      {
        /* The size must be non-zero if set */
        if (0 == params->server_addr_len)
          return false;
        *psockaddr_len = params->server_addr_len;
      }
      else
        *psockaddr_len = 0;
      {
        FILE *fp = fopen("/tmp/logs/3228.txt", "a");
        if (fp)
        {
          fprintf (fp, "ACTION: adopt provided server_addr -> ppsockaddr=%p, psockaddr_len=%u\n", (void *) *ppsockaddr, (unsigned) *psockaddr_len);
          fclose (fp);
        }
      }
    }
  }

  /* Exit log */
  {
    FILE *fp = fopen("/tmp/logs/3228.txt", "a");
    if (fp)
    {
      fprintf (fp, "EXIT process_interim_params -> return true\n\n");
      fclose (fp);
    }
  }
  return true;
}
