static bool
process_interim_params (struct MHD_Daemon *d,
                        const struct sockaddr **ppsockaddr,
                        socklen_t *psockaddr_len,
                        struct MHD_InterimParams_ *params)
{
  FILE *logf = fopen("/tmp/logs/3225.txt", "a");
  if (logf)
  {
    fprintf(logf, "ENTER process_interim_params: d=%p, ppsockaddr=%p, psockaddr_len=%p, params=%p\n",
            (void *) d, (void *) ppsockaddr, (void *) psockaddr_len, (void *) params);
    if (params)
    {
      fprintf(logf, "  params: fdset_size_set=%d, fdset_size=%d, listen_fd_set=%d, listen_fd=%ld, pserver_addr_set=%d, pserver_addr=%p, server_addr_len_set=%d, server_addr_len=%u\n",
              (int) params->fdset_size_set,
              (int) params->fdset_size,
              (int) params->listen_fd_set,
              (long) params->listen_fd,
              (int) params->pserver_addr_set,
              (void *) params->pserver_addr,
              (int) params->server_addr_len_set,
              (unsigned) params->server_addr_len);
    }
    if (d)
      fprintf(logf, "  daemon: options=0x%lx, listen_fd=%ld\n",
              (unsigned long) d->options, (long) d->listen_fd);
    fflush(logf);
  }

  if (params->fdset_size_set)
  {
    if (0 >= params->fdset_size)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (d,
                _ ("MHD_OPTION_APP_FD_SETSIZE value (%d) is not positive.\n"),
                params->fdset_size);
#endif /* HAVE_MESSAGES */
      if (logf) { fprintf(logf, "BRANCH: fdset_size_set && fdset_size <= 0 => return false\n"); fclose(logf); }
      return false;
    }
    if (MHD_D_IS_USING_THREADS_ (d))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (d,
                _ ("MHD_OPTION_APP_FD_SETSIZE is ignored for daemon started " \
                   "with MHD_USE_INTERNAL_POLLING_THREAD.\n"));
#endif /* HAVE_MESSAGES */
      (void) 0;
    }
    else if (MHD_D_IS_USING_POLL_ (d))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (d,
                _ ("MHD_OPTION_APP_FD_SETSIZE is ignored for daemon started " \
                   "with MHD_USE_POLL.\n"));
#endif /* HAVE_MESSAGES */
      (void) 0;
    }
    else
    { /* The daemon without internal threads, external sockets polling */
#ifndef HAS_FD_SETSIZE_OVERRIDABLE
      if (((int) FD_SETSIZE) != params->fdset_size)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (d,
                  _ ("MHD_OPTION_APP_FD_SETSIZE value (%d) does not match " \
                     "the platform FD_SETSIZE value (%d) and this platform " \
                     "does not support overriding of FD_SETSIZE.\n"),
                  params->fdset_size, (int) FD_SETSIZE);
#endif /* HAVE_MESSAGES */
        if (logf) { fprintf(logf, "BRANCH: fdset_size mismatch with FD_SETSIZE => return false (fdset_size=%d, FD_SETSIZE=%d)\n", params->fdset_size, (int) FD_SETSIZE); fclose(logf); }
        return false;
      }
#else  /* HAS_FD_SETSIZE_OVERRIDABLE */
      d->fdset_size = params->fdset_size;
      d->fdset_size_set_by_app = true;
#endif /* HAS_FD_SETSIZE_OVERRIDABLE */
    }
  }

  if (params->listen_fd_set)
  {
    if (MHD_INVALID_SOCKET == params->listen_fd)
    {
      (void) 0; /* Use MHD-created socket */
      if (logf) { fprintf(logf, "BRANCH: listen_fd_set && listen_fd == MHD_INVALID_SOCKET => use MHD-created socket\n"); fflush(logf); }
    }
#ifdef HAS_SIGNED_SOCKET
    else if (0 > params->listen_fd)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (d,
                _ ("The value provided for MHD_OPTION_LISTEN_SOCKET " \
                   "is invalid.\n"));
#endif /* HAVE_MESSAGES */
      if (logf) { fprintf(logf, "BRANCH: listen_fd_set && listen_fd < 0 => return false (invalid listen_fd=%ld)\n", (long) params->listen_fd); fclose(logf); }
      return false;
    }
#endif /* HAS_SIGNED_SOCKET */
    else if (0 != (d->options & MHD_USE_NO_LISTEN_SOCKET))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (d,
                _ ("MHD_OPTION_LISTEN_SOCKET specified for daemon "
                   "with MHD_USE_NO_LISTEN_SOCKET flag set.\n"));
#endif /* HAVE_MESSAGES */
      (void) MHD_socket_close_ (params->listen_fd);
      if (logf) { fprintf(logf, "BRANCH: listen_fd_set but daemon uses NO_LISTEN_SOCKET => closed provided socket and return false (listen_fd=%ld)\n", (long) params->listen_fd); fclose(logf); }
      return false;
    }
    else
    {
      d->listen_fd = params->listen_fd;
      d->listen_is_unix = _MHD_UNKNOWN;
#ifdef MHD_USE_GETSOCKNAME
      d->port = 0;  /* Force use of autodetection */
#endif /* MHD_USE_GETSOCKNAME */
      if (logf) { fprintf(logf, "BRANCH: listen_fd_set accepted => daemon.listen_fd set to %ld\n", (long) d->listen_fd); fflush(logf); }
    }
  }

  mhd_assert (! params->server_addr_len_set || params->pserver_addr_set);
  if (params->pserver_addr_set)
  {
    if (NULL == params->pserver_addr)
    {
      /* The size must be zero if set */
      if (params->server_addr_len_set && (0 != params->server_addr_len))
      {
        if (logf) { fprintf(logf, "BRANCH: pserver_addr_set && pserver_addr == NULL && server_addr_len_set && server_addr_len != 0 => return false (server_addr_len=%u)\n", (unsigned) params->server_addr_len); fclose(logf); }
        return false;
      }
      /* Ignore parameter if it is NULL */
      if (logf) { fprintf(logf, "BRANCH: pserver_addr_set && pserver_addr == NULL => ignoring parameter (treated as not provided)\n"); fflush(logf); }
    }
    else if (MHD_INVALID_SOCKET != d->listen_fd)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (d,
                _ ("MHD_OPTION_LISTEN_SOCKET cannot be used together with " \
                   "MHD_OPTION_SOCK_ADDR_LEN or MHD_OPTION_SOCK_ADDR.\n"));
#endif /* HAVE_MESSAGES */
      if (logf) { fprintf(logf, "BRANCH: pserver_addr_set && pserver_addr != NULL && daemon.listen_fd != MHD_INVALID_SOCKET => return false (daemon.listen_fd=%ld)\n", (long) d->listen_fd); fclose(logf); }
      return false;
    }
    else if (0 != (d->options & MHD_USE_NO_LISTEN_SOCKET))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (d,
                _ ("MHD_OPTION_SOCK_ADDR_LEN or MHD_OPTION_SOCK_ADDR " \
                   "specified for daemon with MHD_USE_NO_LISTEN_SOCKET " \
                   "flag set.\n"));
#endif /* HAVE_MESSAGES */
      if (MHD_INVALID_SOCKET != d->listen_fd)
      {
        (void) MHD_socket_close_ (params->listen_fd);
        params->listen_fd = MHD_INVALID_SOCKET;
      }
      if (logf) { fprintf(logf, "BRANCH: pserver_addr_set && daemon uses NO_LISTEN_SOCKET => closed listen_fd if any and return false\n"); fclose(logf); }
      return false;
    }
    else
    {
      if (logf) { fprintf(logf, "BRANCH: pserver_addr_set && pserver_addr != NULL => assigning *ppsockaddr = params->pserver_addr (pserver_addr=%p)\n", (void *) params->pserver_addr); fflush(logf); }
      *ppsockaddr = params->pserver_addr;
      if (params->server_addr_len_set)
      {
        /* The size must be non-zero if set */
        if (0 == params->server_addr_len)
        {
          if (logf) { fprintf(logf, "BRANCH: server_addr_len_set but server_addr_len == 0 => return false\n"); fclose(logf); }
          return false;
        }
        *psockaddr_len = params->server_addr_len;
        if (logf) { fprintf(logf, "ASSIGN: *psockaddr_len = %u\n", (unsigned) params->server_addr_len); fflush(logf); }
      }
      else
      {
        *psockaddr_len = 0;
        if (logf) { fprintf(logf, "ASSIGN: server_addr_len not set => *psockaddr_len = 0\n"); fflush(logf); }
      }
    }
  }
  if (logf) { fprintf(logf, "EXIT process_interim_params => return true\n"); fclose(logf); }
  return true;
}
