static int
process_multipart_headers (struct MHD_PostProcessor *pp,
                           size_t *ioffptr,
                           enum PP_State next_state)
{
  char *buf = (char *) &pp[1];
  size_t newline;

  /* Log entry and initial state */
  {
    FILE *f = fopen("/tmp/logs/1423.txt", "a");
    if (f)
    {
      fprintf(f, "ENTER process_multipart_headers: pp=%p, buf=%p, buffer_pos=%zu, buffer_size=%zu, ioff=%zu\n",
              (void *)pp, (void *)buf, pp->buffer_pos, pp->buffer_size, (size_t)(*ioffptr));
      fclose(f);
    }
  }

  newline = 0;
  while ( (newline < pp->buffer_pos) &&
          (buf[newline] != '\r') &&
          (buf[newline] != '\n') )
    newline++;

  /* Log newline discovery */
  {
    FILE *f = fopen("/tmp/logs/1423.txt", "a");
    if (f)
    {
      fprintf(f, "Computed newline=%zu (buffer_pos=%zu)\n", newline, pp->buffer_pos);
      /* Safely log the header bytes up to newline (do not read beyond newline) */
      if (newline > 0)
      {
        /* limit output length to avoid huge logs */
        size_t print_len = newline > 200 ? 200 : newline;
        fprintf(f, "Header prefix (first %zu of %zu bytes): '%.*s'\n", print_len, newline, (int)print_len, buf);
        if (newline > 200)
          fprintf(f, "(header truncated in log)\n");
      }
      fclose(f);
    }
  }

  if (newline == pp->buffer_size)
  {
    FILE *f = fopen("/tmp/logs/1423.txt", "a");
    if (f)
    {
      fprintf(f, "ERROR: newline == buffer_size (%zu) -> PP_Error, returning MHD_NO\n", pp->buffer_size);
      fclose(f);
    }
    pp->state = PP_Error;
    return MHD_NO;              /* out of memory */
  }
  if (newline == pp->buffer_pos)
  {
    FILE *f = fopen("/tmp/logs/1423.txt", "a");
    if (f)
    {
      fprintf(f, "Need more data: newline == buffer_pos (%zu), returning MHD_NO\n", pp->buffer_pos);
      fclose(f);
    }
    return MHD_NO;              /* will need more data */
  }
  if (0 == newline)
  {
    /* empty line - end of headers */
    FILE *f = fopen("/tmp/logs/1423.txt", "a");
    if (f)
    {
      fprintf(f, "Empty line: end of headers. Setting skip_rn=RN_Full, state=next_state\n");
      fclose(f);
    }
    pp->skip_rn = RN_Full;
    pp->state = next_state;
    return MHD_YES;
  }
  /* got an actual header */
  if (buf[newline] == '\r')
    pp->skip_rn = RN_OptN;
  buf[newline] = '\0';

  /* Log header type attempts and potential risky conditions */
  {
    FILE *f = fopen("/tmp/logs/1423.txt", "a");
    if (f)
    {
      size_t cd_len = MHD_STATICSTR_LEN_("Content-disposition: ");
      size_t ct_len = MHD_STATICSTR_LEN_("Content-type: ");
      size_t cte_len = MHD_STATICSTR_LEN_("Content-Transfer-Encoding: ");

      fprintf(f, "Checking header: header_len(newline)=%zu\n", newline);
      fprintf(f, "Prefix lengths: Content-disposition=%zu, Content-type=%zu, Content-Transfer-Encoding=%zu\n",
              cd_len, ct_len, cte_len);

      if (pp->buffer_pos < cd_len)
        fprintf(f, "WARNING: buffer_pos (%zu) < Content-disposition prefix length (%zu) -> potential OOB when comparing\n",
                pp->buffer_pos, cd_len);
      if (pp->buffer_size < cd_len)
        fprintf(f, "WARNING: buffer_size (%zu) < Content-disposition prefix length (%zu) -> potential OOB when comparing\n",
                pp->buffer_size, cd_len);

      if (pp->buffer_pos < ct_len)
        fprintf(f, "WARNING: buffer_pos (%zu) < Content-type prefix length (%zu) -> potential OOB in try_match_header\n",
                pp->buffer_pos, ct_len);
      if (pp->buffer_size < ct_len)
        fprintf(f, "WARNING: buffer_size (%zu) < Content-type prefix length (%zu) -> potential OOB in try_match_header\n",
                pp->buffer_size, ct_len);

      if (pp->buffer_pos < cte_len)
        fprintf(f, "WARNING: buffer_pos (%zu) < Content-Transfer-Encoding prefix length (%zu) -> potential OOB in try_match_header\n",
                pp->buffer_pos, cte_len);
      if (pp->buffer_size < cte_len)
        fprintf(f, "WARNING: buffer_size (%zu) < Content-Transfer-Encoding prefix length (%zu) -> potential OOB in try_match_header\n",
                pp->buffer_size, cte_len);

      fprintf(f, "Header string (null-terminated): '%s'\n", buf);
      fclose(f);
    }
  }

  if (MHD_str_equal_caseless_n_ ("Content-disposition: ",
                                 buf,
                                 MHD_STATICSTR_LEN_ ("Content-disposition: ")))
  {
    FILE *f = fopen("/tmp/logs/1423.txt", "a");
    if (f)
    {
      fprintf(f, "Matched Content-disposition branch. Calling try_get_value for name and filename.\n");
      fclose(f);
    }
    try_get_value (&buf[MHD_STATICSTR_LEN_ ("Content-disposition: ")],
                   "name",
                   &pp->content_name);
    try_get_value (&buf[MHD_STATICSTR_LEN_ ("Content-disposition: ")],
                   "filename",
                   &pp->content_filename);
  }
  else
  {
    FILE *f = fopen("/tmp/logs/1423.txt", "a");
    if (f)
    {
      fprintf(f, "Entering else branch: will call try_match_header for Content-type and Content-Transfer-Encoding.\n");
      fclose(f);
    }

    try_match_header ("Content-type: ",
                      MHD_STATICSTR_LEN_ ("Content-type: "),
                      buf,
                      &pp->content_type);

    /* Log immediately after the first try_match_header call to capture any state changes */
    {
      FILE *f = fopen("/tmp/logs/1423.txt", "a");
      if (f)
      {
        fprintf(f, "After try_match_header(Content-type). buffer_pos=%zu, buffer_size=%zu, header='%s'\n",
                pp->buffer_pos, pp->buffer_size, buf);
        fclose(f);
      }
    }

    try_match_header ("Content-Transfer-Encoding: ",
                      MHD_STATICSTR_LEN_ ("Content-Transfer-Encoding: "),
                      buf,
                      &pp->content_transfer_encoding);
  }

  (*ioffptr) += newline + 1;

  {
    FILE *f = fopen("/tmp/logs/1423.txt", "a");
    if (f)
    {
      fprintf(f, "Exiting process_multipart_headers: new ioff=%zu, returning MHD_YES\n", (size_t)(*ioffptr));
      fclose(f);
    }
  }

  return MHD_YES;
}
