static int
process_multipart_headers (struct MHD_PostProcessor *pp,
                           size_t *ioffptr,
                           enum PP_State next_state)
{
  char *buf = (char *) &pp[1];
  size_t newline;

  /* Open log file for appending diagnostic information about control flow
     and state needed to reproduce potential out-of-bounds string reads. */
  {
    FILE *log = fopen("/tmp/logs/1423.txt", "a");
    if (log)
    {
      fprintf(log, "ENTER process_multipart_headers: pp=%p, ioffptr=%p, next_state=%d\n",
              (void *)pp, (void *)ioffptr, (int)next_state);
      fprintf(log, " initial: buffer_pos=%zu, buffer_size=%zu, buf=%p\n",
              pp->buffer_pos, pp->buffer_size, (void *)buf);
      fclose(log);
    }
  }

  newline = 0;
  while ( (newline < pp->buffer_pos) &&
          (buf[newline] != '\r') &&
          (buf[newline] != '\n') )
    newline++;

  /* Log newline discovery */
  {
    FILE *log = fopen("/tmp/logs/1423.txt", "a");
    if (log)
    {
      fprintf(log, "computed newline=%zu (buffer_pos=%zu)\n",
              newline, pp->buffer_pos);
      fclose(log);
    }
  }

  if (newline == pp->buffer_size)
  {
    /* Log the out-of-memory condition */
    FILE *log = fopen("/tmp/logs/1423.txt", "a");
    if (log)
    {
      fprintf(log, "CONDITION: newline == buffer_size -> PP_Error. newline=%zu, buffer_size=%zu\n",
              newline, pp->buffer_size);
      fclose(log);
    }

    pp->state = PP_Error;
    return MHD_NO;              /* out of memory */
  }
  if (newline == pp->buffer_pos)
  {
    FILE *log = fopen("/tmp/logs/1423.txt", "a");
    if (log)
    {
      fprintf(log, "NEED MORE DATA: newline == buffer_pos (%zu) -> returning MHD_NO\n",
              newline);
      fclose(log);
    }
    return MHD_NO;              /* will need more data */
  }
  if (0 == newline)
  {
    /* empty line - end of headers */
    FILE *log = fopen("/tmp/logs/1423.txt", "a");
    if (log)
    {
      fprintf(log, "EMPTY LINE: end of headers -> set skip_rn=RN_Full, state=%d\n",
              (int) next_state);
      fclose(log);
    }

    pp->skip_rn = RN_Full;
    pp->state = next_state;
    return MHD_YES;
  }
  /* got an actual header */
  if (buf[newline] == '\r')
    pp->skip_rn = RN_OptN;
  buf[newline] = '\0';

  /* Prepare diagnostic information for header comparison. */
  {
    size_t cd_len = MHD_STATICSTR_LEN_("Content-disposition: ");
    FILE *log = fopen("/tmp/logs/1423.txt", "a");
    if (log)
    {
      fprintf(log, "HEADER: newline=%zu, buf_addr=%p, cd_len=%zu, buffer_size=%zu\n",
              newline, (void *)buf, cd_len, pp->buffer_size);

      /* Log the header content up to newline (safe). */
      fprintf(log, "header (up to newline): '%.*s'\n", (int)newline, buf);

      /* Indicate whether it's safe to inspect buf + cd_len. */
      if (cd_len < pp->buffer_size)
        fprintf(log, "safe to inspect buf+cd_len (cd_len < buffer_size): %p\n",
                (void *)&buf[cd_len]);
      else
        fprintf(log, "NOT SAFE to inspect buf+cd_len: cd_len=%zu >= buffer_size=%zu\n",
                cd_len, pp->buffer_size);

      fclose(log);
    }

    /* Log before calling the comparison routine. This will capture the
       parameters that may lead to a read beyond the buffer when the
       static string length exceeds the post-processor buffer size. */
    {
      FILE *log2 = fopen("/tmp/logs/1423.txt", "a");
      if (log2)
      {
        fprintf(log2, "CALL: MHD_str_equal_caseless_n_(\"Content-disposition: \", buf, %zu)\n",
                cd_len);
        fclose(log2);
      }
    }

    if (MHD_str_equal_caseless_n_ ("Content-disposition: ",
                                   buf,
                                   cd_len))
    {
      FILE *log3 = fopen("/tmp/logs/1423.txt", "a");
      if (log3)
      {
        fprintf(log3, "MATCH: Content-disposition matched. Calling try_get_value for name and filename.\n");
        fclose(log3);
      }

      try_get_value (&buf[MHD_STATICSTR_LEN_("Content-disposition: ")],
                     "name",
                     &pp->content_name);
      try_get_value (&buf[MHD_STATICSTR_LEN_("Content-disposition: ")],
                     "filename",
                     &pp->content_filename);

      FILE *log4 = fopen("/tmp/logs/1423.txt", "a");
      if (log4)
      {
        fprintf(log4, "AFTER try_get_value: content_name=%p, content_filename=%p\n",
                (void *)pp->content_name, (void *)pp->content_filename);
        fclose(log4);
      }
    }
    else
    {
      FILE *log5 = fopen("/tmp/logs/1423.txt", "a");
      if (log5)
      {
        fprintf(log5, "NO MATCH: calling try_match_header for Content-type and Content-Transfer-Encoding.\n");
        fclose(log5);
      }

      try_match_header ("Content-type: ",
                        MHD_STATICSTR_LEN_("Content-type: "),
                        buf,
                        &pp->content_type);
      try_match_header ("Content-Transfer-Encoding: ",
                        MHD_STATICSTR_LEN_("Content-Transfer-Encoding: "),
                        buf,
                        &pp->content_transfer_encoding);

      FILE *log6 = fopen("/tmp/logs/1423.txt", "a");
      if (log6)
      {
        fprintf(log6, "AFTER try_match_header: content_type=%p, content_transfer_encoding=%p\n",
                (void *)pp->content_type, (void *)pp->content_transfer_encoding);
        fclose(log6);
      }
    }
  }

  (*ioffptr) += newline + 1;

  {
    FILE *log = fopen("/tmp/logs/1423.txt", "a");
    if (log)
    {
      fprintf(log, "EXIT process_multipart_headers: new ioff=%zu, returning MHD_YES\n", *ioffptr);
      fclose(log);
    }
  }

  return MHD_YES;
}
