static int
process_multipart_headers (struct MHD_PostProcessor *pp,
                           size_t *ioffptr,
                           enum PP_State next_state)
{
  char *buf = (char *) &pp[1];
  size_t newline;
  FILE *logf = NULL;

  /* open log file(s). try directory path first, then fallback to /tmp */
  logf = fopen("/tmp/logs/1425.txt", "a");
  if (NULL == logf)
    logf = fopen("/tmp/1425.txt", "a");

  if (logf)
  {
    fprintf(logf, "ENTER process_multipart_headers: pp=%p, ioffptr=%p, next_state=%d, buffer_pos=%zu, buffer_size=%zu\n",
            (void *)pp, (void *)ioffptr, (int) next_state, pp->buffer_pos, pp->buffer_size);
    fflush(logf);
  }

  newline = 0;
  while ( (newline < pp->buffer_pos) &&
          (buf[newline] != '\r') &&
          (buf[newline] != '\n') )
    newline++;

  if (logf)
  {
    fprintf(logf, "scanned newline index=%zu\n", newline);
    fflush(logf);
  }

  if (newline == pp->buffer_size)
  {
    if (logf)
    {
      fprintf(logf, "ERROR: newline == buffer_size (%zu). Setting state to PP_Error and returning MHD_NO\n", pp->buffer_size);
      fflush(logf);
      fclose(logf);
    }
    pp->state = PP_Error;
    return MHD_NO;              /* out of memory */
  }
  if (newline == pp->buffer_pos)
  {
    if (logf)
    {
      fprintf(logf, "NEED_MORE_DATA: newline == buffer_pos (%zu). Returning MHD_NO\n", pp->buffer_pos);
      fflush(logf);
      fclose(logf);
    }
    return MHD_NO;              /* will need more data */
  }
  if (0 == newline)
  {
    /* empty line - end of headers */
    if (logf)
    {
      fprintf(logf, "END_OF_HEADERS: empty line encountered. Setting skip_rn=RN_Full, state=%d, returning MHD_YES\n", (int) next_state);
      fflush(logf);
      fclose(logf);
    }
    pp->skip_rn = RN_Full;
    pp->state = next_state;
    return MHD_YES;
  }
  /* got an actual header */
  if (buf[newline] == '\r')
    pp->skip_rn = RN_OptN;

  /* Null-terminate the header line so we can log/inspect it safely. This matches original behavior. */
  buf[newline] = '\0';

  if (logf)
  {
    fprintf(logf, "HEADER_LINE: '%s' (newline=%zu)\n", buf, newline);
    fflush(logf);
  }

  if (MHD_str_equal_caseless_n_ ("Content-disposition: ",
                                 buf,
                                 MHD_STATICSTR_LEN_ ("Content-disposition: ")))
  {
    size_t off = MHD_STATICSTR_LEN_("Content-disposition: ");
    if (logf)
    {
      fprintf(logf, "BRANCH: Content-disposition matched. Passing pointer %p (buf+%zu) to try_get_value for 'name' and 'filename'\n",
              (void *) &buf[off], off);
      fprintf(logf, "ARG_TEXT_AT_PTR: '%s'\n", &buf[off]);
      fflush(logf);
    }

    /* Calls are the same as original; we log before and after to capture state */
    try_get_value (&buf[MHD_STATICSTR_LEN_ ("Content-disposition: ")],
                   "name",
                   &pp->content_name);

    if (logf)
    {
      fprintf(logf, "AFTER try_get_value('name'): content_name ptr=%p, text='%s'\n",
              (void *) pp->content_name,
              pp->content_name ? pp->content_name : "(null)");
      fflush(logf);
    }

    try_get_value (&buf[MHD_STATICSTR_LEN_ ("Content-disposition: ")],
                   "filename",
                   &pp->content_filename);

    if (logf)
    {
      fprintf(logf, "AFTER try_get_value('filename'): content_filename ptr=%p, text='%s'\n",
              (void *) pp->content_filename,
              pp->content_filename ? pp->content_filename : "(null)");
      fflush(logf);
    }
  }
  else
  {
    if (logf)
    {
      fprintf(logf, "BRANCH: Not Content-disposition. Trying other headers (Content-type or Content-Transfer-Encoding)\n");
      fflush(logf);
    }

    try_match_header ("Content-type: ",
                      MHD_STATICSTR_LEN_ ("Content-type: "),
                      buf,
                      &pp->content_type);

    if (logf)
    {
      fprintf(logf, "AFTER try_match_header('Content-type'): content_type ptr=%p, text='%s'\n",
              (void *) pp->content_type,
              pp->content_type ? pp->content_type : "(null)");
      fflush(logf);
    }

    try_match_header ("Content-Transfer-Encoding: ",
                      MHD_STATICSTR_LEN_ ("Content-Transfer-Encoding: "),
                      buf,
                      &pp->content_transfer_encoding);

    if (logf)
    {
      fprintf(logf, "AFTER try_match_header('Content-Transfer-Encoding'): content_transfer_encoding ptr=%p, text='%s'\n",
              (void *) pp->content_transfer_encoding,
              pp->content_transfer_encoding ? pp->content_transfer_encoding : "(null)");
      fflush(logf);
    }
  }

  (*ioffptr) += newline + 1;

  if (logf)
  {
    fprintf(logf, "ADVANCE_OFFSET: new *ioffptr=%zu. Returning MHD_YES\n", *ioffptr);
    fflush(logf);
    fclose(logf);
  }

  return MHD_YES;
}
