static bool
process_request_target (struct MHD_Connection *c)
{
#ifdef _DEBUG
  size_t params_len;
#endif /* _DEBUG */
  /* Internal logging-only variable to record parameter length when _DEBUG is not set. */
  size_t __log_params_len = 0;
  FILE * __log_f = fopen("/tmp/logs/58.txt", "a");

  if (__log_f)
  {
    fprintf(__log_f, "ENTER process_request_target: c=%p state=%d url=%p url_len=%zu rq_tgt=%p rq_tgt_qmark=%p req_target_len=%zu daemon=%p daemon->uri_log_callback=%p\n",
            (void *)c,
            (int) c->state,
            (void *) c->rq.url,
            (size_t) c->rq.url_len,
            (void *) c->rq.hdrs.rq_line.rq_tgt,
            (void *) c->rq.hdrs.rq_line.rq_tgt_qmark,
            (size_t) c->rq.req_target_len,
            (void *) c->daemon,
            (void *) (c->daemon ? c->daemon->uri_log_callback : NULL));
    fclose(__log_f);
  }

  mhd_assert (MHD_CONNECTION_REQ_LINE_RECEIVING == c->state);
  mhd_assert (NULL == c->rq.url);
  mhd_assert (0 == c->rq.url_len);
  mhd_assert (NULL != c->rq.hdrs.rq_line.rq_tgt);
  mhd_assert ((NULL == c->rq.hdrs.rq_line.rq_tgt_qmark) || \
              (c->rq.hdrs.rq_line.rq_tgt <= c->rq.hdrs.rq_line.rq_tgt_qmark));
  mhd_assert ((NULL == c->rq.hdrs.rq_line.rq_tgt_qmark) || \
              (c->rq.req_target_len > \
               (size_t) (c->rq.hdrs.rq_line.rq_tgt_qmark \
                         - c->rq.hdrs.rq_line.rq_tgt)));

  /* Log the evaluated conditions and relevant state immediately after the asserts. */
  __log_f = fopen("/tmp/logs/58.txt", "a");
  if (__log_f)
  {
    fprintf(__log_f, "AFTER ASSERTS: c=%p state=%d url=%p url_len=%zu rq_tgt=%p rq_tgt_qmark=%p req_target_len=%zu\n",
            (void *)c,
            (int) c->state,
            (void *) c->rq.url,
            (size_t) c->rq.url_len,
            (void *) c->rq.hdrs.rq_line.rq_tgt,
            (void *) c->rq.hdrs.rq_line.rq_tgt_qmark,
            (size_t) c->rq.req_target_len);
    fclose(__log_f);
  }

  /* Log callback before the request-target is modified/decoded */
  if (NULL != c->daemon->uri_log_callback)
  {
    if ((__log_f = fopen("/tmp/logs/58.txt", "a")) != NULL)
    {
      fprintf(__log_f, "URI_LOG_CALLBACK present: invoking for c=%p\n", (void *)c);
      fclose(__log_f);
    }

    c->rq.client_aware = true;
    c->rq.client_context =
      c->daemon->uri_log_callback (c->daemon->uri_log_callback_cls,
                                   c->rq.hdrs.rq_line.rq_tgt,
                                   c);

    if ((__log_f = fopen("/tmp/logs/58.txt", "a")) != NULL)
    {
      fprintf(__log_f, "URI_LOG_CALLBACK returned: client_context=%p client_aware=%d\n",
              (void *) c->rq.client_context,
              (int) c->rq.client_aware);
      fclose(__log_f);
    }
  }

  if (NULL != c->rq.hdrs.rq_line.rq_tgt_qmark)
  {
#ifdef _DEBUG
    params_len =
      c->rq.req_target_len
      - (size_t) (c->rq.hdrs.rq_line.rq_tgt_qmark - c->rq.hdrs.rq_line.rq_tgt);
    __log_params_len = params_len;
#endif /* _DEBUG */
    c->rq.hdrs.rq_line.rq_tgt_qmark[0] = 0; /* Replace '?' with zero termination */
    if (MHD_NO == MHD_parse_arguments_ (c,
                                        MHD_GET_ARGUMENT_KIND,
                                        c->rq.hdrs.rq_line.rq_tgt_qmark + 1,
                                        &connection_add_header,
                                        c))
    {
      mhd_assert (MHD_CONNECTION_REQ_LINE_RECEIVING != c->state);

      if ((__log_f = fopen("/tmp/logs/58.txt", "a")) != NULL)
      {
        fprintf(__log_f, "MHD_parse_arguments_ failed for c=%p, returning false\n", (void *)c);
        fclose(__log_f);
      }

      return false;
    }

    if ((__log_f = fopen("/tmp/logs/58.txt", "a")) != NULL)
    {
      fprintf(__log_f, "Parsed arguments: c=%p params_len=%zu\n",
              (void *)c,
#ifdef _DEBUG
              params_len
#else
              __log_params_len
#endif
              );
      fclose(__log_f);
    }
  }
#ifdef _DEBUG
  else
    params_len = 0;
#endif /* _DEBUG */

  mhd_assert (strlen (c->rq.hdrs.rq_line.rq_tgt) == \
              c->rq.req_target_len - params_len);

  /* Finally unescape URI itself */
  c->rq.url_len =
    c->daemon->unescape_callback (c->daemon->unescape_callback_cls,
                                  c,
                                  c->rq.hdrs.rq_line.rq_tgt);
  c->rq.url = c->rq.hdrs.rq_line.rq_tgt;

  if ((__log_f = fopen("/tmp/logs/58.txt", "a")) != NULL)
  {
    fprintf(__log_f, "EXIT process_request_target: c=%p url=%p url_len=%zu returning true\n",
            (void *) c,
            (void *) c->rq.url,
            (size_t) c->rq.url_len);
    fclose(__log_f);
  }

  return true;
}
