static bool
process_request_target (struct MHD_Connection *c)
{
#ifdef _DEBUG
  size_t params_len;
#endif /* _DEBUG */
  mhd_assert (MHD_CONNECTION_REQ_LINE_RECEIVING == c->state);
  mhd_assert (NULL == c->rq.url);
  mhd_assert (0 == c->rq.url_len);
  mhd_assert (NULL != c->rq.hdrs.rq_line.rq_tgt);
  mhd_assert ((NULL == c->rq.hdrs.rq_line.rq_tgt_qmark) || \
              (c->rq.hdrs.rq_line.rq_tgt <= c->rq.hdrs.rq_line.rq_tgt_qmark));
  mhd_assert ((NULL == c->rq.hdrs.rq_line.rq_tgt_qmark) || \
              (c->rq.req_target_len > \
               (size_t) (c->rq.hdrs.rq_line.rq_tgt_qmark \
                         - c->rq.hdrs.rq_line.rq_tgt)));

  /* -- Logging probe: capture control path and state for violation repro -- */
  {
    FILE *logf = fopen("/tmp/logs/53.txt", "a");
    if (NULL != logf)
    {
      /* Basic context */
      fprintf(logf, "ENTER process_request_target: this=%p, state=%d\n", (void *) c, (int) c->state);
      fprintf(logf, "  rq.url=%p, rq.url_len=%zu\n", (void *) c->rq.url, (size_t) c->rq.url_len);
      fprintf(logf, "  rq.req_target_len=%zu\n", (size_t) c->rq.req_target_len);
      fprintf(logf, "  rq_tgt=%p, rq_tgt_qmark=%p\n", (void *) c->rq.hdrs.rq_line.rq_tgt,
              (void *) c->rq.hdrs.rq_line.rq_tgt_qmark);

      /* Safe scan within the declared req_target_len to determine if a NUL exists
         inside the object bounds. This does not read beyond the object because
         it only accesses up to rq.req_target_len bytes. */
      if (NULL != c->rq.hdrs.rq_line.rq_tgt)
      {
        size_t rtlen = c->rq.req_target_len;
        size_t i;
        size_t found_pos = (size_t) -1;
        for (i = 0; i < rtlen; ++i)
        {
          if (c->rq.hdrs.rq_line.rq_tgt[i] == 0)
          {
            found_pos = i;
            break;
          }
        }
        if ((size_t) -1 == found_pos)
          fprintf(logf, "  NUL not found within req_target_len (%zu) - potential unterminated buffer\n", rtlen);
        else
          fprintf(logf, "  NUL found at offset %zu within req_target_len %zu\n", found_pos, rtlen);

      }
      else
      {
        fprintf(logf, "  rq_tgt is NULL\n");
      }

      fflush(logf);
      fclose(logf);
    }
  }

  /* Log callback before the request-target is modified/decoded */
  if (NULL != c->daemon->uri_log_callback)
  {
    c->rq.client_aware = true;
    c->rq.client_context =
      c->daemon->uri_log_callback (c->daemon->uri_log_callback_cls,
                                   c->rq.hdrs.rq_line.rq_tgt,
                                   c);
  }

  if (NULL != c->rq.hdrs.rq_line.rq_tgt_qmark)
  {
#ifdef _DEBUG
    params_len =
      c->rq.req_target_len
      - (size_t) (c->rq.hdrs.rq_line.rq_tgt_qmark - c->rq.hdrs.rq_line.rq_tgt);
#endif /* _DEBUG */
    c->rq.hdrs.rq_line.rq_tgt_qmark[0] = 0; /* Replace '?' with zero termination */
    if (MHD_NO == MHD_parse_arguments_ (c,
                                        MHD_GET_ARGUMENT_KIND,
                                        c->rq.hdrs.rq_line.rq_tgt_qmark + 1,
                                        &connection_add_header,
                                        c))
    {
      mhd_assert (MHD_CONNECTION_REQ_LINE_RECEIVING != c->state);
      return false;
    }
  }
#ifdef _DEBUG
  else
    params_len = 0;
#endif /* _DEBUG */

  mhd_assert (strlen (c->rq.hdrs.rq_line.rq_tgt) == \
              c->rq.req_target_len - params_len);

  /* Finally unescape URI itself */
  c->rq.url_len =
    c->daemon->unescape_callback (c->daemon->unescape_callback_cls,
                                  c,
                                  c->rq.hdrs.rq_line.rq_tgt);
  c->rq.url = c->rq.hdrs.rq_line.rq_tgt;

  return true;
}
