static void
process_value (struct MHD_PostProcessor *pp,
               const char *value_start,
               const char *value_end,
               const char *last_escape)
{
  FILE *logf = fopen("/tmp/logs/822.txt", "a");
  if (logf)
  {
    fprintf(logf, "ENTER process_value: pp=%p, value_start=%p, value_end=%p, last_escape=%p, pp->xbuf_pos=%zu, pp->must_ikvi=%d\n",
            (void *)pp, (const void *)value_start, (const void *)value_end, (const void *)last_escape,
            (size_t)pp->xbuf_pos, (int)pp->must_ikvi);
    fflush(logf);
  }

  char xbuf[XBUF_SIZE + 1];
  size_t xoff;

  mhd_assert (pp->xbuf_pos < sizeof (xbuf));
  /* 'value_start' and 'value_end' must be either both non-NULL or both NULL */
  mhd_assert ( (NULL == value_start) || (NULL != value_end) );
  mhd_assert ( (NULL != value_start) || (NULL == value_end) );
  mhd_assert ( (NULL == last_escape) || (NULL != value_start) );
  /* move remaining input from previous round into processing buffer */
  if (0 != pp->xbuf_pos)
    memcpy (xbuf,
            pp->xbuf,
            pp->xbuf_pos);
  xoff = pp->xbuf_pos;
  pp->xbuf_pos = 0;
  if ( (NULL != last_escape) &&
       (((size_t) (value_end - last_escape)) < sizeof (pp->xbuf)) )
  {
    mhd_assert (value_end >= last_escape);
    pp->xbuf_pos = (size_t) (value_end - last_escape);
    memcpy (pp->xbuf,
            last_escape,
            (size_t) (value_end - last_escape));
    if (logf)
    {
      fprintf(logf, "last_escape branch: last_escape=%p, keep_len=%zu, set pp->xbuf_pos=%zu\n",
              (const void *)last_escape,
              (size_t)(value_end - last_escape),
              pp->xbuf_pos);
      fflush(logf);
    }
    value_end = last_escape;
    if (logf)
    {
      fprintf(logf, "after adjusting value_end -> last_escape: new value_end=%p\n", (const void *)value_end);
      fflush(logf);
    }
  }
  while ( (value_start != value_end) ||
          (pp->must_ikvi) ||
          (xoff > 0) )
  {
    size_t delta = (size_t) (value_end - value_start);
    bool cut = false;
    size_t clen = 0;

    mhd_assert (value_end >= value_start);

    if (logf)
    {
      fprintf(logf, "LOOP_START: value_start=%p, value_end=%p, delta(initial)=%zu, xoff=%zu, must_ikvi=%d\n",
              (const void *)value_start, (const void *)value_end, delta, xoff, (int)pp->must_ikvi);
      fflush(logf);
    }

    if (delta > XBUF_SIZE - xoff)
      delta = XBUF_SIZE - xoff;
    /* move (additional) input into processing buffer */
    if (0 != delta)
    {
      if (logf)
      {
        fprintf(logf, "memcpy into xbuf: src=%p, delta=%zu, xoff(before)=%zu\n",
                (const void *)value_start, delta, xoff);
        fflush(logf);
      }
      memcpy (&xbuf[xoff],
              value_start,
              delta);
      xoff += delta;

      /* --- Probe: log the modification of the function parameter 'value_start' --- */
      if (logf)
      {
        fprintf(logf, "about to modify parameter value_start: before=%p, delta=%zu\n",
                (const void *)value_start, delta);
        fflush(logf);
      }

      value_start += delta; /* This is the statement flagged by MISRA Rule 17.8 */

      if (logf)
      {
        fprintf(logf, "modified parameter value_start: after=%p\n", (const void *)value_start);
        fflush(logf);
      }
    }
    /* find if escape sequence is at the end of the processing buffer;
       if so, exclude those from processing (reduce delta to point at
       end of processed region) */
    if ( (xoff > 0) &&
         ('%' == xbuf[xoff - 1]) )
    {
      cut = (xoff != XBUF_SIZE);
      xoff--;
      if (cut)
      {
        /* move escape sequence into buffer for next function invocation */
        pp->xbuf[0] = '%';
        pp->xbuf_pos = 1;
        if (logf)
        {
          fprintf(logf, "escape at end: single '%%' cut=true, moved to pp->xbuf[0], pp->xbuf_pos=1\n");
          fflush(logf);
        }
      }
      else
      {
        /* just skip escape sequence for next loop iteration */
        delta = xoff;
        clen = 1;
        if (logf)
        {
          fprintf(logf, "escape at end: single '%%' cut=false, set delta=%zu, clen=1\n", delta);
          fflush(logf);
        }
      }
    }
    else if ( (xoff > 1) &&
              ('%' == xbuf[xoff - 2]) )
    {
      cut = (xoff != XBUF_SIZE);
      xoff -= 2;
      if (cut)
      {
        /* move escape sequence into buffer for next function invocation */
        memcpy (pp->xbuf,
                &xbuf[xoff],
                2);
        pp->xbuf_pos = 2;
        if (logf)
        {
          fprintf(logf, "escape at end: '%%' plus char cut=true, moved 2 bytes to pp->xbuf, pp->xbuf_pos=2\n");
          fflush(logf);
        }
      }
      else
      {
        /* just skip escape sequence for next loop iteration */
        delta = xoff;
        clen = 2;
        if (logf)
        {
          fprintf(logf, "escape at end: '%%' plus char cut=false, set delta=%zu, clen=2\n", delta);
          fflush(logf);
        }
      }
    }
    mhd_assert (xoff < sizeof (xbuf));
    /* unescape */
    xbuf[xoff] = '\0';        /* 0-terminate in preparation */
    if (0 != xoff)
    {
      MHD_unescape_plus (xbuf);
      xoff = MHD_http_unescape (xbuf);
      if (logf)
      {
        fprintf(logf, "after unescape: xbuf='%s', xoff=%zu\n", xbuf, xoff);
        fflush(logf);
      }
    }
    /* finally: call application! */
    if (pp->must_ikvi || (0 != xoff) )
    {
      pp->must_ikvi = false;
      if (logf)
      {
        fprintf(logf, "calling ikvi: cls=%p, key=%p, value_offset=%zu, xoff=%zu\n",
                pp->cls, (const void *)&pp[1], pp->value_offset, xoff);
        fflush(logf);
      }
      if (MHD_NO == pp->ikvi (pp->cls,
                              MHD_POSTDATA_KIND,
                              (const char *) &pp[1],      /* key */
                              NULL,
                              NULL,
                              NULL,
                              xbuf,
                              pp->value_offset,
                              xoff))
      {
        pp->state = PP_Error;
        if (logf)
        {
          fprintf(logf, "ikvi returned MHD_NO: setting pp->state=PP_Error and returning\n");
          fclose(logf);
        }
        return;
      }
      if (logf)
      {
        fprintf(logf, "ikvi returned success\n");
        fflush(logf);
      }
    }
    pp->value_offset += xoff;
    if (logf)
    {
      fprintf(logf, "after processing chunk: updated pp->value_offset=%zu, cut=%d, clen=%zu\n",
              pp->value_offset, (int)cut, clen);
      fflush(logf);
    }
    if (cut)
      break;
    if (0 != clen)
    {
      xbuf[delta] = '%';        /* undo 0-termination */
      memmove (xbuf,
               &xbuf[delta],
               clen);
      if (logf)
      {
        fprintf(logf, "handled clen nonzero: restored xbuf start with %zu bytes, xoff set to clen\n", clen);
        fflush(logf);
      }
    }
    xoff = clen;
  }

  if (logf)
  {
    fprintf(logf, "EXIT process_value: final value_start=%p, value_end=%p, pp->xbuf_pos=%zu, pp->value_offset=%zu, pp->state=%d\n",
            (const void *)value_start, (const void *)value_end, pp->xbuf_pos, pp->value_offset, (int)pp->state);
    fclose(logf);
  }
}
