static void
process_value (struct MHD_PostProcessor *pp,
               const char *value_start,
               const char *value_end,
               const char *last_escape)
{
  char xbuf[XBUF_SIZE + 1];
  size_t xoff;
  FILE *log = fopen("/tmp/logs/823.txt", "a");
  if (log)
  {
    fprintf(log, "ENTER process_value: pp=%p, value_start=%p, value_end=%p, last_escape=%p, pp->xbuf_pos=%zu\n",
            (void *)pp, (const void *)value_start, (const void *)value_end, (const void *)last_escape,
            (size_t)pp->xbuf_pos);
    fflush(log);
  }

  mhd_assert (pp->xbuf_pos < sizeof (xbuf));
  /* 'value_start' and 'value_end' must be either both non-NULL or both NULL */
  mhd_assert ( (NULL == value_start) || (NULL != value_end) );
  mhd_assert ( (NULL != value_start) || (NULL == value_end) );
  mhd_assert ( (NULL == last_escape) || (NULL != value_start) );
  /* move remaining input from previous round into processing buffer */
  if (0 != pp->xbuf_pos)
  {
    if (log)
    {
      fprintf(log, "copying existing pp->xbuf -> xbuf: len=%zu\n", (size_t)pp->xbuf_pos);
      fflush(log);
    }
    memcpy (xbuf,
            pp->xbuf,
            pp->xbuf_pos);
  }
  xoff = pp->xbuf_pos;
  pp->xbuf_pos = 0;
  if ( (NULL != last_escape) &&
       (((size_t) (value_end - last_escape)) < sizeof (pp->xbuf)) )
  {
    mhd_assert (value_end >= last_escape);
    pp->xbuf_pos = (size_t) (value_end - last_escape);
    if (log)
    {
      fprintf(log, "pre-copy from last_escape -> pp->xbuf: last_escape=%p, value_end=%p, len=%zu\n",
              (const void *)last_escape, (const void *)value_end, pp->xbuf_pos);
      fflush(log);
    }
    memcpy (pp->xbuf,
            last_escape,
            (size_t) (value_end - last_escape));
    value_end = last_escape;
  }
  while ( (value_start != value_end) ||
          (pp->must_ikvi) ||
          (xoff > 0) )
  {
    size_t delta = (size_t) (value_end - value_start);
    bool cut = false;
    size_t clen = 0;

    mhd_assert (value_end >= value_start);

    if (delta > XBUF_SIZE - xoff)
      delta = XBUF_SIZE - xoff;
    /* move (additional) input into processing buffer */
    if (0 != delta)
    {
      if (log)
      {
        fprintf(log, "memcpy value_start -> &xbuf[xoff]: value_start=%p, delta=%zu, xoff(before)=%zu\n",
                (const void *)value_start, delta, xoff);
        fflush(log);
      }
      memcpy (&xbuf[xoff],
              value_start,
              delta);
      xoff += delta;
      value_start += delta;
    }
    /* find if escape sequence is at the end of the processing buffer;
       if so, exclude those from processing (reduce delta to point at
       end of processed region) */
    if ( (xoff > 0) &&
         ('%' == xbuf[xoff - 1]) )
    {
      cut = (xoff != XBUF_SIZE);
      xoff--;
      if (cut)
      {
        /* move escape sequence into buffer for next function invocation */
        pp->xbuf[0] = '%';
        pp->xbuf_pos = 1;
        if (log)
        {
          fprintf(log, "escape-tail 1 byte: cut=1, set pp->xbuf[0]='%%', pp->xbuf_pos=1\n");
          fflush(log);
        }
      }
      else
      {
        /* just skip escape sequence for next loop iteration */
        delta = xoff;
        clen = 1;
        if (log)
        {
          fprintf(log, "escape-tail 1 byte at end but not cut: delta=%zu, clen=1\n", delta);
          fflush(log);
        }
      }
    }
    else if ( (xoff > 1) &&
              ('%' == xbuf[xoff - 2]) )
    {
      cut = (xoff != XBUF_SIZE);
      xoff -= 2;
      if (cut)
      {
        /* move escape sequence into buffer for next function invocation */
        if (log)
        {
          fprintf(log, "escape-tail 2 bytes and cut: copying 2 bytes to pp->xbuf\n");
          fflush(log);
        }
        memcpy (pp->xbuf,
                &xbuf[xoff],
                2);
        pp->xbuf_pos = 2;
      }
      else
      {
        /* just skip escape sequence for next loop iteration */
        delta = xoff;
        clen = 2;
        if (log)
        {
          fprintf(log, "escape-tail 2 bytes at end but not cut: delta=%zu, clen=2\n", delta);
          fflush(log);
        }
      }
    }
    mhd_assert (xoff < sizeof (xbuf));
    /* unescape */
    xbuf[xoff] = '\0';        /* 0-terminate in preparation */
    if (0 != xoff)
    {
      if (log)
      {
        fprintf(log, "calling MHD_unescape_plus and MHD_http_unescape: xbuf(before)=%s, xoff(before)=%zu\n",
                xbuf, xoff);
        fflush(log);
      }
      MHD_unescape_plus (xbuf);
      xoff = MHD_http_unescape (xbuf);
      if (log)
      {
        fprintf(log, "after unescape: xbuf(after)=%s, xoff(after)=%zu\n", xbuf, xoff);
        fflush(log);
      }
    }
    /* finally: call application! */
    if (pp->must_ikvi || (0 != xoff) )
    {
      pp->must_ikvi = false;
      if (log)
      {
        fprintf(log, "calling ikvi: cls=%p, value_offset=%zu, xoff=%zu\n",
                pp->cls, pp->value_offset, xoff);
        fflush(log);
      }
      if (MHD_NO == pp->ikvi (pp->cls,
                              MHD_POSTDATA_KIND,
                              (const char *) &pp[1],      /* key */
                              NULL,
                              NULL,
                              NULL,
                              xbuf,
                              pp->value_offset,
                              xoff))
      {
        pp->state = PP_Error;
        if (log)
        {
          fprintf(log, "ikvi returned MHD_NO: setting pp->state=PP_Error and returning\n");
          fclose(log);
        }
        return;
      }
    }
    pp->value_offset += xoff;
    if (cut)
      break;
    if (0 != clen)
    {
      xbuf[delta] = '%';        /* undo 0-termination */
      if (log)
      {
        fprintf(log, "memmove xbuf: from &xbuf[%zu] len=%zu -> xbuf[0], delta=%zu\n",
                delta, clen, delta);
        fflush(log);
      }
      memmove (xbuf,
               &xbuf[delta],
               clen);
    }
    xoff = clen;
  }

  if (log)
  {
    fprintf(log, "EXIT process_value: pp=%p, pp->xbuf_pos=%zu, pp->value_offset=%zu, state=%d\n",
            (void *)pp, (size_t)pp->xbuf_pos, pp->value_offset, (int)pp->state);
    fclose(log);
  }
}
