static enum MHD_Result
resume_suspended_connections (struct MHD_Daemon *daemon)
{
  struct MHD_Connection *pos;
  struct MHD_Connection *prev = NULL;
  enum MHD_Result ret;
  const bool used_thr_p_c = (0 != (daemon->options
                                   & MHD_USE_THREAD_PER_CONNECTION));
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  mhd_assert (NULL == daemon->worker_pool);
  mhd_assert ( (! MHD_D_IS_USING_THREADS_ (daemon)) || \
               MHD_thread_handle_ID_is_current_thread_ (daemon->tid) );
#endif

  /* Open log for tracing control flow and state relevant to the
     invariant-controlling-expression scenario. Appends to the file so
     multiple invocations/tests accumulate evidence. */
  FILE *mhd_log = NULL;

  ret = MHD_NO;
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  MHD_mutex_lock_chk_ (&daemon->cleanup_connection_mutex);
#endif

  /* Open log after acquiring mutex so we capture stable state where
     possible. If fopen fails, proceed silently (don't break original logic). */
  mhd_log = fopen("/tmp/logs/2289.txt", "a");
  if (mhd_log)
  {
    fprintf (mhd_log, "resume_suspended_connections: entered; daemon=%p used_thr_p_c=%d resuming=%d suspended_tail=%p\n",
             (void *) daemon,
             (int) used_thr_p_c,
             (int) daemon->resuming,
             (void *) daemon->suspended_connections_tail);
    fflush (mhd_log);
  }

  if (daemon->resuming)
  {
    prev = daemon->suspended_connections_tail;
    /* During shutdown check for resuming is forced. */
    mhd_assert ((NULL != prev) || (daemon->shutdown) || \
                (0 != (daemon->options & MHD_ALLOW_UPGRADE)));
    if (mhd_log)
    {
      fprintf (mhd_log, "  initial prev=%p (suspended_connections_tail)\n",
               (void *) prev);
      fflush (mhd_log);
    }
  }

  daemon->resuming = false;

  while (NULL != (pos = prev))
  {
#ifdef UPGRADE_SUPPORT
    struct MHD_UpgradeResponseHandle *const urh = pos->urh;
#else  /* ! UPGRADE_SUPPORT */
    static const void *const urh = NULL;
#endif /* ! UPGRADE_SUPPORT */
    prev = pos->prev;

    if (mhd_log)
    {
#ifdef UPGRADE_SUPPORT
      fprintf (mhd_log, "  iter: pos=%p prev=%p pos->resuming=%d pos->suspended=%d urh=%p\n",
               (void *) pos, (void *) prev,
               (int) pos->resuming, (int) pos->suspended,
               (void *) urh);
#else
      fprintf (mhd_log, "  iter: pos=%p prev=%p pos->resuming=%d pos->suspended=%d urh=%p (static NULL)\n",
               (void *) pos, (void *) prev,
               (int) pos->resuming, (int) pos->suspended,
               (void *) urh);
#endif
      fflush (mhd_log);
    }

    if ( (! pos->resuming)
#ifdef UPGRADE_SUPPORT
         || ( (NULL != urh) &&
              ( (! urh->was_closed) ||
                (! urh->clean_ready) ) )
#endif /* UPGRADE_SUPPORT */
         )
    {
      if (mhd_log)
      {
#ifdef UPGRADE_SUPPORT
        if (! pos->resuming)
          fprintf (mhd_log, "    continue: !pos->resuming\n");
        else if ((NULL != urh) && ((! urh->was_closed) || (! urh->clean_ready)))
          fprintf (mhd_log, "    continue: urh indicates not ready (urh=%p was_closed=%d clean_ready=%d)\n",
                   (void *) urh, (int) urh->was_closed, (int) urh->clean_ready);
        else
          fprintf (mhd_log, "    continue: unknown reason\n");
#else
        /* When UPGRADE_SUPPORT is not defined, urh is a static NULL and
           the condition reduces to (! pos->resuming) only. Log that
           the invariant branch based on urh does not exist in this build. */
        if (! pos->resuming)
          fprintf (mhd_log, "    continue: !pos->resuming (UPGRADE_SUPPORT not defined; urh static NULL)\n");
        else
          fprintf (mhd_log, "    continue: unexpected branch (UPGRADE_SUPPORT not defined)\n");
#endif
        fflush (mhd_log);
      }
      continue;
    }

    ret = MHD_YES;
    if (mhd_log)
    {
      fprintf (mhd_log, "  setting ret = MHD_YES for pos=%p\n", (void *) pos);
      fflush (mhd_log);
    }

    mhd_assert (pos->suspended);
    DLL_remove (daemon->suspended_connections_head,
                daemon->suspended_connections_tail,
                pos);
    pos->suspended = false;
    if (NULL == urh)
    {
      if (mhd_log)
      {
        fprintf (mhd_log, "    branch: NULL == urh -> reinserting into connections list\n");
        fflush (mhd_log);
      }
      DLL_insert (daemon->connections_head,
                  daemon->connections_tail,
                  pos);
      if (! used_thr_p_c)
      {
        /* Reset timeout timer on resume. */
        if (0 != pos->connection_timeout_ms)
          pos->last_activity = MHD_monotonic_msec_counter ();

        if (pos->connection_timeout_ms == daemon->connection_timeout_ms)
          XDLL_insert (daemon->normal_timeout_head,
                       daemon->normal_timeout_tail,
                       pos);
        else
          XDLL_insert (daemon->manual_timeout_head,
                       daemon->manual_timeout_tail,
                       pos);
      }
#ifdef EPOLL_SUPPORT
      if (MHD_D_IS_USING_EPOLL_ (daemon))
      {
        if (0 != (pos->epoll_state & MHD_EPOLL_STATE_IN_EREADY_EDLL))
          MHD_PANIC ("Resumed connection was already in EREADY set.\n");
        /* we always mark resumed connections as ready, as we
           might have missed the edge poll event during suspension */
        EDLL_insert (daemon->eready_head,
                     daemon->eready_tail,
                     pos);
        pos->epoll_state |= MHD_EPOLL_STATE_IN_EREADY_EDLL   \
                            | MHD_EPOLL_STATE_READ_READY
                            | MHD_EPOLL_STATE_WRITE_READY;
        pos->epoll_state &= ~((enum MHD_EpollState) MHD_EPOLL_STATE_SUSPENDED);
      }
#endif
    }
#ifdef UPGRADE_SUPPORT
    else
    {
      /* Data forwarding was finished (for TLS connections) AND
       * application was closed upgraded connection.
       * Insert connection into cleanup list. */

      if ( (NULL != daemon->notify_completed) &&
           (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon)) &&
           (pos->rq.client_aware) )
      {
        daemon->notify_completed (daemon->notify_completed_cls,
                                  pos,
                                  &pos->rq.client_context,
                                  MHD_REQUEST_TERMINATED_COMPLETED_OK);
        pos->rq.client_aware = false;
      }
      DLL_insert (daemon->cleanup_head,
                  daemon->cleanup_tail,
                  pos);
      daemon->data_already_pending = true;
      if (mhd_log)
      {
        fprintf (mhd_log, "    branch: urh != NULL -> inserted into cleanup list (urh=%p)\n",
                 (void *) urh);
        fflush (mhd_log);
      }
    }
#endif /* UPGRADE_SUPPORT */
    pos->resuming = false;
  }
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  MHD_mutex_unlock_chk_ (&daemon->cleanup_connection_mutex);
#endif
  if (mhd_log)
  {
    fprintf (mhd_log, "post-loop: ret=%d used_thr_p_c=%d\n",
             (int) ret, (int) used_thr_p_c);
    fflush (mhd_log);
  }
  if ( (used_thr_p_c) &&
       (MHD_NO != ret) )
  {   /* Wake up suspended connections. */
    if (! MHD_itc_activate_ (daemon->itc,
                             "w"))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Failed to signal resume of connection via " \
                   "inter-thread communication channel.\n"));
#endif
      if (mhd_log)
      {
        fprintf (mhd_log, "  warning: failed to activate itc\n");
        fflush (mhd_log);
      }
    }
  }

  if (mhd_log)
  {
    fprintf (mhd_log, "resume_suspended_connections: exiting ret=%d\n\n", (int) ret);
    fclose (mhd_log);
  }

  return ret;
}
