static enum MHD_Result
resume_suspended_connections (struct MHD_Daemon *daemon)
{
  struct MHD_Connection *pos;
  struct MHD_Connection *prev = NULL;
  enum MHD_Result ret;
  const bool used_thr_p_c = (0 != (daemon->options
                                   & MHD_USE_THREAD_PER_CONNECTION));
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  mhd_assert (NULL == daemon->worker_pool);
  mhd_assert ( (! MHD_D_IS_USING_THREADS_ (daemon)) || \
               MHD_thread_handle_ID_is_current_thread_ (daemon->tid) );
#endif

  /* Open a log file to capture control flow and state for debugging the
     potential MISRA Rule 8.13 scenario. Use append mode so repeated runs
     accumulate evidence. */
  FILE *mhd_log = fopen("/tmp/logs/2285.txt", "a");
  if (mhd_log)
  {
    fprintf(mhd_log, "\n---- resume_suspended_connections entered ----\n");
    fprintf(mhd_log, "daemon=%p options=0x%08x resuming=%d suspended_tail=%p\n",
            (void *) daemon,
            (unsigned) daemon->options,
            (int) daemon->resuming,
            (void *) daemon->suspended_connections_tail);
    fprintf(mhd_log, "used_thr_p_c=%d\n", (int) used_thr_p_c);
    fflush(mhd_log);
  }

  ret = MHD_NO;
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  MHD_mutex_lock_chk_ (&daemon->cleanup_connection_mutex);
  if (mhd_log)
  {
    fprintf(mhd_log, "acquired cleanup_connection_mutex\n");
    fflush(mhd_log);
  }
#endif

  if (daemon->resuming)
  {
    prev = daemon->suspended_connections_tail;
    /* During shutdown check for resuming is forced. */
    mhd_assert ((NULL != prev) || (daemon->shutdown) || \
                (0 != (daemon->options & MHD_ALLOW_UPGRADE)));
    if (mhd_log)
    {
      fprintf(mhd_log, "daemon->resuming true, prev (suspended_tail)=%p\n",
              (void *) prev);
      fflush(mhd_log);
    }
  }

  daemon->resuming = false;

  while (NULL != (pos = prev))
  {
#ifdef UPGRADE_SUPPORT
    struct MHD_UpgradeResponseHandle *const urh = pos->urh;
#else  /* ! UPGRADE_SUPPORT */
    static const void *const urh = NULL;
#endif /* ! UPGRADE_SUPPORT */
    prev = pos->prev;

    if (mhd_log)
    {
      fprintf(mhd_log, "loop iter: pos=%p prev=%p pos->resuming=%d pos->suspended=%d\n",
              (void *) pos,
              (void *) prev,
              (int) pos->resuming,
              (int) pos->suspended);
#ifdef UPGRADE_SUPPORT
      fprintf(mhd_log, "  [UPGRADE_SUPPORT] urh=%p\n", (void *) urh);
      if (NULL != urh)
        fprintf(mhd_log, "    urh->was_closed=%d urh->clean_ready=%d\n",
                (int) urh->was_closed, (int) urh->clean_ready);
#else
      fprintf(mhd_log, "  [!UPGRADE_SUPPORT] urh=NULL\n");
#endif
      fflush(mhd_log);
    }

    if ( (! pos->resuming)
#ifdef UPGRADE_SUPPORT
         || ( (NULL != urh) &&
              ( (! urh->was_closed) ||
                (! urh->clean_ready) ) )
#endif /* UPGRADE_SUPPORT */
         )
    {
      if (mhd_log)
      {
        /* Log reason for skipping/resuming check. */
        fprintf(mhd_log, "  continue: ");
        if (! pos->resuming)
          fprintf(mhd_log, "pos->resuming is false ");
#ifdef UPGRADE_SUPPORT
        if ((NULL != urh) && ((! urh->was_closed) || (! urh->clean_ready)))
          fprintf(mhd_log, "urh present but not closed/clean ");
#endif
        fprintf(mhd_log, "\n");
        fflush(mhd_log);
      }
      continue;
    }

    ret = MHD_YES;
    if (mhd_log)
    {
      fprintf(mhd_log, "  resuming this connection: pos=%p -> set ret=MHD_YES\n",
              (void *) pos);
      fflush(mhd_log);
    }

    mhd_assert (pos->suspended);
    DLL_remove (daemon->suspended_connections_head,
                daemon->suspended_connections_tail,
                pos);
    if (mhd_log)
    {
      fprintf(mhd_log, "  removed from suspended list: pos=%p\n", (void *) pos);
      fflush(mhd_log);
    }

    pos->suspended = false;

    if (NULL == urh)
    {
      if (mhd_log)
      {
        fprintf(mhd_log, "  urh==NULL branch: reinserting into active lists\n");
        fprintf(mhd_log, "    pos->connection_timeout_ms=%d daemon->connection_timeout_ms=%d\n",
                pos->connection_timeout_ms, daemon->connection_timeout_ms);
        fflush(mhd_log);
      }

      DLL_insert (daemon->connections_head,
                  daemon->connections_tail,
                  pos);

      if (! used_thr_p_c)
      {
        /* Reset timeout timer on resume. */
        if (0 != pos->connection_timeout_ms)
          pos->last_activity = MHD_monotonic_msec_counter ();

        if (pos->connection_timeout_ms == daemon->connection_timeout_ms)
          XDLL_insert (daemon->normal_timeout_head,
                       daemon->normal_timeout_tail,
                       pos);
        else
          XDLL_insert (daemon->manual_timeout_head,
                       daemon->manual_timeout_tail,
                       pos);

        if (mhd_log)
        {
          fprintf(mhd_log, "    inserted into %s timeout list\n",
                  (pos->connection_timeout_ms == daemon->connection_timeout_ms)
                  ? "normal" : "manual");
          fflush(mhd_log);
        }
      }
#ifdef EPOLL_SUPPORT
      if (MHD_D_IS_USING_EPOLL_ (daemon))
      {
        if (0 != (pos->epoll_state & MHD_EPOLL_STATE_IN_EREADY_EDLL))
          MHD_PANIC ("Resumed connection was already in EREADY set.\n");
        /* we always mark resumed connections as ready, as we
           might have missed the edge poll event during suspension */
        EDLL_insert (daemon->eready_head,
                     daemon->eready_tail,
                     pos);
        pos->epoll_state |= MHD_EPOLL_STATE_IN_EREADY_EDLL   \
                            | MHD_EPOLL_STATE_READ_READY
                            | MHD_EPOLL_STATE_WRITE_READY;
        pos->epoll_state &= ~((enum MHD_EpollState) MHD_EPOLL_STATE_SUSPENDED);
        if (mhd_log)
        {
          fprintf(mhd_log, "    EPOLL: inserted into eready, epoll_state=0x%x\n",
                  (unsigned) pos->epoll_state);
          fflush(mhd_log);
        }
      }
#endif
    }
#ifdef UPGRADE_SUPPORT
    else
    {
      /* Data forwarding was finished (for TLS connections) AND
       * application was closed upgraded connection.
       * Insert connection into cleanup list. */

      if (mhd_log)
      {
        fprintf(mhd_log, "  urh!=NULL branch: forwarding finished/cleanup insertion for pos=%p\n",
                (void *) pos);
        fflush(mhd_log);
      }

      if ( (NULL != daemon->notify_completed) &&
           (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon)) &&
           (pos->rq.client_aware) )
      {
        if (mhd_log)
        {
          fprintf(mhd_log, "    calling notify_completed for pos=%p\n", (void *) pos);
          fflush(mhd_log);
        }
        daemon->notify_completed (daemon->notify_completed_cls,
                                  pos,
                                  &pos->rq.client_context,
                                  MHD_REQUEST_TERMINATED_COMPLETED_OK);
        pos->rq.client_aware = false;
      }
      DLL_insert (daemon->cleanup_head,
                  daemon->cleanup_tail,
                  pos);
      daemon->data_already_pending = true;
      if (mhd_log)
      {
        fprintf(mhd_log, "    inserted into cleanup list, data_already_pending set true\n");
        fflush(mhd_log);
      }
    }
#endif /* UPGRADE_SUPPORT */

    pos->resuming = false;
    if (mhd_log)
    {
      fprintf(mhd_log, "  finished processing pos=%p, pos->resuming now false\n",
              (void *) pos);
      fflush(mhd_log);
    }
  }
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  MHD_mutex_unlock_chk_ (&daemon->cleanup_connection_mutex);
  if (mhd_log)
  {
    fprintf(mhd_log, "released cleanup_connection_mutex\n");
    fflush(mhd_log);
  }
#endif

  if ( (used_thr_p_c) &&
       (MHD_NO != ret) )
  {   /* Wake up suspended connections. */
    if (! MHD_itc_activate_ (daemon->itc,
                             "w"))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Failed to signal resume of connection via " \
                   "inter-thread communication channel.\n"));
#endif
      if (mhd_log)
      {
        fprintf(mhd_log, "Failed to signal resume via itc for daemon=%p\n", (void *) daemon);
        fflush(mhd_log);
      }
    }
    else if (mhd_log)
    {
      fprintf(mhd_log, "Signalled resume via itc for daemon=%p\n", (void *) daemon);
      fflush(mhd_log);
    }
  }

  if (mhd_log)
  {
    fprintf(mhd_log, "resume_suspended_connections exiting with ret=%d\n",
            (int) ret);
    fprintf(mhd_log, "---- end resume_suspended_connections ----\n");
    fclose(mhd_log);
  }

  return ret;
}
