static int
setup_epoll_fd (struct MHD_Daemon *daemon)
{
  int fd;
  /* Open a persistent log for this test to record control flow and state. */
  FILE *log = fopen("/tmp/logs/768.txt", "a");
  if (log)
  {
    fprintf(log, "setup_epoll_fd: entered, daemon=%p\n", (void *) daemon);
  }

#ifndef HAVE_MESSAGES
  (void) daemon; /* Mute compiler warning. */
  if (log)
    fprintf(log, "setup_epoll_fd: executed (void) daemon; (suppressed unused parameter)\n");
#endif /* ! HAVE_MESSAGES */

#ifdef USE_EPOLL_CREATE1
  if (log)
    fprintf(log, "setup_epoll_fd: using epoll_create1\n");
  fd = epoll_create1 (EPOLL_CLOEXEC);
#else  /* ! USE_EPOLL_CREATE1 */
  if (log)
    fprintf(log, "setup_epoll_fd: using epoll_create (MAX_EVENTS=%d)\n", MAX_EVENTS);
  fd = epoll_create (MAX_EVENTS);
#endif /* ! USE_EPOLL_CREATE1 */
  if (log)
    fprintf(log, "setup_epoll_fd: epoll returned fd=%d\n", fd);
  if (MHD_INVALID_SOCKET == fd)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Call to epoll_create1 failed: %s\n"),
              MHD_socket_last_strerr_ ());
#endif
    if (log)
    {
#ifdef HAVE_MESSAGES
      fprintf(log, "setup_epoll_fd: epoll_create failed, err=%s\n", MHD_socket_last_strerr_());
#else
      fprintf(log, "setup_epoll_fd: epoll_create failed\n");
#endif
      fclose(log);
    }
    return MHD_INVALID_SOCKET;
  }
#if ! defined(USE_EPOLL_CREATE1)
  if (! MHD_socket_noninheritable_ (fd))
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to set noninheritable mode on epoll FD.\n"));
#endif
    if (log)
      fprintf(log, "setup_epoll_fd: failed to set noninheritable mode on fd=%d\n", fd);
  }
  else
  {
    if (log)
      fprintf(log, "setup_epoll_fd: set noninheritable mode on fd=%d succeeded\n", fd);
  }
#endif /* ! USE_EPOLL_CREATE1 */
  if (log)
  {
    fprintf(log, "setup_epoll_fd: returning fd=%d\n", fd);
    fclose(log);
  }
  return fd;
}
