static int
setup_epoll_fd (struct MHD_Daemon *daemon)
{
  int fd;
  FILE *logf = fopen("/tmp/logs/765.txt", "a");
  if (logf)
  {
#ifdef HAVE_MESSAGES
    fprintf(logf, "ENTER setup_epoll_fd: daemon=%p, HAVE_MESSAGES=1\n", (void *) daemon);
#else
    fprintf(logf, "ENTER setup_epoll_fd: daemon=%p, HAVE_MESSAGES=0\n", (void *) daemon);
#endif
    fflush(logf);
  }

#ifndef HAVE_MESSAGES
  (void) daemon; /* Mute compiler warning. */
#endif /* ! HAVE_MESSAGES */

#ifdef USE_EPOLL_CREATE1
  if (logf)
    fprintf(logf, "Calling epoll_create1(EPOLL_CLOEXEC)\n");
  fd = epoll_create1 (EPOLL_CLOEXEC);
#else  /* ! USE_EPOLL_CREATE1 */
  if (logf)
    fprintf(logf, "Calling epoll_create(MAX_EVENTS=%d)\n", MAX_EVENTS);
  fd = epoll_create (MAX_EVENTS);
#endif /* ! USE_EPOLL_CREATE1 */

  if (logf)
  {
    fprintf(logf, "epoll returned fd=%d\n", fd);
    fflush(logf);
  }

  if (MHD_INVALID_SOCKET == fd)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Call to epoll_create1 failed: %s\n"),
              MHD_socket_last_strerr_ ());
#endif
    if (logf)
    {
#ifdef HAVE_MESSAGES
      /* Log the textual error only if the helper is available. */
      fprintf(logf, "epoll_create* failed: %s\n", MHD_socket_last_strerr_ ());
#else
      fprintf(logf, "epoll_create* failed: returned MHD_INVALID_SOCKET\n");
#endif
      fflush(logf);
      fclose(logf);
    }
    return MHD_INVALID_SOCKET;
  }

#if ! defined(USE_EPOLL_CREATE1)
  if (! MHD_socket_noninheritable_ (fd))
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to set noninheritable mode on epoll FD.\n"));
#endif
    if (logf)
    {
      fprintf(logf, "Failed to set noninheritable mode on epoll FD (fd=%d)\n", fd);
      fflush(logf);
    }
  }
  else
  {
    if (logf)
    {
      fprintf(logf, "Successfully set noninheritable mode on epoll FD (fd=%d)\n", fd);
      fflush(logf);
    }
  }
#endif /* ! USE_EPOLL_CREATE1 */

  if (logf)
  {
    fprintf(logf, "EXIT setup_epoll_fd returning fd=%d\n", fd);
    fflush(logf);
    fclose(logf);
  }

  return fd;
}
