static enum MHD_Result
test_header (void *cls,
             const char *key,
             size_t key_size,
             const char *value,
             size_t value_size,
             enum MHD_ValueKind kind)
{
  struct test_header_param *const param = (struct test_header_param *) cls;
  struct MHD_Connection *connection = param->connection;
  struct MHD_HTTP_Req_Header *pos;
  size_t i;
  FILE *logf = fopen("/tmp/logs/1341.txt", "a");

  if (logf)
    fprintf(logf, "ENTER test_header: cls=%p param=%p connection=%p num_headers(before)=%zu\n",
            cls, (void *)param, (void *)connection, param->num_headers);

  param->num_headers++;
  if (logf)
    fprintf(logf, "num_headers(after)=%zu\n", param->num_headers);

  i = 0;
  for (pos = connection->rq.headers_received; NULL != pos; pos = pos->next)
  {
    if (logf)
      fprintf(logf, "LOOP iter: pos=%p kind=%d header_size=%zu value_size=%zu next=%p\n",
              (void *)pos, (int) pos->kind, pos->header_size, pos->value_size,
              (void *) pos->next);

    if (kind != pos->kind)
    {
      if (logf)
        fprintf(logf, "  kind mismatch: wanted=%d got=%d -> continue\n", (int) kind, (int) pos->kind);
      continue;
    }

    /* increment the loop-local counter (matches original semantics of ++i in the body) */
    i++;
    if (logf)
      fprintf(logf, "  incremented i -> i=%zu (compare to num_headers=%zu)\n", i, param->num_headers);

    if (i == param->num_headers)
    {
      if (logf)
        fprintf(logf, "  i == num_headers: performing header/value checks\n");

      if (key_size != pos->header_size)
      {
        if (logf)
          fprintf(logf, "  header_size mismatch: key_size=%zu pos->header_size=%zu -> return MHD_NO\n",
                  key_size, pos->header_size);
        if (logf)
          fclose(logf);
        return MHD_NO;
      }
      if (value_size != pos->value_size)
      {
        if (logf)
          fprintf(logf, "  value_size mismatch: value_size=%zu pos->value_size=%zu -> return MHD_NO\n",
                  value_size, pos->value_size);
        if (logf)
          fclose(logf);
        return MHD_NO;
      }
      if (0 != key_size)
      {
        mhd_assert (NULL != key);
        mhd_assert (NULL != pos->header);
        if (0 != memcmp (key,
                         pos->header,
                         key_size))
        {
          if (logf)
            fprintf(logf, "  header memcmp mismatch -> return MHD_NO\n");
          if (logf)
            fclose(logf);
          return MHD_NO;
        }
      }
      if (0 != value_size)
      {
        mhd_assert (NULL != value);
        mhd_assert (NULL != pos->value);
        if (0 != memcmp (value,
                         pos->value,
                         value_size))
        {
          if (logf)
            fprintf(logf, "  value memcmp mismatch -> return MHD_NO\n");
          if (logf)
            fclose(logf);
          return MHD_NO;
        }
      }
      if (logf)
        fprintf(logf, "  all checks passed -> return MHD_YES\n");
      if (logf)
        fclose(logf);
      return MHD_YES;
    }
  }

  if (logf)
    fprintf(logf, "END test_header: no matching header found -> return MHD_NO\n");
  if (logf)
    fclose(logf);
  return MHD_NO;
}
