static enum MHD_Result
test_header (void *cls,
             const char *key,
             size_t key_size,
             const char *value,
             size_t value_size,
             enum MHD_ValueKind kind)
{
  struct test_header_param *const param = (struct test_header_param *) cls;
  struct MHD_Connection *connection = param->connection;
  struct MHD_HTTP_Req_Header *pos;
  size_t i;

  /* Logging probe: record entry and initial state */
  {
    FILE *f = fopen("/tmp/logs/1343.txt", "a");
    if (NULL != f)
    {
      fprintf(f, "ENTER test_header: cls=%p param=%p connection=%p key=%p key_size=%zu value=%p value_size=%zu kind=%d num_headers_before=%zu\n",
              cls, (void *)param, (void *)connection, (const void *)key, key_size, (const void *)value, value_size, (int) kind, param->num_headers);
      fclose(f);
    }
  }

  param->num_headers++;

  /* Log after increment */
  {
    FILE *f = fopen("/tmp/logs/1343.txt", "a");
    if (NULL != f)
    {
      fprintf(f, "AFTER INC: num_headers=%zu\n", param->num_headers);
      fclose(f);
    }
  }

  i = 0;
  for (pos = connection->rq.headers_received; NULL != pos; pos = pos->next)
  {
    /* Log each visited header node */
    {
      FILE *f = fopen("/tmp/logs/1343.txt", "a");
      if (NULL != f)
      {
        fprintf(f, "LOOP: pos=%p pos->kind=%d pos->header_size=%zu pos->value_size=%zu pos->header=%p pos->value=%p\n",
                (void *)pos, (int) pos->kind, pos->header_size, pos->value_size, (const void *) pos->header, (const void *) pos->value);
        fclose(f);
      }
    }

    if (kind != pos->kind)
      continue;
    if (++i == param->num_headers)
    {
      /* Candidate header matched by index: log details */
      {
        FILE *f = fopen("/tmp/logs/1343.txt", "a");
        if (NULL != f)
        {
          fprintf(f, "CANDIDATE: matched index i=%zu expected_index=%zu\n", i, param->num_headers);
          fprintf(f, "CANDIDATE: key_size=%zu pos->header_size=%zu value_size=%zu pos->value_size=%zu\n",
                  key_size, pos->header_size, value_size, pos->value_size);
          fclose(f);
        }
      }

      if (key_size != pos->header_size)
      {
        FILE *f = fopen("/tmp/logs/1343.txt", "a");
        if (NULL != f)
        {
          fprintf(f, "MISMATCH: key_size (%zu) != pos->header_size (%zu) -> return MHD_NO\n", key_size, pos->header_size);
          fclose(f);
        }
        return MHD_NO;
      }
      if (value_size != pos->value_size)
      {
        FILE *f = fopen("/tmp/logs/1343.txt", "a");
        if (NULL != f)
        {
          fprintf(f, "MISMATCH: value_size (%zu) != pos->value_size (%zu) -> return MHD_NO\n", value_size, pos->value_size);
          fclose(f);
        }
        return MHD_NO;
      }
      if (0 != key_size)
      {
        /* Log pointer and size state prior to memcmp; avoid dereferencing pointers here */
        {
          FILE *f = fopen("/tmp/logs/1343.txt", "a");
          if (NULL != f)
          {
            fprintf(f, "PRE_MEMCMP_KEY: key=%p pos->header=%p key_size=%zu\n", (const void *) key, (const void *) pos->header, key_size);
            fclose(f);
          }
        }
        mhd_assert (NULL != key);
        mhd_assert (NULL != pos->header);
        if (0 != memcmp (key,
                         pos->header,
                         key_size))
        {
          FILE *f = fopen("/tmp/logs/1343.txt", "a");
          if (NULL != f)
          {
            fprintf(f, "MEMCMP_KEY_MISMATCH: -> return MHD_NO\n");
            fclose(f);
          }
          return MHD_NO;
        }
        else
        {
          FILE *f = fopen("/tmp/logs/1343.txt", "a");
          if (NULL != f)
          {
            fprintf(f, "MEMCMP_KEY_MATCH\n");
            fclose(f);
          }
        }
      }
      if (0 != value_size)
      {
        /* Log pointer and size state prior to memcmp for value */
        {
          FILE *f = fopen("/tmp/logs/1343.txt", "a");
          if (NULL != f)
          {
            fprintf(f, "PRE_MEMCMP_VALUE: value=%p pos->value=%p value_size=%zu\n", (const void *) value, (const void *) pos->value, value_size);
            fclose(f);
          }
        }
        mhd_assert (NULL != value);
        mhd_assert (NULL != pos->value);
        if (0 != memcmp (value,
                         pos->value,
                         value_size))
        {
          FILE *f = fopen("/tmp/logs/1343.txt", "a");
          if (NULL != f)
          {
            fprintf(f, "MEMCMP_VALUE_MISMATCH: -> return MHD_NO\n");
            fclose(f);
          }
          return MHD_NO;
        }
        else
        {
          FILE *f = fopen("/tmp/logs/1343.txt", "a");
          if (NULL != f)
          {
            fprintf(f, "MEMCMP_VALUE_MATCH\n");
            fclose(f);
          }
        }
      }

      {
        FILE *f = fopen("/tmp/logs/1343.txt", "a");
        if (NULL != f)
        {
          fprintf(f, "RETURN MHD_YES\n");
          fclose(f);
        }
      }
      return MHD_YES;
    }
  }

  {
    FILE *f = fopen("/tmp/logs/1343.txt", "a");
    if (NULL != f)
    {
      fprintf(f, "END: no matching header -> return MHD_NO\n");
      fclose(f);
    }
  }
  return MHD_NO;
}
