static enum MHD_Result
test_header (void *cls,
             const char *key,
             size_t key_size,
             const char *value,
             size_t value_size,
             enum MHD_ValueKind kind)
{
  struct test_header_param *const param = (struct test_header_param *) cls;
  struct MHD_Connection *connection = param->connection;
  struct MHD_HTTP_Req_Header *pos;
  size_t i;
  FILE *log = fopen("/tmp/logs/1345.txt", "a");

  if (log)
  {
    fprintf(log, "test_header ENTER: cls=%p connection=%p key=%p key_size=%zu value=%p value_size=%zu kind=%d param->num_headers(before)=%zu\n",
            cls, (void *)connection, (const void *)key, key_size, (const void *)value, value_size, (int)kind, param->num_headers);
    fflush(log);
  }

  param->num_headers++;
  if (log)
  {
    fprintf(log, "param->num_headers(incremented)=%zu\n", param->num_headers);
    fflush(log);
  }

  i = 0;
  for (pos = connection->rq.headers_received; NULL != pos; pos = pos->next)
  {
    if (log)
    {
      fprintf(log, "loop: pos=%p pos->kind=%d pos->header_size=%zu pos->value_size=%zu pos->header=%p pos->value=%p pos->next=%p\n",
              (void *)pos, (int)pos->kind, pos->header_size, pos->value_size, (const void *)pos->header, (const void *)pos->value, (void *)pos->next);
      fflush(log);
    }

    if (kind != pos->kind)
    {
      if (log)
      {
        fprintf(log, "  kind mismatch: expected=%d found=%d -- continue\n", (int)kind, (int)pos->kind);
        fflush(log);
      }
      continue;
    }
    if (++i == param->num_headers)
    {
      if (key_size != pos->header_size)
      {
        if (log)
        {
          fprintf(log, "  header_size mismatch: expected=%zu found=%zu -> MHD_NO\n", key_size, pos->header_size);
          fclose(log);
        }
        return MHD_NO;
      }
      if (value_size != pos->value_size)
      {
        if (log)
        {
          fprintf(log, "  value_size mismatch: expected=%zu found=%zu -> MHD_NO\n", value_size, pos->value_size);
          fclose(log);
        }
        return MHD_NO;
      }
      if (0 != key_size)
      {
        mhd_assert (NULL != key);
        mhd_assert (NULL != pos->header);
        if (0 != memcmp (key,
                         pos->header,
                         key_size))
        {
          if (log)
          {
            fprintf(log, "  header memcmp mismatch -> MHD_NO\n");
            fclose(log);
          }
          return MHD_NO;
        }
      }
      if (0 != value_size)
      {
        mhd_assert (NULL != value);
        mhd_assert (NULL != pos->value);
        if (0 != memcmp (value,
                         pos->value,
                         value_size))
        {
          if (log)
          {
            fprintf(log, "  value memcmp mismatch -> MHD_NO\n");
            fclose(log);
          }
          return MHD_NO;
        }
      }
      if (log)
      {
        fprintf(log, "  match found -> MHD_YES\n");
        fclose(log);
      }
      return MHD_YES;
    }
  }

  if (log)
  {
    fprintf(log, "no matching header found -> MHD_NO\n");
    fclose(log);
  }
  return MHD_NO;
}
