static enum MHD_Result
test_header (void *cls,
             const char *key,
             size_t key_size,
             const char *value,
             size_t value_size,
             enum MHD_ValueKind kind)
{
  struct test_header_param *const param = (struct test_header_param *) cls;
  struct MHD_Connection *connection = param->connection;
  struct MHD_HTTP_Req_Header *pos;
  size_t i;

  /* Logging probe: write control flow and state information to a file so
     other agents can reproduce and gather evidence for pointer constness
     issues (pos->header and pos->value). */
  {
    FILE *log = fopen("/tmp/logs/1345.txt", "a");
    if (log)
    {
      fprintf(log, "ENTER test_header: cls=%p param=%p connection=%p\n",
              cls, (void *)param, (void *)connection);
      fprintf(log, "  inputs: key=%p key_size=%zu value=%p value_size=%zu kind=%d\n",
              (const void *)key, key_size, (const void *)value, value_size, (int) kind);
      if (param)
        fprintf(log, "  param->num_headers (before)=%u\n", (unsigned) param->num_headers);
      else
        fprintf(log, "  param is NULL\n");
      fclose(log);
    }
  }

  param->num_headers++;

  {
    FILE *log = fopen("/tmp/logs/1345.txt", "a");
    if (log)
    {
      fprintf(log, "  param->num_headers (after)=%u\n", (unsigned) param->num_headers);
      fclose(log);
    }
  }

  i = 0;
  for (pos = connection->rq.headers_received; NULL != pos; pos = pos->next)
  {
    /* Log each header node visited */
    {
      FILE *log = fopen("/tmp/logs/1345.txt", "a");
      if (log)
      {
        fprintf(log, "  visiting pos=%p kind=%d header_size=%zu value_size=%zu header_ptr=%p value_ptr=%p\n",
                (void *) pos, (int) pos->kind, pos->header_size, pos->value_size,
                (const void *) pos->header, (const void *) pos->value);
        fclose(log);
      }
    }

    if (kind != pos->kind)
    {
      /* Log the skip due to kind mismatch */
      FILE *log = fopen("/tmp/logs/1345.txt", "a");
      if (log)
      {
        fprintf(log, "    skipping pos=%p because kind %d != pos->kind %d\n",
                (void *) pos, (int) kind, (int) pos->kind);
        fclose(log);
      }
      continue;
    }
    if (++i == param->num_headers)
    {
      /* We are at the header index of interest. Log sizes and perform checks
         exactly as original code does, but emit logs before returning. */
      FILE *log = fopen("/tmp/logs/1345.txt", "a");
      if (log)
      {
        fprintf(log, "    matched index i=%zu (param->num_headers=%u) at pos=%p\n",
                i, (unsigned) param->num_headers, (void *) pos);
        fprintf(log, "    comparing key_size=%zu pos->header_size=%zu\n",
                key_size, pos->header_size);
        fclose(log);
      }

      if (key_size != pos->header_size)
      {
        FILE *log2 = fopen("/tmp/logs/1345.txt", "a");
        if (log2)
        {
          fprintf(log2, "    key_size mismatch: %zu != %zu -> returning MHD_NO\n",
                  key_size, pos->header_size);
          fclose(log2);
        }
        return MHD_NO;
      }
      if (value_size != pos->value_size)
      {
        FILE *log2 = fopen("/tmp/logs/1345.txt", "a");
        if (log2)
        {
          fprintf(log2, "    value_size mismatch: %zu != %zu -> returning MHD_NO\n",
                  value_size, pos->value_size);
          fclose(log2);
        }
        return MHD_NO;
      }
      if (0 != key_size)
      {
        mhd_assert (NULL != key);
        mhd_assert (NULL != pos->header);

        /* Log the bytes (up to a limit) of key and pos->header to aid
           reproduction. Avoid dereferencing if pointer is NULL. */
        {
          FILE *log2 = fopen("/tmp/logs/1345.txt", "a");
          if (log2)
          {
            size_t dump = key_size > 64 ? 64 : key_size;
            fprintf(log2, "    memcmp header: key=%p pos->header=%p dump_len=%zu\n",
                    (const void *) key, (const void *) pos->header, dump);
            if (dump > 0 && key && pos->header)
            {
              size_t k;
              fprintf(log2, "    key bytes:");
              for (k = 0; k < dump; ++k)
                fprintf(log2, " %02x", (unsigned char) key[k]);
              fprintf(log2, "\n    pos->header bytes:");
              for (k = 0; k < dump; ++k)
                fprintf(log2, " %02x", (unsigned char) pos->header[k]);
              fprintf(log2, "\n");
            }
            fclose(log2);
          }
        }

        if (0 != memcmp (key,
                         pos->header,
                         key_size))
        {
          FILE *log2 = fopen("/tmp/logs/1345.txt", "a");
          if (log2)
          {
            fprintf(log2, "    memcmp(header) != 0 -> returning MHD_NO\n");
            fclose(log2);
          }
          return MHD_NO;
        }
      }
      if (0 != value_size)
      {
        mhd_assert (NULL != value);
        mhd_assert (NULL != pos->value);

        /* Log the bytes (up to a limit) of value and pos->value to aid
           reproduction. Avoid dereferencing if pointer is NULL. */
        {
          FILE *log2 = fopen("/tmp/logs/1345.txt", "a");
          if (log2)
          {
            size_t dump = value_size > 64 ? 64 : value_size;
            fprintf(log2, "    memcmp value: value=%p pos->value=%p dump_len=%zu\n",
                    (const void *) value, (const void *) pos->value, dump);
            if (dump > 0 && value && pos->value)
            {
              size_t k;
              fprintf(log2, "    value bytes:");
              for (k = 0; k < dump; ++k)
                fprintf(log2, " %02x", (unsigned char) value[k]);
              fprintf(log2, "\n    pos->value bytes:");
              for (k = 0; k < dump; ++k)
                fprintf(log2, " %02x", (unsigned char) pos->value[k]);
              fprintf(log2, "\n");
            }
            fclose(log2);
          }
        }

        if (0 != memcmp (value,
                         pos->value,
                         value_size))
        {
          FILE *log2 = fopen("/tmp/logs/1345.txt", "a");
          if (log2)
          {
            fprintf(log2, "    memcmp(value) != 0 -> returning MHD_NO\n");
            fclose(log2);
          }
          return MHD_NO;
        }
      }

      {
        FILE *log2 = fopen("/tmp/logs/1345.txt", "a");
        if (log2)
        {
          fprintf(log2, "    all checks passed -> returning MHD_YES\n");
          fclose(log2);
        }
      }
      return MHD_YES;
    }
  }

  {
    FILE *log = fopen("/tmp/logs/1345.txt", "a");
    if (log)
    {
      fprintf(log, "exit test_header: reached end of header list -> returning MHD_NO\n");
      fclose(log);
    }
  }
  return MHD_NO;
}
