static void
thread_main_connection_upgrade (struct MHD_Connection *con)
{
#ifdef HTTPS_SUPPORT
  struct MHD_UpgradeResponseHandle *urh = con->urh;
  struct MHD_Daemon *daemon = con->daemon;

  /* Logging: open file for append. If /tmp/logs doesn't exist, fall back to /tmp. */
  FILE *logf = fopen("/tmp/logs/3735.txt", "a");
  if (NULL == logf)
    logf = fopen("/tmp/3735.txt", "a");
  if (logf)
  {
    fprintf(logf, "ENTER thread_main_connection_upgrade: con=%p, urh=%p, daemon=%p\n",
            (void *)con, (void *)urh, (void *)daemon);
    fflush(logf);
  }

  mhd_assert ( (! MHD_D_IS_USING_THREADS_ (daemon)) || \
               MHD_thread_handle_ID_is_current_thread_ (con->tid) );
  /* Here, we need to bi-directionally forward
     until the application tells us that it is done
     with the socket; */
  if ( (0 != (daemon->options & MHD_USE_TLS)) &&
       MHD_D_IS_USING_SELECT_ (daemon))
  {
    while ( (0 != urh->in_buffer_size) ||
            (0 != urh->out_buffer_size) ||
            (0 != urh->in_buffer_used) ||
            (0 != urh->out_buffer_used) )
    {
      /* use select */
      fd_set rs;
      fd_set ws;
      fd_set es;
      MHD_socket max_fd;
      int num_ready;
      bool result;

      if (logf)
      {
        fprintf(logf, "ITER: in_size=%zu out_size=%zu in_used=%zu out_used=%zu\n",
                (size_t)urh->in_buffer_size,
                (size_t)urh->out_buffer_size,
                (size_t)urh->in_buffer_used,
                (size_t)urh->out_buffer_used);
        fflush(logf);
      }

      FD_ZERO (&rs);
      FD_ZERO (&ws);
      FD_ZERO (&es);
      max_fd = MHD_INVALID_SOCKET;
      result = urh_to_fdset (urh,
                             &rs,
                             &ws,
                             &es,
                             &max_fd,
                             FD_SETSIZE);
      if (! result)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (con->daemon,
                  _ ("Error preparing select.\n"));
#endif
        if (logf)
        {
          fprintf(logf, "urh_to_fdset returned false\n");
          fflush(logf);
        }
        break;
      }
      if (logf)
      {
        fprintf(logf, "urh_to_fdset success, max_fd=%ld\n", (long) max_fd);
        /* Log which fds are set in each set BEFORE select */
        fprintf(logf, "FDSET BEFORE SELECT: RS:");
        for (int i = 0; i < FD_SETSIZE; ++i)
        {
          if (FD_ISSET(i, &rs))
            fprintf(logf, " %d", i);
        }
        fprintf(logf, " ; WS:");
        for (int i = 0; i < FD_SETSIZE; ++i)
        {
          if (FD_ISSET(i, &ws))
            fprintf(logf, " %d", i);
        }
        fprintf(logf, " ; ES:");
        for (int i = 0; i < FD_SETSIZE; ++i)
        {
          if (FD_ISSET(i, &es))
            fprintf(logf, " %d", i);
        }
        fprintf(logf, "\n");
        fflush(logf);
      }
      /* FIXME: does this check really needed? */
      if (MHD_INVALID_SOCKET != max_fd)
      {
        struct timeval *tvp;
        struct timeval tv;
        if (((con->tls_read_ready) &&
             (urh->in_buffer_used < urh->in_buffer_size)) ||
            (daemon->shutdown))
        {         /* No need to wait if incoming data is already pending in TLS buffers. */
          tv.tv_sec = 0;
          tv.tv_usec = 0;
          tvp = &tv;
        }
        else
          tvp = NULL;
        num_ready = MHD_SYS_select_ (max_fd + 1,
                                     &rs,
                                     &ws,
                                     &es,
                                     tvp);
      }
      else
        num_ready = 0;
      if (num_ready < 0)
      {
        const int err = MHD_socket_get_error_ ();

        if (MHD_SCKT_ERR_IS_EINTR_ (err))
          continue;
#ifdef HAVE_MESSAGES
        MHD_DLOG (con->daemon,
                  _ ("Error during select (%d): `%s'\n"),
                  err,
                  MHD_socket_strerr_ (err));
#endif
        if (logf)
        {
          fprintf(logf, "Error during select: err=%d str=%s\n",
                  err,
                  MHD_socket_strerr_ (err));
          fflush(logf);
        }
        break;
      }

      if (logf)
      {
        fprintf(logf, "num_ready=%d BEFORE urh_from_fdset\n", num_ready);
        /* Save snapshot of fdsets before calling urh_from_fdset */
        /* We will record which fds are set for comparison. */
        /* For memory efficiency, only record up to max of FD_SETSIZE or 1024 whichever is smaller. */
        int limit = FD_SETSIZE;
        if (limit > 1024) limit = 1024;
        /* store flags in arrays */
        unsigned char before_rs[1024];
        unsigned char before_ws[1024];
        unsigned char before_es[1024];
        for (int i = 0; i < limit; ++i)
        {
          before_rs[i] = FD_ISSET(i, &rs) ? 1 : 0;
          before_ws[i] = FD_ISSET(i, &ws) ? 1 : 0;
          before_es[i] = FD_ISSET(i, &es) ? 1 : 0;
        }
        fprintf(logf, "FDSET BEFORE urh_from_fdset (limit=%d): RS:", limit);
        for (int i = 0; i < limit; ++i)
          if (before_rs[i]) fprintf(logf, " %d", i);
        fprintf(logf, " ; WS:");
        for (int i = 0; i < limit; ++i)
          if (before_ws[i]) fprintf(logf, " %d", i);
        fprintf(logf, " ; ES:");
        for (int i = 0; i < limit; ++i)
          if (before_es[i]) fprintf(logf, " %d", i);
        fprintf(logf, "\n");
        fflush(logf);

        /* Call urh_from_fdset but log before/after to detect modifications */
        urh_from_fdset (urh,
                        &rs,
                        &ws,
                        &es,
                        (int) FD_SETSIZE);

        /* Compare after state */
        unsigned char after_rs[1024];
        unsigned char after_ws[1024];
        unsigned char after_es[1024];
        for (int i = 0; i < limit; ++i)
        {
          after_rs[i] = FD_ISSET(i, &rs) ? 1 : 0;
          after_ws[i] = FD_ISSET(i, &ws) ? 1 : 0;
          after_es[i] = FD_ISSET(i, &es) ? 1 : 0;
        }
        fprintf(logf, "FDSET AFTER urh_from_fdset: RS:");
        for (int i = 0; i < limit; ++i)
          if (after_rs[i]) fprintf(logf, " %d", i);
        fprintf(logf, " ; WS:");
        for (int i = 0; i < limit; ++i)
          if (after_ws[i]) fprintf(logf, " %d", i);
        fprintf(logf, " ; ES:");
        for (int i = 0; i < limit; ++i)
          if (after_es[i]) fprintf(logf, " %d", i);
        fprintf(logf, "\n");

        /* Report differences */
        int changes_reported = 0;
        for (int i = 0; i < limit; ++i)
        {
          if (before_rs[i] != after_rs[i])
          {
            if (!changes_reported) fprintf(logf, "MODIFICATIONS BY urh_from_fdset:\n");
            fprintf(logf, " RS fd %d: %d -> %d\n", i, before_rs[i], after_rs[i]);
            changes_reported = 1;
          }
          if (before_ws[i] != after_ws[i])
          {
            if (!changes_reported) fprintf(logf, "MODIFICATIONS BY urh_from_fdset:\n");
            fprintf(logf, " WS fd %d: %d -> %d\n", i, before_ws[i], after_ws[i]);
            changes_reported = 1;
          }
          if (before_es[i] != after_es[i])
          {
            if (!changes_reported) fprintf(logf, "MODIFICATIONS BY urh_from_fdset:\n");
            fprintf(logf, " ES fd %d: %d -> %d\n", i, before_es[i], after_es[i]);
            changes_reported = 1;
          }
        }
        if (!changes_reported)
          fprintf(logf, "urh_from_fdset did not modify fd_sets (within logged range)\n");
        fflush(logf);
      }
      else
      {
        /* If logging is disabled, still call original function and continue. */
        urh_from_fdset (urh,
                        &rs,
                        &ws,
                        &es,
                        (int) FD_SETSIZE);
      }

      process_urh (urh);
    }
  }
#ifdef HAVE_POLL
  else if (0 != (daemon->options & MHD_USE_TLS))
  {
    /* use poll() */
    struct pollfd p[2];
    memset (p,
            0,
            sizeof (p));
    p[0].fd = urh->connection->socket_fd;
    p[1].fd = urh->mhd.socket;

    while ( (0 != urh->in_buffer_size) ||
            (0 != urh->out_buffer_size) ||
            (0 != urh->in_buffer_used) ||
            (0 != urh->out_buffer_used) )
    {
      int timeout;

      urh_update_pollfd (urh, p);

      if (((con->tls_read_ready) &&
           (urh->in_buffer_used < urh->in_buffer_size)) ||
          (daemon->shutdown))
        timeout = 0;     /* No need to wait if incoming data is already pending in TLS buffers. */
      else
        timeout = -1;

      if (MHD_sys_poll_ (p,
                         2,
                         timeout) < 0)
      {
        const int err = MHD_socket_get_error_ ();

        if (MHD_SCKT_ERR_IS_EINTR_ (err))
          continue;
#ifdef HAVE_MESSAGES
        MHD_DLOG (con->daemon,
                  _ ("Error during poll: `%s'\n"),
                  MHD_socket_strerr_ (err));
#endif
        if (logf)
        {
          fprintf(logf, "Error during poll: %s\n", MHD_socket_strerr_ (err));
          fflush(logf);
        }
        break;
      }
      urh_from_pollfd (urh,
                       p);
      process_urh (urh);
    }
  }
  /* end POLL */
#endif
  /* end HTTPS */
#endif /* HTTPS_SUPPORT */
  /* TLS forwarding was finished. Cleanup socketpair. */
  if (logf)
  {
    fprintf(logf, "EXIT thread_main_connection_upgrade: finishing forward for con=%p\n", (void *)con);
    fflush(logf);
    fclose(logf);
  }
  MHD_connection_finish_forward_ (con);
  /* Do not set 'urh->clean_ready' yet as 'urh' will be used
   * in connection thread for a little while. */
}
