static void
thread_main_connection_upgrade (struct MHD_Connection *con)
{
#ifdef HTTPS_SUPPORT
  struct MHD_UpgradeResponseHandle *urh = con->urh;
  struct MHD_Daemon *daemon = con->daemon;
  FILE *logf = fopen("/tmp/logs/3735.txt", "a");

  if (logf) {
    /* Basic entry log */
    fprintf(logf, "ENTER thread_main_connection_upgrade: con=%p urh=%p daemon=%p\n",
            (void *)con, (void *)urh, (void *)daemon);
    if (urh) {
      fprintf(logf, " initial buffers: in_size=%zu out_size=%zu in_used=%zu out_used=%zu\n",
              (size_t)urh->in_buffer_size,
              (size_t)urh->out_buffer_size,
              (size_t)urh->in_buffer_used,
              (size_t)urh->out_buffer_used);
    }
    if (daemon) {
      fprintf(logf, " daemon options: 0x%08x shutdown=%d\n",
              (unsigned)daemon->options,
              (int)daemon->shutdown);
    }
    fflush(logf);
  }

  mhd_assert ( (! MHD_D_IS_USING_THREADS_ (daemon)) || \
               MHD_thread_handle_ID_is_current_thread_ (con->tid) );
  /* Here, we need to bi-directionally forward
     until the application tells us that it is done
     with the socket; */
  if ( (0 != (daemon->options & MHD_USE_TLS)) &&
       MHD_D_IS_USING_SELECT_ (daemon))
  {
    if (logf) {
      fprintf(logf, "Using select() branch\n");
      fflush(logf);
    }
    while ( (0 != urh->in_buffer_size) ||
            (0 != urh->out_buffer_size) ||
            (0 != urh->in_buffer_used) ||
            (0 != urh->out_buffer_used) )
    {
      /* use select */
      fd_set rs;
      fd_set ws;
      fd_set es;
      MHD_socket max_fd;
      int num_ready;
      bool result;

      FD_ZERO (&rs);
      FD_ZERO (&ws);
      FD_ZERO (&es);
      max_fd = MHD_INVALID_SOCKET;
      result = urh_to_fdset (urh,
                             &rs,
                             &ws,
                             &es,
                             &max_fd,
                             FD_SETSIZE);
      if (! result)
      {
        if (logf) {
          fprintf(logf, "urh_to_fdset() failed: urh=%p\n", (void *)urh);
          fflush(logf);
        }
#ifdef HAVE_MESSAGES
        MHD_DLOG (con->daemon,
                  _ ("Error preparing select.\n"));
#endif
        break;
      }
      /* FIXME: does this check really needed? */
      if (MHD_INVALID_SOCKET != max_fd)
      {
        struct timeval *tvp;
        struct timeval tv;
        if (((con->tls_read_ready) &&
             (urh->in_buffer_used < urh->in_buffer_size)) ||
            (daemon->shutdown))
        {         /* No need to wait if incoming data is already pending in TLS buffers. */
          tv.tv_sec = 0;
          tv.tv_usec = 0;
          tvp = &tv;
        }
        else
          tvp = NULL;
        if (logf) {
          fprintf(logf, "select: max_fd=%ld tvp=%p (tls_read_ready=%d shutdown=%d)\n",
                  (long) max_fd, (void *)tvp, (int)con->tls_read_ready, (int)daemon->shutdown);
          fflush(logf);
        }
        num_ready = MHD_SYS_select_ (max_fd + 1,
                                     &rs,
                                     &ws,
                                     &es,
                                     tvp);
      }
      else
        num_ready = 0;
      if (num_ready < 0)
      {
        const int err = MHD_socket_get_error_ ();

        if (MHD_SCKT_ERR_IS_EINTR_ (err)) {
          if (logf) {
            fprintf(logf, "select interrupted by EINTR (err=%d) - continuing\n", err);
            fflush(logf);
          }
          continue;
        }

#ifdef HAVE_MESSAGES
        MHD_DLOG (con->daemon,
                  _ ("Error during select (%d): `%s'\n"),
                  err,
                  MHD_socket_strerr_ (err));
#endif
        /* Additional logging: capture the pointer and the textual message returned. */
        if (logf) {
          const char *estr = MHD_socket_strerr_ (err);
          fprintf(logf, "Error during select: err=%d estr_ptr=%p estr_str=%s\n",
                  err,
                  (void *)estr,
                  (estr ? estr : "(null)"));
          fflush(logf);
        }
        break;
      }
      if (logf) {
        fprintf(logf, "select returned num_ready=%d\n", num_ready);
        fflush(logf);
      }
      urh_from_fdset (urh,
                      &rs,
                      &ws,
                      &es,
                      (int) FD_SETSIZE);
      if (logf) {
        fprintf(logf, "after urh_from_fdset: in_used=%zu out_used=%zu\n",
                (size_t)urh->in_buffer_used,
                (size_t)urh->out_buffer_used);
        fflush(logf);
      }
      process_urh (urh);
      if (logf) {
        fprintf(logf, "after process_urh: in_used=%zu out_used=%zu\n",
                (size_t)urh->in_buffer_used,
                (size_t)urh->out_buffer_used);
        fflush(logf);
      }
    }
  }
#ifdef HAVE_POLL
  else if (0 != (daemon->options & MHD_USE_TLS))
  {
    if (logf) {
      fprintf(logf, "Using poll() branch\n");
      fflush(logf);
    }
    /* use poll() */
    struct pollfd p[2];
    memset (p,
            0,
            sizeof (p));
    p[0].fd = urh->connection->socket_fd;
    p[1].fd = urh->mhd.socket;

    while ( (0 != urh->in_buffer_size) ||
            (0 != urh->out_buffer_size) ||
            (0 != urh->in_buffer_used) ||
            (0 != urh->out_buffer_used) )
    {
      int timeout;

      urh_update_pollfd (urh, p);

      if (((con->tls_read_ready) &&
           (urh->in_buffer_used < urh->in_buffer_size)) ||
          (daemon->shutdown))
        timeout = 0;     /* No need to wait if incoming data is already pending in TLS buffers. */
      else
        timeout = -1;

      if (logf) {
        fprintf(logf, "poll: p[0].fd=%d p[1].fd=%d timeout=%d\n", p[0].fd, p[1].fd, timeout);
        fflush(logf);
      }

      if (MHD_sys_poll_ (p,
                         2,
                         timeout) < 0)
      {
        const int err = MHD_socket_get_error_ ();

        if (MHD_SCKT_ERR_IS_EINTR_ (err)) {
          if (logf) {
            fprintf(logf, "poll interrupted by EINTR (err=%d) - continuing\n", err);
            fflush(logf);
          }
          continue;
        }
#ifdef HAVE_MESSAGES
        MHD_DLOG (con->daemon,
                  _ ("Error during poll: `%s'\n"),
                  MHD_socket_strerr_ (err));
#endif
        if (logf) {
          const char *estr = MHD_socket_strerr_ (err);
          fprintf(logf, "Error during poll: err=%d estr_ptr=%p estr_str=%s\n",
                  err,
                  (void *)estr,
                  (estr ? estr : "(null)"));
          fflush(logf);
        }
        break;
      }
      if (logf) {
        fprintf(logf, "poll returned\n");
        fflush(logf);
      }
      urh_from_pollfd (urh,
                       p);
      if (logf) {
        fprintf(logf, "after urh_from_pollfd: in_used=%zu out_used=%zu\n",
                (size_t)urh->in_buffer_used,
                (size_t)urh->out_buffer_used);
        fflush(logf);
      }
      process_urh (urh);
      if (logf) {
        fprintf(logf, "after process_urh: in_used=%zu out_used=%zu\n",
                (size_t)urh->in_buffer_used,
                (size_t)urh->out_buffer_used);
        fflush(logf);
      }
    }
  }
  /* end POLL */
#endif
  /* end HTTPS */
#endif /* HTTPS_SUPPORT */
  /* TLS forwarding was finished. Cleanup socketpair. */
  if (logf) {
    fprintf(logf, "TLS forwarding finished, calling MHD_connection_finish_forward_: con=%p\n", (void *)con);
    fflush(logf);
  }
  MHD_connection_finish_forward_ (con);
  /* Do not set 'urh->clean_ready' yet as 'urh' will be used
   * in connection thread for a little while. */
  if (logf) {
    fprintf(logf, "EXIT thread_main_connection_upgrade: con=%p\n", (void *)con);
    fclose(logf);
  }
}
