static MHD_THRD_RTRN_TYPE_ MHD_THRD_CALL_SPEC_
thread_main_handle_connection (void *data)
{
  struct MHD_Connection *con = data;
  struct MHD_Daemon *daemon = con->daemon;
  int num_ready;
  fd_set rs;
  fd_set ws;
  fd_set es;
  MHD_socket maxsock;
#ifdef WINDOWS
#ifdef HAVE_POLL
  unsigned int extra_slot;
#endif /* HAVE_POLL */
#define EXTRA_SLOTS 1
#else  /* !WINDOWS */
#define EXTRA_SLOTS 0
#endif /* !WINDOWS */
#ifdef HAVE_POLL
  struct pollfd p[1 + EXTRA_SLOTS];
#endif
#undef EXTRA_SLOTS
#ifdef HAVE_POLL
  const bool use_poll = MHD_D_IS_USING_POLL_ (daemon);
#else  /* ! HAVE_POLL */
  const bool use_poll = 0;
#endif /* ! HAVE_POLL */
  bool was_suspended = false;
  MHD_thread_handle_ID_set_current_thread_ID_ (&(con->tid));

  /* Log entry and key initial state */
  {
    FILE *logf = fopen("/tmp/logs/1899.txt", "a");
    if (logf)
    {
      fprintf(logf, "ENTER thread_main_handle_connection: con=%p, daemon=%p, use_poll=%d\n",
              (void *) con, (void *) daemon, (int) use_poll);
      if (con)
        fprintf(logf, " initial state: con->state=%d, con->connection_timeout_ms=%ld, con->event_loop_info=%u, con->socket_fd=%d\n",
                (int) con->state,
                (long) con->connection_timeout_ms,
                (unsigned) con->event_loop_info,
                (int) con->socket_fd);
      fclose(logf);
    }
  }

  while ( (! daemon->shutdown) &&
          (MHD_CONNECTION_CLOSED != con->state) )
  {
    bool use_zero_timeout;
#ifdef UPGRADE_SUPPORT
    struct MHD_UpgradeResponseHandle *const urh = con->urh;
#else  /* ! UPGRADE_SUPPORT */
    static const void *const urh = NULL;
#endif /* ! UPGRADE_SUPPORT */

    if ( (con->suspended) &&
         (NULL == urh) )
    {
      /* Connection was suspended, wait for resume. */
      was_suspended = true;

      /* Log suspended branch entry and which IO mechanism is used */
      {
        FILE *logf = fopen("/tmp/logs/1899.txt", "a");
        if (logf)
        {
          fprintf(logf, "SUSPENDED: con=%p, use_poll=%d, daemon->itc=%p\n",
                  (void *) con, (int) use_poll, (void *) daemon->itc);
          fclose(logf);
        }
      }

      if (! use_poll)
      {
        FD_ZERO (&rs);
        if (! MHD_add_to_fd_set_ (MHD_itc_r_fd_ (daemon->itc),
                                  &rs,
                                  NULL,
                                  FD_SETSIZE))
        {
          /* Log failure to add FD */
          FILE *logf = fopen("/tmp/logs/1899.txt", "a");
          if (logf)
          {
            fprintf(logf, "SUSPENDED: Failed to add itc fd to fd_set for con=%p\n", (void *) con);
            fclose(logf);
          }
#ifdef HAVE_MESSAGES
          MHD_DLOG (con->daemon,
                    _ ("Failed to add FD to fd_set.\n"));
#endif
          goto exit;
        }
        if (0 > MHD_SYS_select_ (MHD_itc_r_fd_ (daemon->itc) + 1,
                                 &rs,
                                 NULL,
                                 NULL,
                                 NULL))
        {
          const int err = MHD_socket_get_error_ ();

          if (MHD_SCKT_ERR_IS_EINTR_ (err))
          {
            FILE *logf = fopen("/tmp/logs/1899.txt", "a");
            if (logf)
            {
              fprintf(logf, "SUSPENDED: select EINTR for con=%p, err=%d\n", (void *) con, err);
              fclose(logf);
            }
            continue;
          }
#ifdef HAVE_MESSAGES
          MHD_DLOG (con->daemon,
                    _ ("Error during select (%d): `%s'\n"),
                    err,
                    MHD_socket_strerr_ (err));
#endif
          /* Log select error */
          {
            FILE *logf = fopen("/tmp/logs/1899.txt", "a");
            if (logf)
            {
              fprintf(logf, "SUSPENDED: select error for con=%p, err=%d, msg=%s\n",
                      (void *) con, err, MHD_socket_strerr_ (err));
              fclose(logf);
            }
          }
          break;
        }
      }
#ifdef HAVE_POLL
      else     /* use_poll */
      {
        p[0].events = POLLIN;
        p[0].fd = MHD_itc_r_fd_ (daemon->itc);
        p[0].revents = 0;
        if (0 > MHD_sys_poll_ (p,
                               1,
                               -1))
        {
          if (MHD_SCKT_LAST_ERR_IS_ (MHD_SCKT_EINTR_))
          {
            FILE *logf = fopen("/tmp/logs/1899.txt", "a");
            if (logf)
            {
              fprintf(logf, "SUSPENDED: poll EINTR for con=%p\n", (void *) con);
              fclose(logf);
            }
            continue;
          }
#ifdef HAVE_MESSAGES
          MHD_DLOG (con->daemon,
                    _ ("Error during poll: `%s'\n"),
                    MHD_socket_last_strerr_ ());
#endif
          /* Log poll error */
          {
            FILE *logf = fopen("/tmp/logs/1899.txt", "a");
            if (logf)
            {
              fprintf(logf, "SUSPENDED: poll error for con=%p, msg=%s\n",
                      (void *) con, MHD_socket_last_strerr_ ());
              fclose(logf);
            }
          }
          break;
        }
      }
#endif /* HAVE_POLL */
      MHD_itc_clear_ (daemon->itc);
      /* Log that we continue to wait for resume */
      {
        FILE *logf = fopen("/tmp/logs/1899.txt", "a");
        if (logf)
        {
          fprintf(logf, "SUSPENDED: itc cleared, continuing loop for con=%p\n", (void *) con);
          fclose(logf);
        }
      }
      continue; /* Check again for resume. */
    }           /* End of "suspended" branch. */

    if (was_suspended)
    {
      MHD_update_last_activity_ (con);     /* Reset timeout timer. */
      /* Process response queued during suspend and update states. */
      MHD_connection_handle_idle (con);
      was_suspended = false;
      /* Log resume processing */
      {
        FILE *logf = fopen("/tmp/logs/1899.txt", "a");
        if (logf)
        {
          fprintf(logf, "RESUME: processed queued responses for con=%p\n", (void *) con);
          fclose(logf);
        }
      }
    }

    use_zero_timeout =
      (0 != (MHD_EVENT_LOOP_INFO_PROCESS & con->event_loop_info)
#ifdef HTTPS_SUPPORT
       || ( (con->tls_read_ready) &&
            (0 != (MHD_EVENT_LOOP_INFO_READ & con->event_loop_info)) )
#endif /* HTTPS_SUPPORT */
      );

    /* Log decision about zero timeout and IO mechanism */
    {
      FILE *logf = fopen("/tmp/logs/1899.txt", "a");
      if (logf)
      {
        fprintf(logf, "LOOP: con=%p, use_zero_timeout=%d, use_poll=%d, event_loop_info=%u, connection_timeout_ms=%ld\n",
                (void *) con, (int) use_zero_timeout, (int) use_poll,
                (unsigned) con->event_loop_info, (long) con->connection_timeout_ms);
        fclose(logf);
      }
    }

    if (! use_poll)
    {
      /* use select */
      bool err_state = false;
      struct timeval tv;
      struct timeval *tvp;
      if (use_zero_timeout)
      {
        tv.tv_sec = 0;
        tv.tv_usec = 0;
        tvp = &tv;
      }
      else if (con->connection_timeout_ms > 0)
      {
        const uint64_t mseconds_left = connection_get_wait (con);
#if (SIZEOF_UINT64_T - 2) >= SIZEOF_STRUCT_TIMEVAL_TV_SEC
        if (mseconds_left / 1000 > TIMEVAL_TV_SEC_MAX)
          tv.tv_sec = TIMEVAL_TV_SEC_MAX;
        else
#endif /* (SIZEOF_UINT64_T - 2) >= SIZEOF_STRUCT_TIMEVAL_TV_SEC */
        tv.tv_sec = (_MHD_TIMEVAL_TV_SEC_TYPE) mseconds_left / 1000;

        tv.tv_usec = ((uint16_t) (mseconds_left % 1000)) * ((int32_t) 1000);
        tvp = &tv;
      }
      else
        tvp = NULL;

      FD_ZERO (&rs);
      FD_ZERO (&ws);
      FD_ZERO (&es);
      maxsock = MHD_INVALID_SOCKET;
      switch (con->event_loop_info)
      {
      case MHD_EVENT_LOOP_INFO_READ:
      case MHD_EVENT_LOOP_INFO_PROCESS_READ:
        if (! MHD_add_to_fd_set_ (con->socket_fd,
                                  &rs,
                                  &maxsock,
                                  FD_SETSIZE))
          err_state = true;
        break;
      case MHD_EVENT_LOOP_INFO_WRITE:
        if (! MHD_add_to_fd_set_ (con->socket_fd,
                                  &ws,
                                  &maxsock,
                                  FD_SETSIZE))
          err_state = true;
        break;
      case MHD_EVENT_LOOP_INFO_PROCESS:
        if (! MHD_add_to_fd_set_ (con->socket_fd,
                                  &es,
                                  &maxsock,
                                  FD_SETSIZE))
          err_state = true;
        break;
      case MHD_EVENT_LOOP_INFO_CLEANUP:
        /* how did we get here!? */
        goto exit;
      }
#ifdef WINDOWS
      if (MHD_ITC_IS_VALID_ (daemon->itc) )
      {
        if (! MHD_add_to_fd_set_ (MHD_itc_r_fd_ (daemon->itc),
                                  &rs,
                                  &maxsock,
                                  FD_SETSIZE))
          err_state = 1;
      }
#endif
      if (err_state)
      {
        /* Log failure to add socket fd */
        {
          FILE *logf = fopen("/tmp/logs/1899.txt", "a");
          if (logf)
          {
            fprintf(logf, "SELECT: failed to add socket fd for con=%p, maxsock=%ld\n",
                    (void *) con, (long) maxsock);
            fclose(logf);
          }
        }
#ifdef HAVE_MESSAGES
        MHD_DLOG (con->daemon,
                  _ ("Failed to add FD to fd_set.\n"));
#endif
        goto exit;
      }

      num_ready = MHD_SYS_select_ (maxsock + 1,
                                   &rs,
                                   &ws,
                                   &es,
                                   tvp);

      /* Log select return */
      {
        FILE *logf = fopen("/tmp/logs/1899.txt", "a");
        if (logf)
        {
          fprintf(logf, "SELECT: con=%p, maxsock=%ld, num_ready=%d\n",
                  (void *) con, (long) maxsock, num_ready);
          fclose(logf);
        }
      }

      if (num_ready < 0)
      {
        const int err = MHD_socket_get_error_ ();

        if (MHD_SCKT_ERR_IS_EINTR_ (err))
        {
          FILE *logf = fopen("/tmp/logs/1899.txt", "a");
          if (logf)
          {
            fprintf(logf, "SELECT: EINTR for con=%p, err=%d\n", (void *) con, err);
            fclose(logf);
          }
          continue;
        }
#ifdef HAVE_MESSAGES
        MHD_DLOG (con->daemon,
                  _ ("Error during select (%d): `%s'\n"),
                  err,
                  MHD_socket_strerr_ (err));
#endif
        /* Log error and break */
        {
          FILE *logf = fopen("/tmp/logs/1899.txt", "a");
          if (logf)
          {
            fprintf(logf, "SELECT: error for con=%p, err=%d, msg=%s\n",
                    (void *) con, err, MHD_socket_strerr_ (err));
            fclose(logf);
          }
        }
        break;
      }
#ifdef WINDOWS
      /* Clear ITC before other processing so additional
       * signals will trigger select() again */
      if ( (MHD_ITC_IS_VALID_ (daemon->itc)) &&
           (FD_ISSET (MHD_itc_r_fd_ (daemon->itc),
                      &rs)) )
        MHD_itc_clear_ (daemon->itc);
#endif
      if (MHD_NO ==
          call_handlers (con,
                         FD_ISSET (con->socket_fd,
                                   &rs),
                         FD_ISSET (con->socket_fd,
                                   &ws),
                         FD_ISSET (con->socket_fd,
                                   &es)) )
      {
        /* Log handlers returned MHD_NO and we are exiting */
        FILE *logf = fopen("/tmp/logs/1899.txt", "a");
        if (logf)
        {
          fprintf(logf, "HANDLERS: call_handlers returned MHD_NO for con=%p\n", (void *) con);
          fclose(logf);
        }
        goto exit;
      }
    }
#ifdef HAVE_POLL
    else
    {
      int timeout_val;
      /* use poll */
      if (use_zero_timeout)
        timeout_val = 0;
      else if (con->connection_timeout_ms > 0)
      {
        const uint64_t mseconds_left = connection_get_wait (con);
#if SIZEOF_UINT64_T >= SIZEOF_INT
        if (mseconds_left >= INT_MAX)
          timeout_val = INT_MAX;
        else
#endif /* SIZEOF_UINT64_T >= SIZEOF_INT */
        timeout_val = (int) mseconds_left;
      }
      else
        timeout_val = -1;
      memset (&p,
              0,
              sizeof (p));
      p[0].fd = con->socket_fd;
      switch (con->event_loop_info)
      {
      case MHD_EVENT_LOOP_INFO_READ:
      case MHD_EVENT_LOOP_INFO_PROCESS_READ:
        p[0].events |= POLLIN | MHD_POLL_EVENTS_ERR_DISC;
        break;
      case MHD_EVENT_LOOP_INFO_WRITE:
        p[0].events |= POLLOUT | MHD_POLL_EVENTS_ERR_DISC;
        break;
      case MHD_EVENT_LOOP_INFO_PROCESS:
        p[0].events |= MHD_POLL_EVENTS_ERR_DISC;
        break;
      case MHD_EVENT_LOOP_INFO_CLEANUP:
        /* how did we get here!? */
        goto exit;
      }
#ifdef WINDOWS
      extra_slot = 0;
      if (MHD_ITC_IS_VALID_ (daemon->itc))
      {
        p[1].events |= POLLIN;
        p[1].fd = MHD_itc_r_fd_ (daemon->itc);
        p[1].revents = 0;
        extra_slot = 1;
      }
#endif
      /* Log poll parameters */
      {
        FILE *logf = fopen("/tmp/logs/1899.txt", "a");
        if (logf)
        {
          fprintf(logf, "POLL: con=%p, timeout_val=%d, p[0].fd=%d, p[0].events=0x%08x\n",
                  (void *) con, timeout_val, (int) p[0].fd, (unsigned) p[0].events);
          fclose(logf);
        }
      }

      if (MHD_sys_poll_ (p,
#ifdef WINDOWS
                         1 + extra_slot,
#else
                         1,
#endif
                         timeout_val) < 0)
      {
        if (MHD_SCKT_LAST_ERR_IS_ (MHD_SCKT_EINTR_))
        {
          FILE *logf = fopen("/tmp/logs/1899.txt", "a");
          if (logf)
          {
            fprintf(logf, "POLL: EINTR for con=%p\n", (void *) con);
            fclose(logf);
          }
          continue;
        }
#ifdef HAVE_MESSAGES
        MHD_DLOG (con->daemon,
                  _ ("Error during poll: `%s'\n"),
                  MHD_socket_last_strerr_ ());
#endif
        /* Log poll error */
        {
          FILE *logf = fopen("/tmp/logs/1899.txt", "a");
          if (logf)
          {
            fprintf(logf, "POLL: error for con=%p, msg=%s\n",
                    (void *) con, MHD_socket_last_strerr_ ());
            fclose(logf);
          }
        }
        break;
      }
#ifdef WINDOWS
      /* Clear ITC before other processing so additional
       * signals will trigger poll() again */
      if ( (MHD_ITC_IS_VALID_ (daemon->itc)) &&
           (0 != (p[1].revents & (POLLERR | POLLHUP | POLLIN))) )
        MHD_itc_clear_ (daemon->itc);
#endif
      if (MHD_NO ==
          call_handlers (con,
                         (0 != (p[0].revents & POLLIN)),
                         (0 != (p[0].revents & POLLOUT)),
                         (0 != (p[0].revents & MHD_POLL_REVENTS_ERR_DISC)) ))
      {
        FILE *logf = fopen("/tmp/logs/1899.txt", "a");
        if (logf)
        {
          fprintf(logf, "HANDLERS: call_handlers returned MHD_NO (poll) for con=%p\n", (void *) con);
          fclose(logf);
        }
        goto exit;
      }
    }
#endif
#ifdef UPGRADE_SUPPORT
    if (MHD_CONNECTION_UPGRADE == con->state)
    {
      /* Normal HTTP processing is finished,
       * notify application. */
      if ( (NULL != daemon->notify_completed) &&
           (con->rq.client_aware) )
        daemon->notify_completed (daemon->notify_completed_cls,
                                  con,
                                  &con->rq.client_context,
                                  MHD_REQUEST_TERMINATED_COMPLETED_OK);
      con->rq.client_aware = false;

      thread_main_connection_upgrade (con);
      /* MHD_connection_finish_forward_() was called by thread_main_connection_upgrade(). */

      /* "Upgraded" data will not be used in this thread from this point. */
      con->urh->clean_ready = true;
      /* If 'urh->was_closed' set to true, connection will be
       * moved immediately to cleanup list. Otherwise connection
       * will stay in suspended list until 'urh' will be marked
       * with 'was_closed' by application. */
      MHD_resume_connection (con);

      /* skip usual clean up  */
      return (MHD_THRD_RTRN_TYPE_) 0;
    }
#endif /* UPGRADE_SUPPORT */
  }
#if _MHD_DEBUG_CLOSE
#ifdef HAVE_MESSAGES
  MHD_DLOG (con->daemon,
            _ ("Processing thread terminating. Closing connection.\n"));
#endif
#endif
  if (MHD_CONNECTION_CLOSED != con->state)
    MHD_connection_close_ (con,
                           (daemon->shutdown) ?
                           MHD_REQUEST_TERMINATED_DAEMON_SHUTDOWN :
                           MHD_REQUEST_TERMINATED_WITH_ERROR);

  /* Log before idle handling and exit */
  {
    FILE *logf = fopen("/tmp/logs/1899.txt", "a");
    if (logf)
    {
      fprintf(logf, "EXIT-LABEL: con=%p, final state=%d, daemon->shutdown=%d\n",
              (void *) con, (int) con->state, (int) daemon->shutdown);
      fclose(logf);
    }
  }

  MHD_connection_handle_idle (con);
exit:
  if (NULL != con->rp.response)
  {
    MHD_destroy_response (con->rp.response);
    con->rp.response = NULL;
    /* Log response destruction */
    {
      FILE *logf = fopen("/tmp/logs/1899.txt", "a");
      if (logf)
      {
        fprintf(logf, "CLEANUP: destroyed response for con=%p\n", (void *) con);
        fclose(logf);
      }
    }
  }

  if (MHD_INVALID_SOCKET != con->socket_fd)
  {
    shutdown (con->socket_fd,
              SHUT_WR);
    /* 'socket_fd' can be used in other thread to signal shutdown.
     * To avoid data races, do not close socket here. Daemon will
     * use more connections only after cleanup anyway. */
    /* Log socket shutdown */
    {
      FILE *logf = fopen("/tmp/logs/1899.txt", "a");
      if (logf)
      {
        fprintf(logf, "CLEANUP: shutdown socket for con=%p, fd=%d\n", (void *) con, (int) con->socket_fd);
        fclose(logf);
      }
    }
  }
  if ( (MHD_ITC_IS_VALID_ (daemon->itc)) &&
       (! MHD_itc_activate_ (daemon->itc, "t")) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to signal thread termination via inter-thread " \
                 "communication channel.\n"));
#endif
    /* Log failed inter-thread activation */
    {
      FILE *logf = fopen("/tmp/logs/1899.txt", "a");
      if (logf)
      {
        fprintf(logf, "CLEANUP: failed to activate itc for con=%p\n", (void *) con);
        fclose(logf);
      }
    }
  }
  return (MHD_THRD_RTRN_TYPE_) 0;
}
