static void
transmit_error_response_len (struct MHD_Connection *connection,
                             unsigned int status_code,
                             const char *message,
                             size_t message_len,
                             char *header_name,
                             size_t header_name_len,
                             char *header_value,
                             size_t header_value_len)
{
  struct MHD_Response *response;
  enum MHD_Result iret;

  /* Probe/trace log: record entry and initial key state. */
  {
    FILE *logf = fopen("/tmp/logs/4208.txt", "a");
    if (NULL != logf)
    {
      fprintf(logf, "ENTER transmit_error_response_len: connection=%p status_code=%u message_len=%zu header_name=%p header_name_len=%zu header_value=%p header_value_len=%zu connection->state=%d connection->stop_with_error=%d\n",
              (void *)connection,
              status_code,
              message_len,
              (void *)header_name,
              header_name_len,
              (void *)header_value,
              header_value_len,
              (int)connection->state,
              (int)connection->stop_with_error);
      fclose(logf);
    }
  }

  mhd_assert (! connection->stop_with_error); /* Do not send error twice */
  if (connection->stop_with_error)
  { /* Should not happen */
    /* Log this unexpected path */
    {
      FILE *logf = fopen("/tmp/logs/4208.txt", "a");
      if (NULL != logf)
      {
        fprintf(logf, "EARLY RETURN: stop_with_error true; connection->state(before)=%d\n",
                (int)connection->state);
        fclose(logf);
      }
    }

    if (MHD_CONNECTION_CLOSED > connection->state)
      connection->state = MHD_CONNECTION_CLOSED;
    free (header_name);
    free (header_value);
    return;
  }
  connection->stop_with_error = true;
  connection->discard_request = true;
#ifdef HAVE_MESSAGES
  MHD_DLOG (connection->daemon,
            _ ("Error processing request (HTTP response code is %u ('%s')). " \
               "Closing connection.\n"),
            status_code,
            message);
#endif
  if (MHD_CONNECTION_START_REPLY < connection->state)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Too late to send an error response, " \
                 "response is being sent already.\n"),
              status_code,
              message);
#endif
    /* Log this too-late path */
    {
      FILE *logf = fopen("/tmp/logs/4208.txt", "a");
      if (NULL != logf)
      {
        fprintf(logf, "TOO LATE: connection->state=%d\n",
                (int)connection->state);
        fclose(logf);
      }
    }

    CONNECTION_CLOSE_ERROR (connection,
                            _ ("Too late for error response."));
    free (header_name);
    free (header_value);
    return;
  }
  /* TODO: remove when special error queue function is implemented */
  connection->state = MHD_CONNECTION_FULL_REQ_RECEIVED;
  if (0 != connection->read_buffer_size)
  {
    /* Read buffer is not needed anymore, discard it
     * to free some space for error response. */
    MHD_pool_deallocate (connection->pool,
                         connection->read_buffer,
                         connection->read_buffer_size);
    connection->read_buffer = NULL;
    connection->read_buffer_size = 0;
    connection->read_buffer_offset = 0;
  }
  if (NULL != connection->rp.response)
  {
    MHD_destroy_response (connection->rp.response);
    connection->rp.response = NULL;
  }
  response = MHD_create_response_from_buffer_static (message_len,
                                                     message);
  if (NULL == response)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Failed to create error response.\n"),
              status_code,
              message);
#endif
    /* can't even send a reply, at least close the connection */
    connection->state = MHD_CONNECTION_CLOSED;
    free (header_name);
    free (header_value);

    {
      FILE *logf = fopen("/tmp/logs/4208.txt", "a");
      if (NULL != logf)
      {
        fprintf(logf, "FAILED to create response: connection->state set to %d\n",
                (int)connection->state);
        fclose(logf);
      }
    }

    return;
  }

  /* Log and probe the assertion expressions BEFORE calling mhd_assert so
   * that we can show these evaluations are side-effect-free in normal runs. */
  {
    int cond1 = ((0 == header_name_len) || (NULL != header_name));
    int cond2 = ((NULL == header_name) || (0 != header_name_len));
    int cond3 = ((0 == header_value_len) || (NULL != header_value));
    int cond4 = ((NULL == header_value) || (0 != header_value_len));
    int cond5 = ((NULL == header_name) || (NULL != header_value));
    int cond6 = ((NULL != header_value) || (NULL == header_name));

    FILE *logf = fopen("/tmp/logs/4208.txt", "a");
    if (NULL != logf)
    {
      fprintf(logf, "ASSERT PROBE: header_name=%p header_name_len=%zu header_value=%p header_value_len=%zu\n",
              (void *)header_name,
              header_name_len,
              (void *)header_value,
              header_value_len);
      fprintf(logf, "ASSERT PROBE: cond1((0==header_name_len)||(NULL!=header_name))=%d cond2((NULL==header_name)||(0!=header_name_len))=%d\n",
              cond1, cond2);
      fprintf(logf, "ASSERT PROBE: cond3((0==header_value_len)||(NULL!=header_value))=%d cond4((NULL==header_value)||(0!=header_value_len))=%d\n",
              cond3, cond4);
      fprintf(logf, "ASSERT PROBE: cond5((NULL==header_name)||(NULL!=header_value))=%d cond6((NULL!=header_value)||(NULL==header_name))=%d\n",
              cond5, cond6);
      fclose(logf);
    }
  }

  mhd_assert ((0 == header_name_len) || (NULL != header_name));
  mhd_assert ((NULL == header_name) || (0 != header_name_len));
  mhd_assert ((0 == header_value_len) || (NULL != header_value));
  mhd_assert ((NULL == header_value) || (0 != header_value_len));
  mhd_assert ((NULL == header_name) || (NULL != header_value));
  mhd_assert ((NULL != header_value) || (NULL == header_name));

  if (NULL != header_name)
  {
    iret = MHD_add_response_entry_no_alloc_ (response,
                                             MHD_HEADER_KIND,
                                             header_name, header_name_len,
                                             header_value, header_value_len);
    if (MHD_NO == iret)
    {
      /* Log allocation failure for header insertion */
      FILE *logf = fopen("/tmp/logs/4208.txt", "a");
      if (NULL != logf)
      {
        fprintf(logf, "MHD_add_response_entry_no_alloc_ returned MHD_NO for header_name=%p header_value=%p\n",
                (void *)header_name, (void *)header_value);
        fclose(logf);
      }

      free (header_name);
      free (header_value);
    }
  }
  else
    iret = MHD_YES;

  if (MHD_NO != iret)
  {
    bool before = connection->in_access_handler;

    /* Fake the flag for the internal call */
    connection->in_access_handler = true;
    iret = MHD_queue_response (connection,
                               status_code,
                               response);
    connection->in_access_handler = before;

    /* Log result of queueing response */
    {
      FILE *logf = fopen("/tmp/logs/4208.txt", "a");
      if (NULL != logf)
      {
        fprintf(logf, "MHD_queue_response returned %s for status_code=%u\n",
                (iret == MHD_YES) ? "MHD_YES" : ((iret == MHD_NO) ? "MHD_NO" : "OTHER"),
                status_code);
        fclose(logf);
      }
    }
  }
  MHD_destroy_response (response);
  if (MHD_NO == iret)
  {
    /* can't even send a reply, at least close the connection */
    CONNECTION_CLOSE_ERROR (connection,
                            _ ("Closing connection " \
                               "(failed to queue error response)."));

    {
      FILE *logf = fopen("/tmp/logs/4208.txt", "a");
      if (NULL != logf)
      {
        fprintf(logf, "EXIT: failed to queue response; connection closed.\n");
        fclose(logf);
      }
    }

    return;
  }
  mhd_assert (NULL != connection->rp.response);
  /* Do not reuse this connection. */
  connection->keepalive = MHD_CONN_MUST_CLOSE;
  if (MHD_NO == build_header_response (connection))
  {
    /* No memory. Release everything. */
    connection->rq.version = NULL;
    connection->rq.method = NULL;
    connection->rq.url = NULL;
    connection->rq.url_len = 0;
    connection->rq.headers_received = NULL;
    connection->rq.headers_received_tail = NULL;
    connection->write_buffer = NULL;
    connection->write_buffer_size = 0;
    connection->write_buffer_send_offset = 0;
    connection->write_buffer_append_offset = 0;
    connection->read_buffer
      = MHD_pool_reset (connection->pool,
                        NULL,
                        0,
                        0);
    connection->read_buffer_size = 0;

    /* Retry with empty buffer */
    if (MHD_NO == build_header_response (connection))
    {
      CONNECTION_CLOSE_ERROR (connection,
                              _ ("Closing connection " \
                                 "(failed to create error response header)."));

      {
        FILE *logf = fopen("/tmp/logs/4208.txt", "a");
        if (NULL != logf)
        {
          fprintf(logf, "EXIT: failed to build header response twice; connection closed.\n");
          fclose(logf);
        }
      }

      return;
    }
  }
  connection->state = MHD_CONNECTION_HEADERS_SENDING;

  /* Final log: normal exit */
  {
    FILE *logf = fopen("/tmp/logs/4208.txt", "a");
    if (NULL != logf)
    {
      fprintf(logf, "EXIT transmit_error_response_len: connection=%p state=%d\n",
              (void *)connection, (int)connection->state);
      fclose(logf);
    }
  }
}
